  async createTask(task: CreateTaskRequest): Promise<TaskResponse> {
    return this.executeWithEnhancements(() => this.apiClient.createTask(task), {
      operationName: 'Create Task',
      showSpinner: true,
      spinnerText: `Creating task: ${task.title}`,
