    operation: () => Promise<T>,
    metadata: RequestMetadata
  ): Promise<T> {
    return this.executeWithEnhancements(operation, metadata);
  }
}

import type { ConfigManager } from './config';
import type {
  HealthResponse,
  CreateTaskRequest,
  UpdateTaskRequest,
  CreateBoardRequest,
  UpdateBoardRequest,
  CreateNoteRequest,
  UpdateNoteRequest,
  CreateTagRequest,
  UpdateTagRequest,
  TaskResponse,
  BoardResponse,
  NoteResponse,
  TagResponse,
  AnyApiResponse,
} from './types';

interface RequestOptions {
  method?: 'GET' | 'POST' | 'PATCH' | 'DELETE';
  body?: unknown;
  params?: Record<string, string> | undefined;
  timeout?: number;
}

export class ApiClient {
  private readonly config: ConfigManager;

  private baseUrl: string;

  private apiKey: string | undefined;

  constructor(config: ConfigManager) {
    this.config = config;
    this.baseUrl = config.getServerUrl();
    this.apiKey = config.getApiKey();
  }

  /**
   * Make authenticated API request
   */
  async request<T = AnyApiResponse>(endpoint: string, options: RequestOptions = {}): Promise<T> {
    const { method = 'GET', body, params, timeout = 10000 } = options;

    // Build URL with query parameters
    const url = new URL(`${this.baseUrl}${endpoint}`);
    if (params) {
      Object.entries(params).forEach(([key, value]) => {
        url.searchParams.set(key, value);
      });
    }

    // Prepare headers
    const headers: Record<string, string> = {
      'Content-Type': 'application/json',
      'User-Agent': 'mcp-kanban-cli',
    };

    if (this.apiKey) {
      headers['X-API-Key'] = this.apiKey;
    }

    // Prepare request options
    const requestOptions: RequestInit = {
      method,
      headers,
      signal: AbortSignal.timeout(timeout),
    };

    if (body && ['POST', 'PATCH', 'PUT'].includes(method)) {
      requestOptions.body = JSON.stringify(body);
    }

    try {
      const response = await fetch(url.toString(), requestOptions);

      if (!response.ok) {
        await ApiClient.handleErrorResponse(response);
      }

      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        const data = await response.json();
        // Handle both wrapped and unwrapped responses
        if (data && typeof data === 'object' && 'data' in data) {
          const wrappedData = data as { data: T };
          return wrappedData.data;
        }
        return data as T;
      }

      return (await response.text()) as T;
    } catch (error) {
      if (error instanceof Error) {
        if (error.name === 'AbortError') {
          throw new Error('Request timeout');
        }
        if (error.message.includes('fetch')) {
          throw new Error(`Network error: Unable to connect to ${this.baseUrl}`);
        }
      }
      throw error;
    }
  }

  /**
   * Handle error responses
   */
  private static async handleErrorResponse(response: Response): Promise<never> {
    const contentType = response.headers.get('content-type');
    let errorMessage = `HTTP ${response.status}: ${response.statusText}`;

    try {
      if (contentType && contentType.includes('application/json')) {
        const errorData = (await response.json()) as {
          error?: { message: string };
          message?: string;
        };
        if (
          errorData.error &&
          typeof errorData.error === 'object' &&
          'message' in errorData.error
        ) {
          errorMessage = errorData.error.message;
        } else if (errorData.message) {
          errorMessage = errorData.message;
        }
      } else {
        const text = await response.text();
        if (text) {
          errorMessage = text;
        }
      }
    } catch {
      // Ignore parsing errors, use default message
    }

    if (response.status === 401) {
      throw new Error('Authentication failed. Check your API key with "kanban config show"');
    }

    if (response.status === 403) {
      throw new Error('Access denied. Check your API key permissions');
    }

    if (response.status === 404) {
      throw new Error('Resource not found');
    }

    if (response.status === 429) {
      throw new Error('Rate limit exceeded. Please try again later');
    }

    if (response.status >= 500) {
      throw new Error(`Server error: ${errorMessage}`);
    }

    throw new Error(errorMessage);
  }

  /**
   * Test connection to server
   */
