import type { Command } from 'commander';
import inquirer from 'inquirer';
import * as fs from 'fs';
import * as path from 'path';
import type { CliComponents, BackupInfo, BackupSchedule } from '../types';

interface CreateBackupOptions {
  compress?: boolean;
  verify?: boolean;
  description?: string;
}

interface ListBackupOptions {
  limit?: string;
  sort?: string;
  order?: string;
}

interface DeleteBackupOptions {
  force?: boolean;
}

interface ExportBackupOptions {
  format?: string;
}

interface RestoreBackupOptions {
  noVerify?: boolean;
  preserveExisting?: boolean;
  confirmed?: boolean;
  targetTime?: string;
}

interface CreateScheduleOptions {
  cron?: string;
  type?: string;
  description?: string;
  retention?: string;
  'no-compression'?: boolean;
  'no-verification'?: boolean;
  disabled?: boolean;
}

interface ListScheduleOptions {
  enabled?: boolean;
  disabled?: boolean;
  limit?: string;
}

export function registerBackupCommands(program: Command): void {
  const backupCmd = program.command('backup').alias('bak').description('Manage database backups');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  backupCmd
    .command('create [name]')
    .description('Create a new backup')
    .option('-c, --compress', 'compress backup file')
    .option('-v, --verify', 'verify backup after creation')
    .option('--description <desc>', 'backup description')
    .action(async (name?: string, options: CreateBackupOptions = {}) => {
      const { apiClient, formatter } = getComponents();

      try {
        const backupData: Partial<BackupInfo> = {};

        const backupName =
          name ||
          ((): string => {
            const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, 19);
            return `backup-${timestamp}`;
          })();

        backupData.name = backupName;
        backupData.compress = Boolean(options.compress);
        backupData.verify = Boolean(options.verify);

        if (options.description) {
          backupData.description = String(options.description);
        }

        formatter.info(`Creating backup: ${backupName}...`);

        const backup = await apiClient.request('/api/backup/create', {
          method: 'POST',
          body: backupData,
        });

        formatter.success(`Backup created successfully: ${backup.data.id}`);
        formatter.output(backup.data, {
          fields: ['id', 'name', 'size', 'compressed', 'verified', 'createdAt'],
          headers: ['ID', 'Name', 'Size', 'Compressed', 'Verified', 'Created'],
        });
      } catch (error) {
        formatter.error(
          `Failed to create backup: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  backupCmd
    .command('list')
    .alias('ls')
    .description('List all backups')
    .option('-l, --limit <number>', 'limit number of results', '20')
    .option('--sort <field>', 'sort by field', 'createdAt')
    .option('--order <direction>', 'sort order (asc/desc)', 'desc')
    .action(async (options: ListBackupOptions) => {
      const { apiClient, formatter } = getComponents();

      try {
        const params: Record<string, string> = {
          limit: options.limit ?? '20',
          sort: options.sort ?? 'createdAt',
          order: options.order ?? 'desc',
        };

        const backups = await apiClient.request('/api/backup/list', {
          params,
        });

        if (!backups.data || !Array.isArray(backups.data) || backups.data.length === 0) {
          formatter.info('No backups found');
          return;
        }

        formatter.output(backups.data, {
          fields: ['id', 'name', 'size', 'compressed', 'verified', 'createdAt'],
          headers: ['ID', 'Name', 'Size', 'Compressed', 'Verified', 'Created'],
        });
      } catch (error) {
        formatter.error(
          `Failed to list backups: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  backupCmd
    .command('restore <id>')
    .description('Restore from a backup')
    .option('-f, --force', 'skip confirmation')
    .option('--verify', 'verify backup before restore')
    .action(async (id: string, options: RestoreBackupOptions) => {
      const { apiClient, formatter } = getComponents();

      try {
        // Get backup info
        const backup = await apiClient.request(`/api/backup/${id}`);
        if (!backup.data) {
          formatter.error(`Backup ${id} not found`);
          process.exit(1);
        }

        if (!options.force) {
          formatter.warn('WARNING: This will replace all current data!');
          formatter.output(backup.data, {
            fields: ['id', 'name', 'size', 'createdAt'],
            headers: ['ID', 'Name', 'Size', 'Created'],
          });

          const { confirm } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirm',
              message: `Restore from backup "${backup.data.name}"? This cannot be undone!`,
              default: false,
            },
          ]);

          if (!confirm) {
            formatter.info('Restore cancelled');
            return;
          }
        }

        const restoreData = { verify: options.verify || false };

        formatter.info(`Restoring from backup: ${backup.data.name}...`);

        const result = await apiClient.request(`/api/backup/${id}/restore`, {
          method: 'POST',
          body: restoreData,
        });

        formatter.success('Database restored successfully');
        formatter.output(result);
      } catch (error) {
        formatter.error(
          `Failed to restore backup: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  backupCmd
    .command('delete <id>')
    .alias('rm')
    .description('Delete a backup')
    .option('-f, --force', 'skip confirmation')
    .action(async (id: string, options: DeleteBackupOptions) => {
      const { apiClient, formatter } = getComponents();

      try {
        if (!options.force) {
          const backup = await apiClient.request(`/api/backup/${id}`);
          if (!backup.data) {
            formatter.error(`Backup ${id} not found`);
            process.exit(1);
          }

          const { confirm } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirm',
              message: `Delete backup "${backup.data.name}"?`,
              default: false,
            },
          ]);

          if (!confirm) {
            formatter.info('Delete cancelled');
            return;
          }
        }

        await apiClient.request(`/api/backup/${id}`, { method: 'DELETE' });
        formatter.success(`Backup ${id} deleted successfully`);
      } catch (error) {
        formatter.error(
          `Failed to delete backup: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  backupCmd
    .command('verify <id>')
    .description('Verify backup integrity')
    .action(async (id: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        formatter.info(`Verifying backup: ${id}...`);

        const result = await apiClient.request(`/api/backup/${id}/verify`, {
          method: 'POST',
        });

        if (result.valid) {
          formatter.success('Backup verification passed');
        } else {
          formatter.error('Backup verification failed');
        }

        formatter.output(result);
      } catch (error) {
        formatter.error(
          `Failed to verify backup: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  backupCmd
    .command('export <id> [path]')
    .description('Export backup to file')
    .option('-f, --format <format>', 'export format (json|sql|csv)', 'json')
    .action(async (id: string, exportPath?: string, options: ExportBackupOptions = {}) => {
      const { apiClient, formatter } = getComponents();

      try {
        const backup = await apiClient.request(`/api/backup/${id}`);
        if (!backup.data) {
          formatter.error(`Backup ${id} not found`);
          process.exit(1);
        }

        const finalExportPath = exportPath || `${backup.data.name}.${options.format ?? 'json'}`;

        formatter.info(`Exporting backup to: ${finalExportPath}...`);

        const data = (await apiClient.request(`/api/backup/${id}/export`, {
          params: { format: options.format ?? 'json' },
        })) as unknown;

        // Ensure directory exists
        const dir = path.dirname(finalExportPath);
        if (!fs.existsSync(dir)) {
          fs.mkdirSync(dir, { recursive: true });
        }

        // Write file
        if (options.format === 'json') {
          fs.writeFileSync(finalExportPath, JSON.stringify(data, null, 2));
        } else {
          fs.writeFileSync(finalExportPath, data as string);
        }

        const stats = fs.statSync(finalExportPath);
        formatter.success(`Backup exported successfully to ${finalExportPath}`);
        formatter.info(`File size: ${(stats.size / 1024 / 1024).toFixed(2)} MB`);
      } catch (error) {
        formatter.error(
          `Failed to export backup: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  backupCmd
    .command('restore-to-time [targetTime]')
    .description('Restore database to specific point in time')
    .option('--no-verify', 'skip backup verification before restoration')
    .option('--preserve-existing', 'create backup of current state before restoration')
    .action(async (targetTime?: string, options: RestoreBackupOptions = {}) => {
      const { apiClient, formatter } = getComponents();

      try {
        let restoreTime = targetTime;

        // Interactive mode if no target time provided
        if (!restoreTime) {
          const now = new Date();
          const defaultTime = new Date(now.getTime() - 24 * 60 * 60 * 1000).toISOString(); // 24 hours ago

          const answers = await inquirer.prompt([
            {
              type: 'input',
              name: 'targetTime',
              message: 'Target restoration time (ISO format):',
              default: defaultTime,
              validate: (input: string): string | boolean => {
                const date = new Date(input);
                if (Number.isNaN(date.getTime())) {
                  return 'Invalid date format. Use ISO format (e.g., 2025-07-26T10:30:00Z)';
                }
                if (date > now) {
                  return 'Target time cannot be in the future';
                }
                return true;
              },
            },
            {
              type: 'confirm',
              name: 'verify',
              message: 'Verify backups before restoration?',
              default: !options.noVerify,
            },
            {
              type: 'confirm',
              name: 'preserveExisting',
              message: 'Create backup of current state before restoration?',
              default: options.preserveExisting || false,
            },
            {
              type: 'confirm',
              name: 'confirmed',
              message: 'This will replace the current database. Are you sure?',
              default: false,
            },
          ]);

          if (!answers.confirmed) {
            formatter.info('Point-in-time restoration cancelled');
            return;
          }

          restoreTime = answers.targetTime;
          // Note: Can't modify options object directly due to readonly, these values are handled in the API call
        } else {
          // Validate provided time
          const targetDate = new Date(restoreTime);
          if (Number.isNaN(targetDate.getTime())) {
            formatter.error(
              'Invalid target time format. Use ISO format (e.g., 2025-07-26T10:30:00Z)'
            );
            process.exit(1);
          }

          if (targetDate > new Date()) {
            formatter.error('Target time cannot be in the future');
            process.exit(1);
          }

          // Confirmation for non-interactive mode
          const { confirmed } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirmed',
              message: `Restore database to ${restoreTime}? This will replace the current database.`,
              default: false,
            },
          ]);

          if (!confirmed) {
            formatter.info('Point-in-time restoration cancelled');
            return;
          }
        }

        formatter.info(`Starting point-in-time restoration to: ${restoreTime}...`);

        const result = await apiClient.request('/api/backup/restore-to-time', {
          method: 'POST',
          body: JSON.stringify({
            targetTime: restoreTime,
            verify: !options.noVerify,
            preserveExisting: options.preserveExisting,
          }),
        });

        formatter.success('Point-in-time restoration completed successfully');
        formatter.info(`Database restored to: ${restoreTime}`);

        if (result.backupsApplied) {
          formatter.info(`Applied ${result.backupsApplied} backup(s)`);
        }
      } catch (error) {
        formatter.error('Point-in-time restoration failed');
        if (error instanceof Error) {
          formatter.error(error.message);
        }
        process.exit(1);
      }
    });

  // Schedule management commands
  backupCmd
    .command('schedule')
    .description('Manage backup schedules')
    .action((): void => {
      const { formatter } = getComponents();
      formatter.info('Available schedule commands:');
      formatter.info('  backup schedule create    - Create a new backup schedule');
      formatter.info('  backup schedule list      - List all backup schedules');
      formatter.info('  backup schedule show <id> - Show schedule details');
      formatter.info('  backup schedule update <id> - Update a schedule');
      formatter.info('  backup schedule delete <id> - Delete a schedule');
      formatter.info('  backup schedule run <id>  - Execute a schedule manually');
      formatter.info('  backup schedule start     - Start the backup scheduler');
      formatter.info('  backup schedule stop      - Stop the backup scheduler');
      formatter.info('  backup schedule cleanup   - Clean up old backups');
    });

  backupCmd
    .command('schedule create [name]')
    .description('Create a new backup schedule')
    .option('--cron <expression>', 'cron expression (e.g., "0 2 * * *" for daily at 2 AM)')
    .option('--type <type>', 'backup type (full, incremental)', 'full')
    .option('--description <desc>', 'schedule description')
    .option('--retention <days>', 'retention period in days', '30')
    .option('--no-compression', 'disable compression')
    .option('--no-verification', 'disable verification')
    .option('--disabled', 'create schedule as disabled')
    .action(async (name?: string, options: CreateScheduleOptions = {}) => {
      const { apiClient, formatter } = getComponents();

      try {
        let scheduleData: Partial<BackupSchedule> = {};

        // Interactive mode if no name provided
        if (!name || !options.cron) {
          const answers = await inquirer.prompt([
            {
              type: 'input',
              name: 'name',
              message: 'Schedule name:',
              default: name || `schedule-${Date.now()}`,
              validate: (input: string): string | boolean =>
                input.trim().length > 0 || 'Name is required',
            },
            {
              type: 'input',
              name: 'cronExpression',
              message: 'Cron expression (e.g., "0 2 * * *" for daily at 2 AM):',
              default: options.cron || '0 2 * * *',
              validate: (input: string): string | boolean => {
                // Basic validation - in production you'd want more thorough validation
                const parts = input.trim().split(' ');
                return parts.length === 5 || 'Invalid cron expression format (should have 5 parts)';
              },
            },
            {
              type: 'list',
              name: 'backupType',
              message: 'Backup type:',
              choices: ['full', 'incremental'],
              default: options.type || 'full',
            },
            {
              type: 'input',
              name: 'description',
              message: 'Description (optional):',
              default: options.description || '',
            },
            {
              type: 'number',
              name: 'retentionDays',
              message: 'Retention period (days):',
              default: parseInt(options.retention) || 30,
              validate: (input: number): string | boolean =>
                input > 0 || 'Retention days must be positive',
            },
            {
              type: 'confirm',
              name: 'compressionEnabled',
              message: 'Enable compression?',
              default: !options['no-compression'],
            },
            {
              type: 'confirm',
              name: 'verificationEnabled',
              message: 'Enable verification?',
              default: !options['no-verification'],
            },
            {
              type: 'confirm',
              name: 'enabled',
              message: 'Enable schedule immediately?',
              default: !options.disabled,
            },
          ]);

          scheduleData = answers;
        } else {
          scheduleData = {
            name,
            cronExpression: options.cron,
            backupType: options.type || 'full',
            description: options.description,
            retentionDays: parseInt(options.retention) || 30,
            compressionEnabled: !options['no-compression'],
            verificationEnabled: !options['no-verification'],
            enabled: !options.disabled,
          };
        }

        formatter.info(`Creating backup schedule: ${scheduleData.name}...`);

        const schedule = await apiClient.request('/api/schedule/create', {
          method: 'POST',
          body: JSON.stringify(scheduleData),
        });

        formatter.success('Backup schedule created successfully');
        formatter.output(schedule.data);
      } catch (error) {
        formatter.error('Failed to create backup schedule');
        process.exit(1);
      }
    });

  backupCmd
    .command('schedule list')
    .description('List all backup schedules')
    .option('--enabled', 'show only enabled schedules')
    .option('--disabled', 'show only disabled schedules')
    .option('--limit <count>', 'maximum number of schedules to show', '20')
    .action(async (options: ListScheduleOptions = {}) => {
      const { apiClient, formatter } = getComponents();

      try {
        const params: Record<string, string> = {};

        if (options.enabled) params.enabled = 'true';
        if (options.disabled) params.enabled = 'false';
        if (options.limit) params.limit = options.limit;

        const queryString = new URLSearchParams(params).toString();
        const url = `/api/schedule/list${queryString ? `?${queryString}` : ''}`;

        const response = await apiClient.request(url);
        const schedules = response.data;

        if (schedules.length === 0) {
          formatter.info('No backup schedules found');
          return;
        }

        formatter.success(`Found ${schedules.length} backup schedule(s)`);
        formatter.output(schedules);
      } catch (error) {
        formatter.error('Failed to list backup schedules');
        process.exit(1);
      }
    });

  backupCmd
    .command('schedule show <id>')
    .description('Show backup schedule details')
    .action(async (id: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        const response = await apiClient.request(`/api/schedule/${id}`);
        const schedule = response.data;

        formatter.output(schedule);
      } catch (error) {
        formatter.error('Failed to get backup schedule');
        process.exit(1);
      }
    });

  backupCmd
    .command('schedule run <id>')
    .description('Execute a backup schedule manually')
    .action(async (id: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        formatter.info(`Executing backup schedule: ${id}...`);

        await apiClient.request(`/api/schedule/${id}/execute`, {
          method: 'POST',
        });

        formatter.success('Backup schedule executed successfully');
      } catch (error) {
        formatter.error('Failed to execute backup schedule');
        process.exit(1);
      }
    });

  backupCmd
    .command('schedule start')
    .description('Start the backup scheduler')
    .action(async () => {
      const { apiClient, formatter } = getComponents();

      try {
        formatter.info('Starting backup scheduler...');

        await apiClient.request('/api/schedule/start', {
          method: 'POST',
        });

        formatter.success('Backup scheduler started successfully');
      } catch (error) {
        formatter.error('Failed to start backup scheduler');
        process.exit(1);
      }
    });

  backupCmd
    .command('schedule stop')
    .description('Stop the backup scheduler')
    .action(async () => {
      const { apiClient, formatter } = getComponents();

      try {
        formatter.info('Stopping backup scheduler...');

        await apiClient.request('/api/schedule/stop', {
          method: 'POST',
        });

        formatter.success('Backup scheduler stopped successfully');
      } catch (error) {
        formatter.error('Failed to stop backup scheduler');
        process.exit(1);
      }
    });

  backupCmd
    .command('schedule cleanup')
    .description('Clean up old backups based on retention policies')
    .action(async () => {
      const { apiClient, formatter } = getComponents();

      try {
        formatter.info('Cleaning up old backups...');

        await apiClient.request('/api/schedule/cleanup', {
          method: 'POST',
        });

        formatter.success('Backup cleanup completed successfully');
      } catch (error) {
        formatter.error('Failed to clean up old backups');
        process.exit(1);
      }
    });
}

import type { Command } from 'commander';
import inquirer from 'inquirer';
import * as React from 'react';
import { render } from 'ink';
import chalk from 'chalk';
import type { CliComponents } from '../types';
import { BoardView } from '../ui/components/BoardView';
import type { Board, Column } from '../ui/components/BoardView';
import type { Task } from '../ui/components/TaskList';
import { spinner } from '../utils/spinner';

interface ListBoardOptions {
  active?: boolean;
  archived?: boolean;
}

interface CreateBoardOptions {
  description?: string;
  template?: string;
}

interface ShowBoardOptions {
  tasks?: boolean;
  stats?: boolean;
}

interface UpdateBoardOptions {
  name?: string;
  description?: string;
}

interface BoardData {
  id: string;
  name: string;
  description?: string;
  archived: boolean;
  createdAt: string;
  updatedAt?: string;
  columns?: Column[];
  tasks?: Task[];
}

interface ApiColumnData {
  id: string;
  name: string;
  wip_limit?: number;
  tasks?: ApiTaskData[];
}

interface ApiTaskData {
  id: string;
  title: string;
  status: string;
  priority: string;
  assignee?: string;
  tags?: string[];
  due_date?: string;
}

interface ApiBoardResponse {
  id: string;
  name: string;
  description?: string;
  columns?: ApiColumnData[];
}

interface ViewBoardOptions {
  interactive?: boolean;
  wipLimits?: boolean;
  refresh?: string;
  maxHeight?: string;
  columnWidth?: string;
}

interface CreateBoardData {
  name?: string;
  description?: string;
  useAsDefault?: boolean;
}

interface QuickSetupDefaults {
  name?: string;
  description?: string;
  isPublic?: boolean;
  columns?: Array<{ name: string; order: number }>;
}

interface BoardSetupData {
  name: string;
  description?: string;
  isPublic: boolean;
  columns: Array<{ name: string; order: number }>;
}

export function registerBoardCommands(program: Command): void {
  const boardCmd = program.command('board').alias('b').description('Manage boards');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  boardCmd
    .command('list')
    .alias('ls')
    .description('List boards')
    .option('--active', 'show only active boards')
    .option('--archived', 'show only archived boards')
    .action(async (options: ListBoardOptions) => {
      const { apiClient, formatter } = getComponents();

      try {
        const response = await apiClient.getBoards();
        const boards = response.data as BoardData[];

        if (!boards || boards.length === 0) {
          formatter.info('No boards found');
          return;
        }

        // Filter based on options
        let filteredBoards = boards;
        if (options.active) {
          filteredBoards = boards.filter((board: BoardData) => !board.archived);
        } else if (options.archived) {
          filteredBoards = boards.filter((board: BoardData) => board.archived);
        }

        formatter.output(filteredBoards, {
          fields: ['id', 'name', 'description', 'archived', 'createdAt'],
          headers: ['ID', 'Name', 'Description', 'Archived', 'Created'],
        });
      } catch (error) {
        formatter.error(
          `Failed to list boards: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  boardCmd
    .command('show <id>')
    .description('Show board details')
    .option('--tasks', 'include tasks in board')
    .option('--stats', 'include board statistics')
    .action(async (id: string, options: ShowBoardOptions) => {
      const { apiClient, formatter } = getComponents();

      try {
        const response = await apiClient.getBoard(id);
        const board = response.data as BoardData;

        if (!board) {
          formatter.error(`Board ${id} not found`);
          process.exit(1);
        }

        formatter.output(board);

        if (options.tasks && board.tasks) {
          formatter.info('\n--- Tasks ---');
          formatter.output(board.tasks, {
            fields: ['id', 'title', 'status', 'priority'],
            headers: ['ID', 'Title', 'Status', 'Priority'],
          });
        }

        if (options.stats) {
          formatter.info('\n--- Statistics ---');
          try {
            const statsResponse = await apiClient.getBoardStats(id);
            formatter.output(statsResponse.data);
          } catch (error) {
            formatter.warn('Could not retrieve board statistics');
          }
        }
      } catch (error) {
        formatter.error(
          `Failed to get board: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  boardCmd
    .command('view [id]')
    .description('Interactive board view with live updates')
    .option('-i, --interactive', 'enable interactive mode (default)', true)
    .option('--no-interactive', 'disable interactive mode')
    .option('--wip-limits', 'show WIP limits', true)
    .option('--refresh <seconds>', 'auto-refresh interval', '30')
    .option('--max-height <number>', 'maximum column height', '8')
    .option('--column-width <number>', 'column width', '25')
    .action(async (id?: string, options?: ViewBoardOptions) => {
      const { config, apiClient, formatter } = getComponents();

      try {
        // Determine board ID
        const boardId = id || config.getDefaultBoard();
        if (!boardId) {
          formatter.error(
            'Board ID is required. Specify an ID or set default board with "kanban board use <id>"'
          );
          process.exit(1);
        }

        // Fetch board data with spinner
        const boardData = await spinner.withSpinner(
          `Loading board: ${boardId}`,
          apiClient.getBoard(boardId),
          {
            successText: 'Board loaded successfully',
            failText: 'Failed to load board',
          }
        );

        if (!boardData) {
          formatter.error(`Board ${boardId} not found`);
          process.exit(1);
        }

        // Transform API data to component format
        const apiResponse = boardData as ApiBoardResponse;
        const board: Board = {
          id: apiResponse.id,
          name: apiResponse.name,
          description: apiResponse.description,
          columns: (apiResponse.columns || []).map(
            (col: ApiColumnData): Column => ({
              id: col.id,
              name: col.name,
              limit: col.wip_limit,
              tasks: (col.tasks || []).map(
                (task: ApiTaskData): Task => ({
                  id: task.id,
                  title: task.title,
                  status: task.status,
                  priority: task.priority,
                  assignee: task.assignee,
                  tags: task.tags,
                  due_date: task.due_date,
                })
              ),
            })
          ),
        };

        if (!options?.interactive) {
          // Non-interactive mode - just show board data
          formatter.output(board);
          return;
        }

        // Interactive mode with React component
        let refreshInterval: NodeJS.Timeout | null = null;
        let shouldRefresh = false;

        const InteractiveBoardView = () => {
          const [currentBoard, setCurrentBoard] = React.useState<Board>(board);
          const [isLoading, setIsLoading] = React.useState(false);

          // Auto-refresh functionality
          React.useEffect(() => {
            if (options?.refresh && parseInt(options.refresh) > 0) {
              const interval = parseInt(options.refresh) * 1000;
              refreshInterval = setInterval(async () => {
                if (!shouldRefresh) return;

                setIsLoading(true);
                try {
                  const refreshedData = await apiClient.getBoard(boardId);
                  if (refreshedData) {
                    const apiResponse = refreshedData as ApiBoardResponse;
                    const refreshedBoard: Board = {
                      id: apiResponse.id,
                      name: apiResponse.name,
                      description: apiResponse.description,
                      columns: (apiResponse.columns || []).map(
                        (col: ApiColumnData): Column => ({
                          id: col.id,
                          name: col.name,
                          limit: col.wip_limit,
                          tasks: (col.tasks || []).map(
                            (task: ApiTaskData): Task => ({
                              id: task.id,
                              title: task.title,
                              status: task.status,
                              priority: task.priority,
                              assignee: task.assignee,
                              tags: task.tags,
                              due_date: task.due_date,
                            })
                          ),
                        })
                      ),
                    };
                    setCurrentBoard(refreshedBoard);
                  }
                } catch (error) {
                  // Silently fail refresh
                } finally {
                  setIsLoading(false);
                }
              }, interval);
            }

            return () => {
              if (refreshInterval) {
                clearInterval(refreshInterval);
              }
            };
          }, []);

          // Enable refresh after initial render
          React.useEffect(() => {
            shouldRefresh = true;
          }, []);

          return React.createElement(BoardView, {
            board: currentBoard,
            showWIPLimits: options?.wipLimits,
            maxColumnHeight: parseInt(options?.maxHeight || '8'),
            columnWidth: parseInt(options?.columnWidth || '25'),
            onTaskSelect: async (task, _columnId) => {
              try {
                // Show task details
                formatter.info(`\nTask: ${task.title}`);
                formatter.info(`Status: ${task.status}`);
                formatter.info(`Priority: ${task.priority || 'None'}`);
                if (task.assignee) formatter.info(`Assignee: ${task.assignee}`);
                if (task.tags?.length) formatter.info(`Tags: ${task.tags.join(', ')}`);
                if (task.due_date) formatter.info(`Due: ${task.due_date}`);
              } catch (error) {
                // Handle task selection error
              }
            },
            onColumnSelect: async column => {
              try {
                formatter.info(`\nColumn: ${column.name}`);
                formatter.info(`Tasks: ${column.tasks.length}`);
                if (column.limit) formatter.info(`WIP Limit: ${column.limit}`);
              } catch (error) {
                // Handle column selection error
              }
            },
            onKeyPress: async (key, context) => {
              switch (key) {
                case 'r':
                  // Manual refresh
                  setIsLoading(true);
                  try {
                    const refreshedData = await apiClient.getBoard(boardId);
                    if (refreshedData) {
                      const apiResponse = refreshedData as ApiBoardResponse;
                      const refreshedBoard: Board = {
                        id: apiResponse.id,
                        name: apiResponse.name,
                        description: apiResponse.description,
                        columns: (apiResponse.columns || []).map(
                          (col: ApiColumnData): Column => ({
                            id: col.id,
                            name: col.name,
                            limit: col.wip_limit,
                            tasks: (col.tasks || []).map(
                              (task: ApiTaskData): Task => ({
                                id: task.id,
                                title: task.title,
                                status: task.status,
                                priority: task.priority,
                                assignee: task.assignee,
                                tags: task.tags,
                                due_date: task.due_date,
                              })
                            ),
                          })
                        ),
                      };
                      setCurrentBoard(refreshedBoard);
                    }
                  } catch (error) {
                    // Handle refresh error
                  } finally {
                    setIsLoading(false);
                  }
                  break;
                case 'n':
                  // Create new task in selected column
                  if (context.selectedColumn) {
                    formatter.info(`\nCreate new task in column: ${context.selectedColumn.name}`);
                  }
                  break;
                case 'e':
                  // Edit selected task
                  if (context.selectedTask) {
                    formatter.info(`\nEdit task: ${context.selectedTask.title}`);
                  }
                  break;
                case 'd':
                  // Delete selected task
                  if (context.selectedTask) {
                    formatter.info(`\nDelete task: ${context.selectedTask.title}`);
                  }
                  break;
                case '?':
                  // Show help
                  formatter.info('\nKeyboard shortcuts:');
                  formatter.info('  â†/â†’ or h/l: Switch columns');
                  formatter.info('  â†‘/â†“ or j/k: Navigate tasks');
                  formatter.info('  Enter: Select task/column');
                  formatter.info('  r: Refresh board');
                  formatter.info('  n: New task in column');
                  formatter.info('  e: Edit selected task');
                  formatter.info('  d: Delete selected task');
                  formatter.info('  ?: Show this help');
                  formatter.info('  q: Quit');
                  break;
                default:
                  // Handle unknown key
                  break;
              }
            },
          });
        };

        // Show loading indicator and instructions
        formatter.info(`Starting interactive board view for: ${board.name}`);
        formatter.info('Press ? for help, q to quit');

        // Render the interactive board view
        render(React.createElement(InteractiveBoardView));
      } catch (error) {
        formatter.error(
          `Failed to start board view: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  boardCmd
    .command('create')
    .alias('new')
    .description('Create a new board')
    .option('-n, --name <name>', 'board name')
    .option('-d, --description <desc>', 'board description')
    .option('-i, --interactive', 'interactive mode')
    .action(async options => {
      const { config, apiClient, formatter } = getComponents();

      let boardData: CreateBoardData = {};

      if (options.interactive || !options.name) {
        const questions: Array<{
          type: string;
          name: string;
          message: string;
          validate?: (input: string) => boolean | string;
          default?: boolean;
        }> = [];

        if (!options.name) {
          questions.push({
            type: 'input',
            name: 'name',
            message: 'Board name:',
            validate: (input: string) => input.length > 0 || 'Name is required',
          });
        }

        if (!options.description) {
          questions.push({
            type: 'input',
            name: 'description',
            message: 'Board description (optional):',
          });
        }

        questions.push({
          type: 'confirm',
          name: 'useAsDefault',
          message: 'Set as default board?',
          default: false,
        });

        const answers = await inquirer.prompt(questions);
        boardData = { ...boardData, ...answers };
      }

      // Use command line options or answers
      boardData.name = options.name || boardData.name;
      boardData.description = options.description || boardData.description;

      try {
        const board = await apiClient.createBoard(boardData);
        formatter.success(`Board created successfully: ${(board as { id: string }).id}`);
        formatter.output(board);

        // Set as default if requested
        if (boardData.useAsDefault) {
          config.setDefaultBoard(board.id);
          formatter.info(`Set as default board`);
        }
      } catch (error) {
        formatter.error(
          `Failed to create board: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  boardCmd
    .command('update <id>')
    .description('Update a board')
    .option('-n, --name <name>', 'board name')
    .option('-d, --description <desc>', 'board description')
    .option('-i, --interactive', 'interactive mode')
    .action(async (id: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        // Get current board data
        const currentBoard = await apiClient.getBoard(id);
        if (!currentBoard) {
          formatter.error(`Board ${id} not found`);
          process.exit(1);
        }

        let updates: Record<string, unknown> = {};

        if (options.interactive) {
          const answers = await inquirer.prompt([
            {
              type: 'input',
              name: 'name',
              message: 'Board name:',
              default: currentBoard.name,
            },
            {
              type: 'input',
              name: 'description',
              message: 'Board description:',
              default: currentBoard.description || '',
            },
          ]);
          updates = answers;
        } else {
          // Use command line options
          if (options.name) updates.name = options.name;
          if (options.description) updates.description = options.description;
        }

        if (Object.keys(updates).length === 0) {
          formatter.warn('No updates specified');
          return;
        }

        const updatedBoard = await apiClient.updateBoard(id, updates);
        formatter.success('Board updated successfully');
        formatter.output(updatedBoard);
      } catch (error) {
        formatter.error(
          `Failed to update board: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  boardCmd
    .command('delete <id>')
    .alias('rm')
    .description('Delete a board')
    .option('-f, --force', 'skip confirmation')
    .action(async (id: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        if (!options.force) {
          const board = await apiClient.getBoard(id);
          if (!board) {
            formatter.error(`Board ${id} not found`);
            process.exit(1);
          }

          const { confirm } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirm',
              message: `Delete board "${board.name}"? This will also delete all tasks in the board.`,
              default: false,
            },
          ]);

          if (!confirm) {
            formatter.info('Delete cancelled');
            return;
          }
        }

        await apiClient.deleteBoard(id);
        formatter.success(`Board ${id} deleted successfully`);
      } catch (error) {
        formatter.error(
          `Failed to delete board: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  boardCmd
    .command('use <id>')
    .description('Set board as default')
    .action(async (id: string) => {
      const { config, apiClient, formatter } = getComponents();

      try {
        // Verify board exists
        const board = await apiClient.getBoard(id);
        if (!board) {
          formatter.error(`Board ${id} not found`);
          process.exit(1);
        }

        config.setDefaultBoard(id);
        formatter.success(`Default board set to "${board.name}" (${id})`);
      } catch (error) {
        formatter.error(
          `Failed to set default board: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  boardCmd
    .command('archive <id>')
    .description('Archive a board')
    .action(async (id: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        await apiClient.updateBoard(id, { archived: true });
        formatter.success(`Board ${id} archived successfully`);
      } catch (error) {
        formatter.error(
          `Failed to archive board: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  boardCmd
    .command('unarchive <id>')
    .description('Unarchive a board')
    .action(async (id: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        await apiClient.updateBoard(id, { archived: false });
        formatter.success(`Board ${id} unarchived successfully`);
      } catch (error) {
        formatter.error(
          `Failed to unarchive board: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  boardCmd
    .command('quick-setup')
    .alias('setup')
    .description('Quick interactive board setup with templates')
    .option('-n, --name <name>', 'board name')
    .option('-d, --description <desc>', 'board description')
    .option('--template <type>', 'use template (basic, scrum, bugs, content)')
    .option('--public', 'make board public')
    .option('--set-default', 'set as default board after creation')
    .action(async options => {
      const { config, apiClient, formatter } = getComponents();

      try {
        // Import the quick setup function
        const { quickBoardSetup, confirmAction } = await import('../prompts/board-prompts');

        // Prepare defaults from command line options
        const defaults: QuickSetupDefaults = {};
        if (options.name) defaults.name = options.name;
        if (options.description) defaults.description = options.description;
        if (options.public) defaults.isPublic = true;

        // If template is specified, we'll handle it differently
        if (options.template) {
          const templates = {
            basic: [
              { name: 'To Do', order: 0 },
              { name: 'In Progress', order: 1 },
              { name: 'Done', order: 2 },
            ],
            scrum: [
              { name: 'Backlog', order: 0 },
              { name: 'To Do', order: 1 },
              { name: 'In Progress', order: 2 },
              { name: 'Review', order: 3 },
              { name: 'Done', order: 4 },
            ],
            bugs: [
              { name: 'New', order: 0 },
              { name: 'Confirmed', order: 1 },
              { name: 'In Progress', order: 2 },
              { name: 'Testing', order: 3 },
              { name: 'Resolved', order: 4 },
            ],
            content: [
              { name: 'Ideas', order: 0 },
              { name: 'Writing', order: 1 },
              { name: 'Editing', order: 2 },
              { name: 'Review', order: 3 },
              { name: 'Published', order: 4 },
            ],
          };

          if (!templates[options.template]) {
            formatter.error(
              `Invalid template: ${options.template}. Available: ${Object.keys(templates).join(', ')}`
            );
            process.exit(1);
          }

          defaults.columns = templates[options.template];
        }

        formatter.info('Starting quick board setup...');

        // Run the interactive setup
        const boardData = await quickBoardSetup(defaults);

        // Show confirmation if not using command line options
        if (!options.name || !options.template) {
          const confirmed = await confirmAction('Create board with these settings?', true);

          if (!confirmed) {
            formatter.warn('Board creation cancelled');
            return;
          }
        }

        // Transform data for API
        const createData = {
          name: boardData.name,
          description: boardData.description,
          isPublic: boardData.isPublic,
          columns: boardData.columns.map(col => ({
            name: col.name,
            order: col.order,
          })),
        };

        // Create the board with spinner
        const board = await spinner.withSpinner(
          `Creating board: ${boardData.name}`,
          apiClient.createBoard(createData),
          {
            successText: 'Board created successfully! ðŸŽ‰',
            failText: 'Failed to create board',
          }
        );

        formatter.success(
          `Board "${boardData.name}" created with ID: ${(board as { id: string }).id}`
        );

        // Show board details
        formatter.info('\nðŸ“Š Board Summary:');
        formatter.info(`Name: ${chalk.bold(boardData.name)}`);
        if (boardData.description) {
          formatter.info(`Description: ${boardData.description}`);
        }
        formatter.info(`Visibility: ${boardData.isPublic ? 'Public' : 'Private'}`);
        formatter.info(`Columns: ${boardData.columns.map(c => c.name).join(' â†’ ')}`);

        // Set as default if requested
        if (options.setDefault) {
          config.setDefaultBoard((board as { id: string }).id);
          formatter.success('Set as default board');
        } else {
          // Ask if they want to set it as default
          const setAsDefault = await confirmAction('Set this board as your default?', false);

          if (setAsDefault) {
            config.setDefaultBoard((board as { id: string }).id);
            formatter.success('Set as default board');
          }
        }

        // Offer quick actions
        formatter.info('\nðŸ’¡ Quick Actions:');
        formatter.info(`  View board: kanban board view ${(board as { id: string }).id}`);
        formatter.info(`  Create task: kanban task create --interactive`);
        formatter.info(`  List tasks: kanban task list`);

        formatter.output(board);
      } catch (error) {
        if (error.message === 'Board setup cancelled') {
          formatter.warn('Board setup cancelled');
          return;
        }

        formatter.error(
          `Failed to setup board: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });
}

import type { Command } from 'commander';
import inquirer from 'inquirer';
import type { CliComponents } from '../types';

export function registerConfigCommands(program: Command): void {
  const configCmd = program.command('config').description('Manage CLI configuration');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  configCmd
    .command('show')
    .description('Show current configuration')
    .option('--path', 'show config file path')
    .action(async options => {
      const { config, formatter } = getComponents();

      if (options.path) {
        console.log(config.getConfigPath());
        return;
      }

      if (!config.exists()) {
        formatter.warn('No configuration file found');
        console.log('Run "kanban config init" to create initial configuration');
        return;
      }

      const configData = config.getAll();
      const validation = config.validate();

      if (!validation.valid) {
        formatter.warn('Configuration has issues:');
        validation.errors.forEach(error => formatter.error(error));
        console.log();
      }

      formatter.output(configData);
    });

  configCmd
    .command('set <key> <value>')
    .description('Set configuration value')
    .action(async (key: string, value: string) => {
      const { config, formatter, apiClient } = getComponents();

      try {
        // Parse value based on type
        let parsedValue: any = value;

        if (value === 'true') parsedValue = true;
        else if (value === 'false') parsedValue = false;
        else if (/^\d+$/.test(value)) parsedValue = parseInt(value, 10);
        else if (/^\d*\.\d+$/.test(value)) parsedValue = parseFloat(value);

        config.set(key, parsedValue);
        config.save();

        // Update API client if server config changed
        if (key.startsWith('server.') || key.startsWith('auth.')) {
          apiClient.updateConfig();
        }

        formatter.success(`Set ${key} = ${parsedValue}`);

        // Validate after setting
        const validation = config.validate();
        if (!validation.valid) {
          formatter.warn('Configuration validation failed:');
          validation.errors.forEach(error => formatter.error(error));
        }
      } catch (error) {
        formatter.error(
          `Failed to set configuration: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  configCmd
    .command('get <key>')
    .description('Get configuration value')
    .action(async (key: string) => {
      const { config, formatter } = getComponents();

      if (!config.exists()) {
        formatter.error('No configuration found');
        process.exit(1);
      }

      const value = config.get(key);
      if (value === undefined) {
        formatter.error(`Configuration key "${key}" not found`);
        process.exit(1);
      }

      formatter.output({ [key]: value });
    });

  configCmd
    .command('init')
    .description('Initialize configuration interactively')
    .option('--force', 'overwrite existing configuration')
    .action(async options => {
      const { config, formatter, apiClient } = getComponents();

      if (config.exists() && !options.force) {
        const { overwrite } = await inquirer.prompt([
          {
            type: 'confirm',
            name: 'overwrite',
            message: 'Configuration already exists. Overwrite?',
            default: false,
          },
        ]);

        if (!overwrite) {
          formatter.info('Configuration initialization cancelled');
          return;
        }
      }

      formatter.info('Initializing MCP Kanban CLI configuration...');

      const answers = await inquirer.prompt([
        {
          type: 'input',
          name: 'serverUrl',
          message: 'Server URL:',
          default: 'http://localhost:3000',
          validate: (input: string) => {
            try {
              new URL(input);
              return true;
            } catch {
              return 'Please enter a valid URL';
            }
          },
        },
        {
          type: 'input',
          name: 'apiKey',
          message: 'API Key (optional):',
        },
        {
          type: 'list',
          name: 'format',
          message: 'Default output format:',
          choices: ['table', 'json', 'csv'],
          default: 'table',
        },
        {
          type: 'confirm',
          name: 'verbose',
          message: 'Enable verbose output by default?',
          default: false,
        },
        {
          type: 'confirm',
          name: 'gitEnabled',
          message: 'Enable Git integration?',
          default: true,
        },
      ]);

      // Update configuration
      config.set('server.url', answers.serverUrl);
      if (answers.apiKey) {
        config.set('auth.apiKey', answers.apiKey);
      }
      config.set('defaults.format', answers.format);
      config.set('defaults.verbose', answers.verbose);
      config.set('git.enabled', answers.gitEnabled);

      try {
        config.save();
        apiClient.updateConfig();
        formatter.success('Configuration saved successfully');

        // Test connection if API key provided
        if (answers.apiKey) {
          formatter.info('Testing connection...');
          try {
            const connected = await apiClient.testConnection();
            if (connected) {
              formatter.success('Successfully connected to server');
            } else {
              formatter.warn('Could not connect to server');
            }
          } catch (error) {
            formatter.warn(
              `Connection test failed: ${error instanceof Error ? error.message : 'Unknown error'}`
            );
          }
        }
      } catch (error) {
        formatter.error(
          `Failed to save configuration: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  configCmd
    .command('validate')
    .description('Validate configuration')
    .action(async () => {
      const { config, formatter } = getComponents();

      if (!config.exists()) {
        formatter.error('No configuration found');
        process.exit(1);
      }

      const validation = config.validate();

      if (validation.valid) {
        formatter.success('Configuration is valid');
      } else {
        formatter.error('Configuration validation failed:');
        validation.errors.forEach(error => formatter.error(`  ${error}`));
        process.exit(1);
      }
    });

  configCmd
    .command('reset')
    .description('Reset configuration to defaults')
    .option('--force', 'skip confirmation')
    .action(async options => {
      const { config, formatter } = getComponents();

      if (!options.force) {
        const { confirm } = await inquirer.prompt([
          {
            type: 'confirm',
            name: 'confirm',
            message: 'Are you sure you want to reset configuration to defaults?',
            default: false,
          },
        ]);

        if (!confirm) {
          formatter.info('Reset cancelled');
          return;
        }
      }

      try {
        config.reset();
        config.save();
        formatter.success('Configuration reset to defaults');
      } catch (error) {
        formatter.error(
          `Failed to reset configuration: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  configCmd
    .command('test')
    .description('Test connection to server')
    .action(async () => {
      const { config, formatter, apiClient } = getComponents();

      if (!config.exists()) {
        formatter.error('No configuration found. Run "kanban config init" first');
        process.exit(1);
      }

      const validation = config.validate();
      if (!validation.valid) {
        formatter.error('Configuration is invalid:');
        validation.errors.forEach(error => formatter.error(`  ${error}`));
        process.exit(1);
      }

      formatter.info(`Testing connection to ${config.getServerUrl()}...`);

      try {
        const connected = await apiClient.testConnection();

        if (connected) {
          formatter.success('âœ“ Connection successful');

          // Get detailed health info
          try {
            const health = await apiClient.getHealth();
            formatter.info('Server information:');
            formatter.output(health);
          } catch (error) {
            formatter.warn('Could not get detailed server information');
          }
        } else {
          formatter.error('âœ— Connection failed');
          process.exit(1);
        }
      } catch (error) {
        formatter.error(
          `âœ— Connection failed: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });
}

