import type { Command } from 'commander';
import inquirer from 'inquirer';
import type {
  CliComponents,
  DatabaseOptimizationResult,
  DatabaseVacuumResult,
  DatabaseAnalysisResult,
  DatabaseStats,
  DatabaseIntegrityResult,
  DatabaseRepairResult,
  Migration,
  MigrationResult,
} from '../types';

export function registerDatabaseCommands(program: Command): void {
  const dbCmd = program.command('db').description('Database management commands');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  dbCmd
    .command('optimize')
    .description('Optimize database performance')
    .option('-v, --verbose', 'verbose output')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        formatter.info('Optimizing database...');

        const result = await apiClient.request('/api/database/optimize', {
          method: 'POST',
          body: { verbose: options.verbose || false },
        });

        formatter.success('Database optimization completed');
        formatter.output(result, {
          fields: ['operation', 'duration', 'before', 'after', 'improvement'],
          headers: ['Operation', 'Duration', 'Before', 'After', 'Improvement'],
        });
      } catch (error) {
        formatter.error(
          `Failed to optimize database: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  dbCmd
    .command('vacuum')
    .description('Vacuum database to reclaim space')
    .option('-f, --force', 'skip confirmation')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        if (!options.force) {
          const { confirm } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirm',
              message: 'Vacuum database? This may take some time.',
              default: true,
            },
          ]);

          if (!confirm) {
            formatter.info('Vacuum cancelled');
            return;
          }
        }

        formatter.info('Vacuuming database...');

        const result = await apiClient.request('/api/database/vacuum', {
          method: 'POST',
        });

        formatter.success('Database vacuum completed');
        formatter.output(result, {
          fields: ['sizeBefore', 'sizeAfter', 'spaceReclaimed', 'duration'],
          headers: ['Size Before', 'Size After', 'Space Reclaimed', 'Duration'],
        });
      } catch (error) {
        formatter.error(
          `Failed to vacuum database: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  dbCmd
    .command('analyze')
    .description('Analyze database and update statistics')
    .action(async () => {
      const { apiClient, formatter } = getComponents();

      try {
        formatter.info('Analyzing database...');

        const result = await apiClient.request('/api/database/analyze', {
          method: 'POST',
        });

        formatter.success('Database analysis completed');
        formatter.output(result, {
          fields: ['table', 'rowCount', 'indexCount', 'avgRowSize', 'totalSize'],
          headers: ['Table', 'Rows', 'Indexes', 'Avg Row Size', 'Total Size'],
        });
      } catch (error) {
        formatter.error(
          `Failed to analyze database: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  dbCmd
    .command('stats')
    .description('Show database statistics')
    .option('--tables', 'include table statistics')
    .option('--indexes', 'include index statistics')
    .option('--performance', 'include performance metrics')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        const params: Record<string, string> = {};
        if (options.tables) params.tables = 'true';
        if (options.indexes) params.indexes = 'true';
        if (options.performance) params.performance = 'true';

        const stats = await apiClient.request('/api/database/stats', { params });

        formatter.info('Database Statistics:');

        // General stats
        if (stats.general) {
          formatter.output(stats.general, {
            fields: ['metric', 'value'],
            headers: ['Metric', 'Value'],
          });
        }

        // Table stats
        if (stats.tables && options.tables) {
          console.log('\n--- Table Statistics ---');
          formatter.output(stats.tables, {
            fields: ['name', 'rowCount', 'size', 'lastModified'],
            headers: ['Table', 'Rows', 'Size', 'Last Modified'],
          });
        }

        // Index stats
        if (stats.indexes && options.indexes) {
          console.log('\n--- Index Statistics ---');
          formatter.output(stats.indexes, {
            fields: ['name', 'table', 'size', 'usage'],
            headers: ['Index', 'Table', 'Size', 'Usage'],
          });
        }

        // Performance metrics
        if (stats.performance && options.performance) {
          console.log('\n--- Performance Metrics ---');
          formatter.output(stats.performance, {
            fields: ['metric', 'value', 'unit'],
            headers: ['Metric', 'Value', 'Unit'],
          });
        }
      } catch (error) {
        formatter.error(
          `Failed to get database stats: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  dbCmd
    .command('check')
    .description('Check database integrity')
    .option('--repair', 'attempt to repair corruption if found')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        formatter.info('Checking database integrity...');

        const result = await apiClient.request('/api/database/check', {
          method: 'POST',
          body: { repair: options.repair || false },
        });

        if (result.healthy) {
          formatter.success('Database integrity check passed');
        } else {
          formatter.error('Database integrity issues found');
        }

        formatter.output(result, {
          fields: ['check', 'status', 'details'],
          headers: ['Check', 'Status', 'Details'],
        });

        if (result.issues && result.issues.length > 0) {
          console.log('\n--- Issues Found ---');
          formatter.output(result.issues, {
            fields: ['type', 'severity', 'message', 'suggestion'],
            headers: ['Type', 'Severity', 'Message', 'Suggestion'],
          });
        }
      } catch (error) {
        formatter.error(
          `Failed to check database: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  dbCmd
    .command('repair')
    .description('Repair database corruption')
    .option('-f, --force', 'skip confirmation')
    .option('--backup', 'create backup before repair')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        if (!options.force) {
          formatter.warn('WARNING: Database repair may cause data loss!');

          const { confirm } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirm',
              message: 'Proceed with database repair?',
              default: false,
            },
          ]);

          if (!confirm) {
            formatter.info('Repair cancelled');
            return;
          }
        }

        const repairData = {
          createBackup: options.backup || false,
        };

        formatter.info('Repairing database...');

        const result = await apiClient.request('/api/database/repair', {
          method: 'POST',
          body: repairData,
        });

        formatter.success('Database repair completed');
        formatter.output(result, {
          fields: ['operation', 'status', 'recordsFixed', 'backupCreated'],
          headers: ['Operation', 'Status', 'Records Fixed', 'Backup Created'],
        });
      } catch (error) {
        formatter.error(
          `Failed to repair database: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  // Migration commands
  const migrateCmd = dbCmd.command('migrate').description('Database migration management');

  migrateCmd
    .command('status')
    .description('Show migration status')
    .action(async () => {
      const { apiClient, formatter } = getComponents();

      try {
        const migrations = await apiClient.request('/api/database/migrations/status');

        if (!migrations || migrations.length === 0) {
          formatter.info('No migrations found');
          return;
        }

        formatter.output(migrations, {
          fields: ['name', 'version', 'status', 'appliedAt'],
          headers: ['Migration', 'Version', 'Status', 'Applied At'],
        });
      } catch (error) {
        formatter.error(
          `Failed to get migration status: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  migrateCmd
    .command('up')
    .description('Run pending migrations')
    .option('--to <version>', 'migrate to specific version')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        const migrateData: { targetVersion?: string } = {};
        if (options.to) {
          migrateData.targetVersion = options.to;
        }

        formatter.info('Running migrations...');

        const result = await apiClient.request('/api/database/migrations/up', {
          method: 'POST',
          body: migrateData,
        });

        formatter.success('Migrations completed');
        formatter.output(result, {
          fields: ['migration', 'status', 'duration'],
          headers: ['Migration', 'Status', 'Duration'],
        });
      } catch (error) {
        formatter.error(
          `Failed to run migrations: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  migrateCmd
    .command('down')
    .description('Rollback migrations')
    .option('--to <version>', 'rollback to specific version')
    .option('-f, --force', 'skip confirmation')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        if (!options.force) {
          formatter.warn('WARNING: Rolling back migrations may cause data loss!');

          const { confirm } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirm',
              message: 'Proceed with migration rollback?',
              default: false,
            },
          ]);

          if (!confirm) {
            formatter.info('Rollback cancelled');
            return;
          }
        }

        const rollbackData: { targetVersion?: string } = {};
        if (options.to) {
          rollbackData.targetVersion = options.to;
        }

        formatter.info('Rolling back migrations...');

        const result = await apiClient.request('/api/database/migrations/down', {
          method: 'POST',
          body: rollbackData,
        });

        formatter.success('Migration rollback completed');
        formatter.output(result, {
          fields: ['migration', 'status', 'duration'],
          headers: ['Migration', 'Status', 'Duration'],
        });
      } catch (error) {
        formatter.error(
          `Failed to rollback migrations: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  migrateCmd
    .command('create <name>')
    .description('Create a new migration')
    .action(async (name: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        const migration = await apiClient.request('/api/database/migrations/create', {
          method: 'POST',
          body: { name },
        });

        formatter.success(`Migration created: ${migration.filename}`);
        formatter.output(migration, {
          fields: ['name', 'version', 'filename', 'createdAt'],
          headers: ['Name', 'Version', 'Filename', 'Created At'],
        });
      } catch (error) {
        formatter.error(
          `Failed to create migration: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });
}

import type { Command } from 'commander';
import fs from 'fs/promises';
import path from 'path';
import type {
  CliComponents,
  ExportParams,
  ExportResponse,
  ImportValidationResponse,
  ImportResponse,
} from '../types';

export function registerExportCommands(program: Command): void {
  const exportCmd = program.command('export').description('Export kanban data');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;
  const importCmd = program.command('import').description('Import kanban data');

  // Export to JSON
  exportCmd
    .command('json [file]')
    .description('Export data to JSON format')
    .option('--boards', 'Include boards', true)
    .option('--tasks', 'Include tasks', true)
    .option('--tags', 'Include tags', true)
    .option('--notes', 'Include notes', true)
    .option('--board-ids <ids...>', 'Filter by board IDs')
    .option('--pretty', 'Pretty print JSON', true)
    .action(async (file, options) => {
      try {
        const { apiClient, formatter } = getComponents();

        formatter.info('Exporting data to JSON...');

        const params: ExportParams = {
          format: 'json',
          includeBoards: options.boards,
          includeTasks: options.tasks,
          includeTags: options.tags,
          includeNotes: options.notes,
        };

        if (options.boardIds) {
          params.boardIds = options.boardIds;
        }

        const response = await apiClient.get('/export', { params });

        if (file) {
          const outputPath = path.resolve(file);
          const jsonData = options.pretty
            ? JSON.stringify(response.data, null, 2)
            : JSON.stringify(response.data);

          await fs.writeFile(outputPath, jsonData);
          formatter.success(`Data exported to ${outputPath}`);
        } else {
          formatter.output(JSON.stringify(response.data, null, 2));
        }
      } catch (error) {
        const { formatter } = getComponents();
        formatter.error(
          `Export failed: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  // Export to CSV
  exportCmd
    .command('csv <directory>')
    .description('Export data to CSV files')
    .option('--boards', 'Include boards', true)
    .option('--tasks', 'Include tasks', true)
    .option('--tags', 'Include tags', true)
    .option('--notes', 'Include notes', true)
    .action(async (_directory, options) => {
      try {
        const { apiClient, formatter } = getComponents();

        formatter.info('Exporting data to CSV...');

        const params: ExportParams = {
          format: 'csv',
          includeBoards: options.boards,
          includeTasks: options.tasks,
          includeTags: options.tags,
          includeNotes: options.notes,
        };

        const response = await apiClient.get('/export', { params });

        formatter.success('CSV export completed');
        formatter.info(`Files: ${response.filePath}`);
        formatter.info(`Items: ${response.itemCount}`);
      } catch (error) {
        const { formatter } = getComponents();
        formatter.error(
          `Export failed: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  // Import from JSON
  importCmd
    .command('json <file>')
    .description('Import data from JSON file')
    .option('--validate-only', 'Only validate without importing')
    .option('--conflict <resolution>', 'Conflict resolution (skip/overwrite/rename)', 'skip')
    .action(async (file, options) => {
      try {
        const { apiClient, formatter } = getComponents();

        formatter.info('Reading JSON file...');

        const filePath = path.resolve(file);
        const fileContent = await fs.readFile(filePath, 'utf-8');

        // Parse to validate JSON
        JSON.parse(fileContent);

        formatter.info('Uploading for import...');

        const formData = new FormData();
        formData.append('file', new Blob([fileContent]), path.basename(filePath));
        formData.append('format', 'json');
        formData.append('conflictResolution', options.conflict);

        if (options.validateOnly) {
          formData.append('validateOnly', 'true');
        }

        const endpoint = options.validateOnly ? '/import/validate' : '/import';
        const response = await apiClient.post(endpoint, formData);

        if (options.validateOnly) {
          const validationData = response.data as ImportValidationResponse;
          formatter.success('Validation completed');
          formatter.info(`Valid: ${validationData.valid}`);
          formatter.info(`Would import: ${validationData.wouldImport} items`);
          formatter.info(`Would skip: ${validationData.wouldSkip} items`);

          if (validationData.errors.length > 0) {
            formatter.error('Validation errors:');
            validationData.errors.forEach((err: string) => {
              formatter.error(`  â€¢ ${err}`);
            });
          }
        } else {
          const importData = response.data as ImportResponse;
          formatter.success('Import completed');
          formatter.info(`Imported: ${importData.imported} items`);
          formatter.info(`Skipped: ${importData.skipped} items`);

          if (importData.errors.length > 0) {
            formatter.error('Import errors:');
            importData.errors.forEach((err: string) => {
              formatter.error(`  â€¢ ${err}`);
            });
          }
        }
      } catch (error) {
        const { formatter } = getComponents();
        formatter.error(
          `Import failed: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });
}

import React, { useState, useEffect } from 'react';
import { render, useApp, useInput } from 'ink';
import { Command } from 'commander';
import chalk from 'chalk';
import TaskList, { type Task } from '../ui/components/TaskList';
import BoardView, { type Board, type Column } from '../ui/components/BoardView';
import StatusIndicator from '../ui/components/StatusIndicator';

interface InteractiveViewProps {
  mode: 'tasks' | 'board';
  data: any;
}

const InteractiveView: React.FC<InteractiveViewProps> = ({ mode, data }) => {
  const [currentView, setCurrentView] = useState<'tasks' | 'board' | 'help'>(mode);
  const [statusMessage, setStatusMessage] = useState<string>('');
  const [statusType, setStatusType] = useState<'info' | 'success' | 'error' | 'loading'>('info');
  const { exit } = useApp();

  useInput((input, key) => {
    if (input === 'q' && !key.ctrl) {
      exit();
    } else if (input === '1') {
      setCurrentView('tasks');
      setStatusMessage('Switched to Task List view');
      setStatusType('info');
    } else if (input === '2') {
      setCurrentView('board');
      setStatusMessage('Switched to Board view');
      setStatusType('info');
    } else if (input === '?' || input === 'h') {
      setCurrentView('help');
      setStatusMessage('Showing help');
      setStatusType('info');
    } else if (input === 'r') {
      setStatusMessage('Refreshing data...');
      setStatusType('loading');
      // In a real app, this would fetch fresh data
      setTimeout(() => {
        setStatusMessage('Data refreshed');
        setStatusType('success');
      }, 1000);
    }
  });

  const handleTaskSelect = (task: Task) => {
    setStatusMessage(`Selected task: ${task.title}`);
    setStatusType('success');
  };

  const handleBoardTaskSelect = (task: Task, columnId: string) => {
    setStatusMessage(`Selected task: ${task.title} in column ${columnId}`);
    setStatusType('success');
  };

  const handleColumnSelect = (column: Column) => {
    setStatusMessage(`Selected column: ${column.name} (${column.tasks.length} tasks)`);
    setStatusType('info');
  };

  const renderHelp = () => (
    <div>
      <h2>{chalk.cyan('ðŸŽ® Interactive View Help')}</h2>
      <br />

      <h3>{chalk.yellow('Global Controls:')}</h3>
      <p>
        â€¢ <strong>1</strong> - Switch to Task List view
      </p>
      <p>
        â€¢ <strong>2</strong> - Switch to Board view
      </p>
      <p>
        â€¢ <strong>h</strong> or <strong>?</strong> - Show this help
      </p>
      <p>
        â€¢ <strong>r</strong> - Refresh data
      </p>
      <p>
        â€¢ <strong>q</strong> - Quit
      </p>
      <br />

      <h3>{chalk.yellow('Task List Controls:')}</h3>
      <p>
        â€¢ <strong>â†‘/â†“</strong> or <strong>j/k</strong> - Navigate tasks
      </p>
      <p>
        â€¢ <strong>Enter</strong> - Select task
      </p>
      <br />

      <h3>{chalk.yellow('Board Controls:')}</h3>
      <p>
        â€¢ <strong>â†/â†’</strong> or <strong>h/l</strong> - Switch columns
      </p>
      <p>
        â€¢ <strong>â†‘/â†“</strong> or <strong>j/k</strong> - Navigate tasks
      </p>
      <p>
        â€¢ <strong>Enter</strong> - Select task or column
      </p>
      <br />

      <p>{chalk.gray('Press any key to return to previous view')}</p>
    </div>
  );

  const renderCurrentView = () => {
    switch (currentView) {
      case 'tasks':
        return (
          <TaskList
            tasks={data.tasks || []}
            title="Interactive Task List"
            onTaskSelect={handleTaskSelect}
            showSelection={true}
            maxHeight={15}
            showStats={true}
          />
        );

      case 'board':
        return (
          <BoardView
            board={data.board || { id: '1', name: 'Sample Board', columns: [] }}
            onTaskSelect={handleBoardTaskSelect}
            onColumnSelect={handleColumnSelect}
            showWIPLimits={true}
            maxColumnHeight={10}
            columnWidth={25}
          />
        );

      case 'help':
        return renderHelp();

      default:
        return <div>Unknown view</div>;
    }
  };

  return (
    <div>
      {/* Header */}
      <div style={{ marginBottom: 1 }}>
        <h1>{chalk.bold.cyan('ðŸš€ Kanban CLI Interactive Mode')}</h1>
        <p>{chalk.gray(`Current view: ${currentView} | Press 'h' for help | Press 'q' to quit`)}</p>
      </div>

      {/* Status message */}
      {statusMessage && (
        <div style={{ marginBottom: 1 }}>
          <StatusIndicator status={statusType} message={statusMessage} />
        </div>
      )}

      {/* Main content */}
      {renderCurrentView()}

      {/* Footer */}
      <div style={{ marginTop: 1 }}>
        <p>{chalk.gray('â”€'.repeat(80))}</p>
        <p>{chalk.gray('Interactive Kanban CLI - Press h for help, q to quit')}</p>
      </div>
    </div>
  );
};

