import type { Command } from 'commander';
import inquirer from 'inquirer';
import type { CliComponents } from '../types';

export function registerNoteCommands(program: Command): void {
  const noteCmd = program.command('note').alias('n').description('Manage notes');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  noteCmd
    .command('list')
    .alias('ls')
    .description('List notes')
    .option('-c, --category <category>', 'filter by category')
    .option('-t, --task <taskId>', 'filter by task ID')
    .option('--pinned', 'show only pinned notes')
    .option('-l, --limit <number>', 'limit number of results', '20')
    .option('--sort <field>', 'sort by field', 'createdAt')
    .option('--order <direction>', 'sort order (asc/desc)', 'desc')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        const params: Record<string, string> = {
          limit: options.limit,
          sort: options.sort,
          order: options.order,
        };

        if (options.category) params.category = options.category;
        if (options.task) params.taskId = options.task;
        if (options.pinned) params.pinned = 'true';

        const notes = (await apiClient.getNotes(params)) as any;

        if (!notes || notes.length === 0) {
          formatter.info('No notes found');
          return;
        }

        formatter.output(notes, {
          fields: ['id', 'title', 'category', 'pinned', 'taskId', 'createdAt'],
          headers: ['ID', 'Title', 'Category', 'Pinned', 'Task ID', 'Created'],
        });
      } catch (error) {
        formatter.error(
          `Failed to list notes: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  noteCmd
    .command('show <id>')
    .description('Show note details')
    .action(async (id: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        const note = (await apiClient.getNote(id)) as any;

        if (!note) {
          formatter.error(`Note ${id} not found`);
          process.exit(1);
        }

        formatter.output(note);
      } catch (error) {
        formatter.error(
          `Failed to get note: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  noteCmd
    .command('add')
    .alias('create')
    .description('Add a new note')
    .option('-t, --title <title>', 'note title')
    .option('-c, --content <content>', 'note content')
    .option('--category <category>', 'note category')
    .option('--task <taskId>', 'link to task ID')
    .option('--pin', 'pin the note')
    .option('-i, --interactive', 'interactive mode')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      let noteData: any = {};

      if (options.interactive || !options.title) {
        const questions: any[] = [];

        if (!options.title) {
          questions.push({
            type: 'input',
            name: 'title',
            message: 'Note title:',
            validate: (input: string) => input.length > 0 || 'Title is required',
          });
        }

        if (!options.content) {
          questions.push({
            type: 'editor',
            name: 'content',
            message: 'Note content:',
          });
        }

        if (!options.category) {
          questions.push({
            type: 'list',
            name: 'category',
            message: 'Note category:',
            choices: ['general', 'meeting', 'idea', 'bug', 'feature', 'docs'],
            default: 'general',
          });
        }

        if (!options.task) {
          questions.push({
            type: 'input',
            name: 'taskId',
            message: 'Link to task ID (optional):',
          });
        }

        if (!options.pin) {
          questions.push({
            type: 'confirm',
            name: 'pinned',
            message: 'Pin this note?',
            default: false,
          });
        }

        const answers = await inquirer.prompt(questions);
        noteData = { ...noteData, ...answers };
      }

      // Use command line options or answers
      noteData.title = options.title || noteData.title;
      noteData.content = options.content || noteData.content;
      noteData.category = options.category || noteData.category || 'general';
      noteData.taskId = options.task || noteData.taskId;
      noteData.pinned = options.pin || noteData.pinned || false;

      try {
        const note = (await apiClient.createNote(noteData)) as any;
        formatter.success(`Note created successfully: ${note.id}`);
        formatter.output(note);
      } catch (error) {
        formatter.error(
          `Failed to create note: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  noteCmd
    .command('update <id>')
    .description('Update a note')
    .option('-t, --title <title>', 'note title')
    .option('-c, --content <content>', 'note content')
    .option('--category <category>', 'note category')
    .option('--task <taskId>', 'link to task ID')
    .option('--pin', 'pin the note')
    .option('--unpin', 'unpin the note')
    .option('-i, --interactive', 'interactive mode')
    .action(async (id: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        // Get current note data
        const currentNote = (await apiClient.getNote(id)) as any;
        if (!currentNote) {
          formatter.error(`Note ${id} not found`);
          process.exit(1);
        }

        let updates: any = {};

        if (options.interactive) {
          const answers = await inquirer.prompt([
            {
              type: 'input',
              name: 'title',
              message: 'Note title:',
              default: currentNote.title,
            },
            {
              type: 'editor',
              name: 'content',
              message: 'Note content:',
              default: currentNote.content || '',
            },
            {
              type: 'list',
              name: 'category',
              message: 'Note category:',
              choices: ['general', 'meeting', 'idea', 'bug', 'feature', 'docs'],
              default: currentNote.category || 'general',
            },
            {
              type: 'confirm',
              name: 'pinned',
              message: 'Pin this note?',
              default: currentNote.pinned || false,
            },
          ]);
          updates = answers;
        } else {
          // Use command line options
          if (options.title) updates.title = options.title;
          if (options.content) updates.content = options.content;
          if (options.category) updates.category = options.category;
          if (options.task) updates.taskId = options.task;
          if (options.pin) updates.pinned = true;
          if (options.unpin) updates.pinned = false;
        }

        if (Object.keys(updates).length === 0) {
          formatter.warn('No updates specified');
          return;
        }

        const updatedNote = (await apiClient.updateNote(id, updates)) as any;
        formatter.success('Note updated successfully');
        formatter.output(updatedNote);
      } catch (error) {
        formatter.error(
          `Failed to update note: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  noteCmd
    .command('delete <id>')
    .alias('rm')
    .description('Delete a note')
    .option('-f, --force', 'skip confirmation')
    .action(async (id: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        if (!options.force) {
          const note = (await apiClient.getNote(id)) as any;
          if (!note) {
            formatter.error(`Note ${id} not found`);
            process.exit(1);
          }

          const { confirm } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirm',
              message: `Delete note "${note.title}"?`,
              default: false,
            },
          ]);

          if (!confirm) {
            formatter.info('Delete cancelled');
            return;
          }
        }

        await apiClient.deleteNote(id);
        formatter.success(`Note ${id} deleted successfully`);
      } catch (error) {
        formatter.error(
          `Failed to delete note: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  noteCmd
    .command('search <query>')
    .alias('find')
    .description('Search notes')
    .option('-c, --category <category>', 'filter by category')
    .option('-l, --limit <number>', 'limit number of results', '10')
    .action(async (query: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        const searchParams: Record<string, string> = {};
        if (options.category) searchParams.category = options.category;
        if (options.limit) searchParams.limit = options.limit;

        const notes = (await apiClient.searchNotes(query)) as any;

        if (!notes || notes.length === 0) {
          formatter.info(`No notes found matching "${query}"`);
          return;
        }

        formatter.output(notes, {
          fields: ['id', 'title', 'category', 'content'],
          headers: ['ID', 'Title', 'Category', 'Content'],
        });
      } catch (error) {
        formatter.error(
          `Failed to search notes: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  noteCmd
    .command('pin <id>')
    .description('Pin a note')
    .action(async (id: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        await apiClient.updateNote(id, { pinned: true });
        formatter.success(`Note ${id} pinned successfully`);
      } catch (error) {
        formatter.error(
          `Failed to pin note: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  noteCmd
    .command('unpin <id>')
    .description('Unpin a note')
    .action(async (id: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        await apiClient.updateNote(id, { pinned: false });
        formatter.success(`Note ${id} unpinned successfully`);
      } catch (error) {
        formatter.error(
          `Failed to unpin note: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });
}

import type { Command } from 'commander';
import type { CliComponents } from '../types';

export function registerPriorityCommands(program: Command): void {
  const priorityCmd = program.command('priority').alias('p').description('Manage task priorities');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  priorityCmd
    .command('next')
    .description('Get next prioritized task')
    .option('-c, --count <number>', 'number of tasks to show', '1')
    .option('--explain', 'show priority reasoning')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        const count = parseInt(options.count, 10);

        if (count === 1) {
          const nextTask = await apiClient.getNextTask();

          if (!nextTask) {
            formatter.info('No prioritized tasks available');
            return;
          }

          formatter.success('Next prioritized task:');
          formatter.output(nextTask);

          if (options.explain && (nextTask as any).priorityReasoning) {
            console.log('\n--- Priority Reasoning ---');
            console.log((nextTask as any).priorityReasoning);
          }
        } else {
          const priorities = await apiClient.getPriorities();

          if (!priorities || (priorities as any).length === 0) {
            formatter.info('No prioritized tasks available');
            return;
          }

          const topTasks = (priorities as any[]).slice(0, count);
          formatter.success(`Top ${count} prioritized tasks:`);
          formatter.output(topTasks, {
            fields: ['id', 'title', 'priority', 'status', 'dueDate'],
            headers: ['ID', 'Title', 'Priority', 'Status', 'Due Date'],
          });
        }
      } catch (error) {
        formatter.error(
          `Failed to get next task: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  priorityCmd
    .command('list')
    .alias('ls')
    .description('List all tasks by priority')
    .option('-l, --limit <number>', 'limit number of results', '20')
    .option('--status <status>', 'filter by status')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        const priorities = (await apiClient.getPriorities()) as any;

        if (!priorities || priorities.length === 0) {
          formatter.info('No prioritized tasks available');
          return;
        }

        let filteredTasks = priorities as any[];
        if (options.status) {
          filteredTasks = (priorities as any[]).filter(
            (task: any) => task.status === options.status
          );
        }

        const limitedTasks = filteredTasks.slice(0, parseInt(options.limit, 10));

        formatter.output(limitedTasks, {
          fields: ['id', 'title', 'priority', 'status', 'dueDate', 'dependencies'],
          headers: ['ID', 'Title', 'Priority', 'Status', 'Due Date', 'Dependencies'],
        });
      } catch (error) {
        formatter.error(
          `Failed to list priorities: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  priorityCmd
    .command('recalc')
    .alias('calculate')
    .description('Recalculate all task priorities')
    .action(async () => {
      const { apiClient, formatter } = getComponents();

      try {
        formatter.info('Recalculating task priorities...');
        const result = await apiClient.recalculatePriorities();

        formatter.success('Priority recalculation completed');
        if ((result as any).message) {
          formatter.info((result as any).message);
        }

        if ((result as any).updated) {
          formatter.info(`Updated ${(result as any).updated} task priorities`);
        }
      } catch (error) {
        formatter.error(
          `Failed to recalculate priorities: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  priorityCmd
    .command('set <taskId> <priority>')
    .description('Set task priority (1-10)')
    .option('--reason <reason>', 'reason for priority change')
    .action(async (taskId: string, priority: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        const priorityNum = parseInt(priority, 10);

        if (priorityNum < 1 || priorityNum > 10) {
          formatter.error('Priority must be between 1 and 10');
          process.exit(1);
        }

        const updateData: any = { priority: priorityNum };
        if (options.reason) {
          updateData.priorityReason = options.reason;
        }

        await apiClient.updateTaskPriority(taskId, priorityNum);
        formatter.success(`Task ${taskId} priority set to ${priorityNum}`);

        if (options.reason) {
          formatter.info(`Reason: ${options.reason}`);
        }
      } catch (error) {
        formatter.error(
          `Failed to set task priority: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  priorityCmd
    .command('boost <taskId>')
    .description('Boost task priority by 1')
    .action(async (taskId: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        const task = (await apiClient.getTask(taskId)) as any;
        if (!task) {
          formatter.error(`Task ${taskId} not found`);
          process.exit(1);
        }

        const currentPriority = task.priority || 5;
        const newPriority = Math.min(currentPriority + 1, 10);

        await apiClient.updateTaskPriority(taskId, newPriority);
        formatter.success(
          `Task ${taskId} priority boosted from ${currentPriority} to ${newPriority}`
        );
      } catch (error) {
        formatter.error(
          `Failed to boost task priority: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  priorityCmd
    .command('lower <taskId>')
    .description('Lower task priority by 1')
    .action(async (taskId: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        const task = (await apiClient.getTask(taskId)) as any;
        if (!task) {
          formatter.error(`Task ${taskId} not found`);
          process.exit(1);
        }

        const currentPriority = task.priority || 5;
        const newPriority = Math.max(currentPriority - 1, 1);

        await apiClient.updateTaskPriority(taskId, newPriority);
        formatter.success(
          `Task ${taskId} priority lowered from ${currentPriority} to ${newPriority}`
        );
      } catch (error) {
        formatter.error(
          `Failed to lower task priority: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });
}

import { Command } from 'commander';
import chalk from 'chalk';
import { existsSync } from 'fs';
