interface ProcessTodosOptions {
  concurrent: boolean;
  maxConcurrent: string;
  groupByPhase: boolean;
  showDependencies: boolean;
  generateReport: boolean;
  dryRun: boolean;
}

export const processTodosCommand = new Command('process-todos')
  .description('Process TODO items with visual feedback')
  .argument('<file>', 'Path to TODO.md file')
  .option('-c, --concurrent', 'Run independent tasks concurrently', false)
  .option('-m, --max-concurrent <number>', 'Maximum concurrent tasks', '5')
  .option('-g, --group-by-phase', 'Group tasks by phase', false)
  .option('-d, --show-dependencies', 'Show task dependencies', false)
  .option('-r, --generate-report', 'Generate implementation report', false)
  .option('--dry-run', 'Simulate execution without making changes', false)
  .action(async (file: string, options: ProcessTodosOptions): Promise<void> => {
    try {
      // Validate file exists
      if (!existsSync(file)) {
        console.error(chalk.red(`Error: File not found: ${file}`));
        process.exit(1);
      }

      console.log(chalk.cyan('\nðŸš€ Processing TODOs...\n'));

      await todoProcessor.processTodos(file, {
        concurrent: options.concurrent,
        maxConcurrent: parseInt(options.maxConcurrent, 10),
        groupByPhase: options.groupByPhase,
        showDependencies: options.showDependencies,
        generateReport: options.generateReport,
        dryRun: options.dryRun,
      });

      console.log(chalk.green('\nâœ… TODO processing complete!\n'));
    } catch (error) {
      console.error(
        chalk.red('\nâŒ Error processing TODOs:'),
        error instanceof Error ? error.message : String(error)
      );
      process.exit(1);
    }
  });

import type { Command } from 'commander';
import * as WebSocket from 'ws';
import type { CliComponents } from '../types';

export function registerRealtimeCommands(program: Command): void {
  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  program
    .command('watch')
    .alias('w')
    .description('Watch for real-time updates')
    .option('-b, --board <id>', 'watch specific board')
    .option('-t, --task <id>', 'watch specific task')
    .option('--events <types>', 'event types to watch (comma-separated)')
    .option('--format <format>', 'output format (json|table|compact)', 'compact')
    .action(async options => {
      const { config, formatter } = getComponents();

      try {
        const serverUrl = config.getServerUrl();
        const apiKey = config.getApiKey();

        if (!apiKey) {
          formatter.error('API key required. Run "kanban config set api.key <key>"');
          process.exit(1);
        }

        // Convert HTTP URL to WebSocket URL
        const wsUrl = serverUrl.replace(/^https?:\/\//, 'ws://').replace(/^ws:\/\//, 'wss://');
        const wsEndpoint = `${wsUrl}/ws?apiKey=${apiKey}`;

        formatter.info('Connecting to real-time updates...');

        const ws = new (WebSocket as any)(wsEndpoint);

        ws.on('open', () => {
          formatter.success('Connected to real-time updates');

          // Subscribe to events based on options
          const subscriptions: any = {};

          if (options.board) {
            subscriptions.board = options.board;
          }

          if (options.task) {
            subscriptions.task = options.task;
          }

          if (options.events) {
            subscriptions.events = options.events.split(',').map((e: string) => e.trim());
          }

          // Send subscription message
          ws.send(
            JSON.stringify({
              type: 'subscribe',
              data: subscriptions,
            })
          );

          formatter.info('Watching for updates... (Press Ctrl+C to stop)');
        });

        ws.on('message', (data: WebSocket.Data) => {
          try {
            const message = JSON.parse(data.toString());

            if (message.type === 'event') {
              const event = message.data;
              const timestamp = new Date().toLocaleTimeString();

              if (options.format === 'json') {
                console.log(JSON.stringify({ timestamp, ...event }, null, 2));
              } else if (options.format === 'table') {
                formatter.output([{ timestamp, ...event }]);
              } else {
                // Compact format
                const icon = getEventIcon(event.type);
                const color = getEventColor(event.type);
                console.log(
                  `${timestamp} ${icon} ${color(event.type)}: ${event.message || formatEventMessage(event)}`
                );
              }
            } else if (message.type === 'error') {
              formatter.error(`WebSocket error: ${message.data.message}`);
            }
          } catch (error) {
            formatter.warn(`Failed to parse message: ${data}`);
          }
        });

        ws.on('error', (error: any) => {
          formatter.error(`WebSocket error: ${error.message}`);
          process.exit(1);
        });

        ws.on('close', (code: any, reason: any) => {
          if (code === 1000) {
            formatter.info('Connection closed normally');
          } else {
            formatter.error(`Connection closed: ${code} ${reason}`);
            process.exit(1);
          }
        });

        // Handle graceful shutdown
        process.on('SIGINT', () => {
          formatter.info('\nClosing connection...');
          ws.close();
          process.exit(0);
        });
      } catch (error) {
        formatter.error(
          `Failed to start watching: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  program
    .command('logs')
    .description('View system logs')
    .option('-f, --follow', 'follow log output')
    .option('-n, --lines <number>', 'number of lines to show', '50')
    .option('--level <level>', 'minimum log level (debug|info|warn|error)', 'info')
    .option('--component <name>', 'filter by component')
    .option('--since <time>', 'show logs since time (e.g., "1h", "30m", "2024-01-01")')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        const params: Record<string, string> = {
          lines: options.lines,
          level: options.level,
        };

        if (options.component) params.component = options.component;
        if (options.since) params.since = options.since;

        if (options.follow) {
          // Stream logs in real-time
          formatter.info('Following logs... (Press Ctrl+C to stop)');

          const streamLogs = async () => {
            try {
              const logs = (await apiClient.request('/api/logs/stream', { params })) as any;

              if (logs && logs.length > 0) {
                logs.forEach((log: any) => {
                  const timestamp = new Date(log.timestamp).toLocaleTimeString();
                  const level = log.level.toUpperCase().padEnd(5);
                  const component = log.component ? `[${log.component}]` : '';
                  console.log(`${timestamp} ${level} ${component} ${log.message}`);
                });
              }
            } catch (error) {
              // Ignore errors in streaming mode, will retry
            }
          };

          // Poll for new logs every second
          const interval = setInterval(streamLogs, 1000);

          // Handle graceful shutdown
          process.on('SIGINT', () => {
            clearInterval(interval);
            formatter.info('\nStopped following logs');
            process.exit(0);
          });

          // Initial load
          await streamLogs();
        } else {
          // One-time log fetch
          const logs = (await apiClient.request('/api/logs', { params })) as any;

          if (!logs || logs.length === 0) {
            formatter.info('No logs found');
            return;
          }

          formatter.output(logs, {
            fields: ['timestamp', 'level', 'component', 'message'],
            headers: ['Timestamp', 'Level', 'Component', 'Message'],
          });
        }
      } catch (error) {
        formatter.error(
          `Failed to get logs: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  // Utility functions for event formatting
  function getEventIcon(eventType: string): string {
    const icons: Record<string, string> = {
      'task:created': 'âœ¨',
      'task:updated': 'ðŸ“',
      'task:moved': 'ðŸ”„',
      'task:deleted': 'ðŸ—‘ï¸',
      'task:completed': 'âœ…',
      'note:added': 'ðŸ“„',
      'note:updated': 'ðŸ“',
      'priority:changed': 'âš¡',
      'dependency:blocked': 'ðŸš«',
      'subtask:completed': 'âœ“',
      default: 'ðŸ“‹',
    };
    return icons[eventType] || icons.default || 'ðŸ“‹';
  }

  function getEventColor(_eventType: string): (text: string) => string {
    // This would typically use chalk, but keeping it simple for now
    return (text: string) => text; // In real implementation, apply colors based on event type
  }

  function formatEventMessage(event: any): string {
    switch (event.type) {
      case 'task:created':
        return `Task "${event.data.title}" created`;
      case 'task:updated':
        return `Task "${event.data.title}" updated`;
      case 'task:moved':
        return `Task "${event.data.title}" moved to ${event.data.columnName}`;
      case 'task:deleted':
        return `Task "${event.data.title}" deleted`;
      case 'task:completed':
        return `Task "${event.data.title}" completed`;
      case 'note:added':
        return `Note "${event.data.title}" added to task ${event.data.taskId}`;
      case 'note:updated':
        return `Note "${event.data.title}" updated`;
      case 'priority:changed':
        return `Task "${event.data.title}" priority changed to ${event.data.newPriority}`;
      case 'dependency:blocked':
        return `Task "${event.data.title}" blocked by dependency`;
      case 'subtask:completed':
        return `Subtask "${event.data.title}" completed`;
      default:
        return event.data ? JSON.stringify(event.data) : 'Unknown event';
    }
  }
}

import type { Command } from 'commander';
import type { CliComponents } from '../types';

export function registerSearchCommands(program: Command): void {
  const searchCmd = program.command('search').alias('s').description('Search tasks and content');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  searchCmd
    .command('tasks <query>')
    .description('Search tasks')
    .option('-b, --board <id>', 'search within specific board')
    .option('-s, --status <status>', 'filter by status')
    .option('-t, --tags <tags>', 'filter by tags (comma-separated)')
    .option('-l, --limit <number>', 'limit number of results', '20')
    .option('--sort <field>', 'sort by field', 'relevance')
    .option('--order <direction>', 'sort order (asc/desc)', 'desc')
    .action(async (query: string, options) => {
      const { config, apiClient, formatter } = getComponents();

      try {
        const params: Record<string, string> = {
          q: query,
          limit: options.limit,
          sort: options.sort,
          order: options.order,
        };

        if (options.board) params.board = options.board;
        if (options.status) params.status = options.status;
        if (options.tags) params.tags = options.tags;

        // Use default board if no board specified
        if (!options.board && config.getDefaultBoard()) {
          params.board = config.getDefaultBoard()!;
        }

        const results = await apiClient.searchTasks(query, params);

        if (!results || (results as any).length === 0) {
          formatter.info(`No tasks found for "${query}"`);
          return;
        }

        formatter.success(`Found ${(results as any).length} tasks matching "${query}"`);
        formatter.output(results, {
          fields: ['id', 'title', 'status', 'priority', 'relevance', 'board'],
          headers: ['ID', 'Title', 'Status', 'Priority', 'Relevance', 'Board'],
        });
      } catch (error) {
        formatter.error(
          `Failed to search tasks: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  searchCmd
    .command('notes <query>')
    .description('Search notes')
    .option('-c, --category <category>', 'filter by category')
    .option('-l, --limit <number>', 'limit number of results', '20')
    .option('--sort <field>', 'sort by field', 'relevance')
    .option('--order <direction>', 'sort order (asc/desc)', 'desc')
    .action(async (query: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        const params: Record<string, string> = {
          q: query,
          limit: options.limit,
          sort: options.sort,
          order: options.order,
        };

        if (options.category) params.category = options.category;

        const results = await apiClient.searchNotes(query);

        if (!results || (results as any).length === 0) {
          formatter.info(`No notes found for "${query}"`);
          return;
        }

        formatter.success(`Found ${(results as any).length} notes matching "${query}"`);
        formatter.output(results, {
          fields: ['id', 'title', 'category', 'relevance', 'createdAt'],
          headers: ['ID', 'Title', 'Category', 'Relevance', 'Created'],
        });
      } catch (error) {
        formatter.error(
          `Failed to search notes: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  searchCmd
    .command('tags <query>')
    .description('Search tags')
    .option('-l, --limit <number>', 'limit number of results', '20')
    .action(async (query: string, _options) => {
      const { apiClient, formatter } = getComponents();

      try {
        const results = await apiClient.searchTags(query);

        if (!results || (results as any).length === 0) {
          formatter.info(`No tags found for "${query}"`);
          return;
        }

        formatter.success(`Found ${(results as any).length} tags matching "${query}"`);
        formatter.output(results, {
          fields: ['id', 'name', 'description', 'taskCount', 'parentId'],
          headers: ['ID', 'Name', 'Description', 'Tasks', 'Parent'],
        });
      } catch (error) {
        formatter.error(
          `Failed to search tags: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  searchCmd
    .command('all <query>')
    .description('Search all content (tasks, notes, tags)')
    .option('-l, --limit <number>', 'limit number of results per type', '10')
    .option('--tasks-only', 'search only tasks')
    .option('--notes-only', 'search only notes')
    .option('--tags-only', 'search only tags')
    .action(async (query: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        const searchPromises: Promise<any>[] = [];
        const searchTypes: string[] = [];

        if (!options.notesOnly && !options.tagsOnly) {
          searchPromises.push(apiClient.searchTasks(query, { limit: options.limit }));
          searchTypes.push('tasks');
        }

        if (!options.tasksOnly && !options.tagsOnly) {
          searchPromises.push(apiClient.searchNotes(query));
          searchTypes.push('notes');
        }

        if (!options.tasksOnly && !options.notesOnly) {
          searchPromises.push(apiClient.searchTags(query));
          searchTypes.push('tags');
        }

        const results = await Promise.all(searchPromises);

        let totalResults = 0;
        results.forEach((result, index) => {
          const type = searchTypes[index];
          const count = result ? result.length : 0;
          totalResults += count;

          if (count > 0 && type) {
            console.log(`\n--- ${type.toUpperCase()} (${count} results) ---`);

            if (type === 'tasks') {
              formatter.output(result, {
                fields: ['id', 'title', 'status', 'priority'],
                headers: ['ID', 'Title', 'Status', 'Priority'],
              });
            } else if (type === 'notes') {
              formatter.output(result, {
                fields: ['id', 'title', 'category'],
                headers: ['ID', 'Title', 'Category'],
              });
            } else if (type === 'tags') {
              formatter.output(result, {
                fields: ['id', 'name', 'taskCount'],
                headers: ['ID', 'Name', 'Tasks'],
              });
            }
          }
        });

        if (totalResults === 0) {
          formatter.info(`No results found for "${query}"`);
        } else {
          formatter.success(`Found ${totalResults} total results for "${query}"`);
        }
      } catch (error) {
        formatter.error(
          `Failed to search: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  // Advanced search with filters
  searchCmd
    .command('advanced')
    .description('Advanced search with multiple filters')
    .option('--title <query>', 'search in titles')
    .option('--description <query>', 'search in descriptions')
    .option('--tags <tags>', 'filter by tags (comma-separated)')
    .option('--status <status>', 'filter by status')
    .option('--priority-min <number>', 'minimum priority')
    .option('--priority-max <number>', 'maximum priority')
    .option('--created-after <date>', 'created after date (YYYY-MM-DD)')
    .option('--created-before <date>', 'created before date (YYYY-MM-DD)')
    .option('--due-after <date>', 'due after date (YYYY-MM-DD)')
    .option('--due-before <date>', 'due before date (YYYY-MM-DD)')
    .option('-l, --limit <number>', 'limit number of results', '20')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        const params: Record<string, string> = {
          limit: options.limit,
        };

        if (options.title) params.title = options.title;
        if (options.description) params.description = options.description;
        if (options.tags) params.tags = options.tags;
        if (options.status) params.status = options.status;
        if (options.priorityMin) params.priorityMin = options.priorityMin;
        if (options.priorityMax) params.priorityMax = options.priorityMax;
        if (options.createdAfter) params.createdAfter = options.createdAfter;
        if (options.createdBefore) params.createdBefore = options.createdBefore;
        if (options.dueAfter) params.dueAfter = options.dueAfter;
        if (options.dueBefore) params.dueBefore = options.dueBefore;

        const results = (await apiClient.request('/api/search/advanced', { params })) as any;

        if (!results || results.length === 0) {
          formatter.info('No results found with the specified filters');
          return;
        }

        formatter.success(`Found ${results.length} results with advanced filters`);
        formatter.output(results, {
          fields: ['id', 'title', 'status', 'priority', 'dueDate', 'createdAt'],
          headers: ['ID', 'Title', 'Status', 'Priority', 'Due Date', 'Created'],
        });
      } catch (error) {
        formatter.error(
          `Failed to perform advanced search: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });
}

import type { Command } from 'commander';
import inquirer from 'inquirer';
import type { CliComponents } from '../types';

export function registerSubtaskCommands(program: Command): void {
  const subtaskCmd = program
    .command('subtask')
    .alias('sub')
    .description('Manage subtasks and dependencies');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  subtaskCmd
    .command('create <parentId>')
    .description('Create a subtask for a parent task')
    .option('-t, --title <title>', 'subtask title')
    .option('-d, --description <desc>', 'subtask description')
    .option('-p, --priority <number>', 'priority (1-10)', '5')
    .option('--due <date>', 'due date (YYYY-MM-DD)')
    .option('-i, --interactive', 'interactive mode')
    .action(async (parentId: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        // Verify parent task exists
        const parentTask = (await apiClient.getTask(parentId)) as any;
        if (!parentTask) {
          formatter.error(`Parent task ${parentId} not found`);
          process.exit(1);
        }

        let subtaskData: any = {
          parentId,
          boardId: parentTask.boardId,
          columnId: parentTask.columnId,
        };

        if (options.interactive || !options.title) {
          const questions: any[] = [];

          if (!options.title) {
            questions.push({
              type: 'input',
              name: 'title',
              message: 'Subtask title:',
              validate: (input: string) => input.length > 0 || 'Title is required',
            });
          }

          if (!options.description) {
            questions.push({
              type: 'input',
              name: 'description',
              message: 'Subtask description (optional):',
            });
          }

          if (!options.priority) {
            questions.push({
              type: 'number',
              name: 'priority',
              message: 'Priority (1-10):',
              default: 5,
              validate: (input: number) =>
                (input >= 1 && input <= 10) || 'Priority must be between 1 and 10',
            });
          }

          if (!options.due) {
            questions.push({
              type: 'input',
              name: 'dueDate',
              message: 'Due date (YYYY-MM-DD, optional):',
              validate: (input: string) => {
                if (!input) return true;
                const date = new Date(input);
                return !isNaN(date.getTime()) || 'Invalid date format';
              },
            });
          }

          const answers = await inquirer.prompt(questions);
          subtaskData = { ...subtaskData, ...answers };
        }

        // Use command line options or answers
        subtaskData.title = options.title || subtaskData.title;
        subtaskData.description = options.description || subtaskData.description;
        subtaskData.priority = parseInt(options.priority || subtaskData.priority, 10);

        if (options.due || subtaskData.dueDate) {
          subtaskData.dueDate = options.due || subtaskData.dueDate;
        }

        const subtask = (await apiClient.createTask(subtaskData)) as any;
        formatter.success(`Subtask created successfully: ${subtask.id}`);
        formatter.output(subtask);
      } catch (error) {
        formatter.error(
          `Failed to create subtask: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  subtaskCmd
    .command('list <parentId>')
    .description('List subtasks for a parent task')
    .option('--status <status>', 'filter by status')
    .option('-l, --limit <number>', 'limit number of results', '20')
    .action(async (parentId: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        const params: Record<string, string> = {
          parent: parentId,
          limit: options.limit,
        };

        if (options.status) {
          params.status = options.status;
        }

        const subtasks = (await apiClient.getTasks(params)) as any;

        if (!subtasks || subtasks.length === 0) {
          formatter.info(`No subtasks found for task ${parentId}`);
          return;
        }

        formatter.output(subtasks, {
          fields: ['id', 'title', 'status', 'priority', 'dueDate', 'createdAt'],
          headers: ['ID', 'Title', 'Status', 'Priority', 'Due Date', 'Created'],
        });
      } catch (error) {
        formatter.error(
          `Failed to list subtasks: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  // Dependency commands
  const dependCmd = subtaskCmd
    .command('depend')
    .alias('dep')
    .description('Manage task dependencies');

  dependCmd
    .command('add <taskId> <dependsOnId>')
    .description('Add a dependency (taskId depends on dependsOnId)')
    .action(async (taskId: string, dependsOnId: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        await apiClient.request(`/api/tasks/${taskId}/dependencies`, {
          method: 'POST',
          body: { dependsOn: dependsOnId },
        });
        formatter.success(`Task ${taskId} now depends on task ${dependsOnId}`);
      } catch (error) {
        formatter.error(
          `Failed to add dependency: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  dependCmd
    .command('remove <taskId> <dependsOnId>')
    .description('Remove a dependency')
    .action(async (taskId: string, dependsOnId: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        await apiClient.request(`/api/tasks/${taskId}/dependencies/${dependsOnId}`, {
          method: 'DELETE',
        });
        formatter.success(
          `Removed dependency: task ${taskId} no longer depends on task ${dependsOnId}`
        );
      } catch (error) {
        formatter.error(
          `Failed to remove dependency: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  dependCmd
    .command('list <taskId>')
    .description('List task dependencies')
    .option('--blocked', 'show tasks blocked by this task')
    .action(async (taskId: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        if (options.blocked) {
          // Show tasks that are blocked by this task
          const blockedTasks = (await apiClient.request(`/api/tasks/${taskId}/blocking`)) as any;

          if (!blockedTasks || blockedTasks.length === 0) {
            formatter.info(`No tasks are blocked by task ${taskId}`);
            return;
          }

          formatter.info(`Tasks blocked by ${taskId}:`);
          formatter.output(blockedTasks, {
            fields: ['id', 'title', 'status', 'priority'],
            headers: ['ID', 'Title', 'Status', 'Priority'],
          });
        } else {
          // Show dependencies of this task
          const dependencies = (await apiClient.request(
            `/api/tasks/${taskId}/dependencies`
          )) as any;

          if (!dependencies || dependencies.length === 0) {
            formatter.info(`Task ${taskId} has no dependencies`);
            return;
          }

          formatter.info(`Dependencies for task ${taskId}:`);
          formatter.output(dependencies, {
            fields: ['id', 'title', 'status', 'priority'],
            headers: ['ID', 'Title', 'Status', 'Priority'],
          });
        }
      } catch (error) {
        formatter.error(
          `Failed to list dependencies: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  dependCmd
    .command('visualize <taskId>')
    .description('Visualize dependency graph for a task')
    .option('--depth <number>', 'depth of dependency tree', '3')
    .action(async (taskId: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        const depth = parseInt(options.depth, 10);
        const graph = (await apiClient.request(`/api/tasks/${taskId}/dependency-graph`, {
          params: { depth: depth.toString() },
        })) as any;

        if (!graph) {
          formatter.info(`No dependency graph available for task ${taskId}`);
          return;
        }

        formatter.info(`Dependency graph for task ${taskId}:`);

        // Simple text-based visualization
        const printNode = (node: any, indent = 0) => {
          const prefix = '  '.repeat(indent) + (indent > 0 ? 'â””â”€ ' : '');
          const status = node.status === 'completed' ? 'âœ“' : node.status === 'blocked' ? 'âš ' : 'â—‹';
          console.log(`${prefix}${status} ${node.id}: ${node.title} (${node.status})`);

          if (node.dependencies && node.dependencies.length > 0) {
            node.dependencies.forEach((dep: any) => printNode(dep, indent + 1));
          }
        };

        printNode(graph);
      } catch (error) {
        formatter.error(
          `Failed to visualize dependencies: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });
}

import type { Command } from 'commander';
import inquirer from 'inquirer';
import type { CliComponents } from '../types';

export function registerTagCommands(program: Command): void {
  const tagCmd = program.command('tag').description('Manage tags');

  // Get global components with proper typing
  const getComponents = (): CliComponents => global.cliComponents;

  tagCmd
    .command('list')
    .alias('ls')
    .description('List tags')
    .option('--usage', 'include usage statistics')
    .option('--tree', 'show hierarchical tree structure')
    .option('-l, --limit <number>', 'limit number of results', '50')
    .action(async options => {
      const { apiClient, formatter } = getComponents();

      try {
        const tags = (await apiClient.getTags()) as any;

        if (!tags || tags.length === 0) {
          formatter.info('No tags found');
          return;
        }

        if (options.tree) {
          // Display as hierarchical tree
          formatter.info('Hierarchical tag structure:');
          const rootTags = tags.filter((tag: any) => !tag.parentId);
          displayTagTree(rootTags, tags, formatter, 0);
        } else {
          // Regular table display
          const fields = options.usage
            ? ['id', 'name', 'color', 'description', 'taskCount', 'parentId']
            : ['id', 'name', 'color', 'description', 'parentId'];

          const headers = options.usage
            ? ['ID', 'Name', 'Color', 'Description', 'Task Count', 'Parent']
            : ['ID', 'Name', 'Color', 'Description', 'Parent'];

          formatter.output(tags.slice(0, parseInt(options.limit)), {
            fields,
            headers,
          });
        }
      } catch (error) {
        formatter.error(
          `Failed to list tags: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  tagCmd
    .command('show <id>')
    .description('Show tag details')
    .option('--tasks', 'include tasks with this tag')
    .action(async (id: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        const tag = (await apiClient.getTag(id)) as any;

        if (!tag) {
          formatter.error(`Tag ${id} not found`);
          process.exit(1);
        }

        formatter.output(tag);

        if (options.tasks && tag.tasks) {
          console.log('\n--- Tasks with this tag ---');
          formatter.output(tag.tasks, {
            fields: ['id', 'title', 'status', 'priority'],
            headers: ['ID', 'Title', 'Status', 'Priority'],
          });
        }
      } catch (error) {
        formatter.error(
          `Failed to get tag: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  tagCmd
    .command('create <name>')
    .alias('new')
    .description('Create a new tag')
    .option('-d, --description <desc>', 'tag description')
    .option('-c, --color <color>', 'tag color (hex code)')
    .option('-p, --parent <parentId>', 'parent tag ID for hierarchy')
    .option('-i, --interactive', 'interactive mode')
    .action(async (name: string, options) => {
      const { apiClient, formatter } = getComponents();

      let tagData: any = { name };

      if (options.interactive) {
        const questions: any[] = [
          {
            type: 'input',
            name: 'description',
            message: 'Tag description (optional):',
            default: options.description || '',
          },
          {
            type: 'input',
            name: 'color',
            message: 'Tag color (hex code, optional):',
            default: options.color || '#007acc',
            validate: (input: string) => {
              if (!input) return true;
              return /^#[0-9A-Fa-f]{6}$/.test(input) || 'Invalid hex color format (use #RRGGBB)';
            },
          },
          {
            type: 'input',
            name: 'parentId',
            message: 'Parent tag ID (optional, for hierarchy):',
            default: options.parent || '',
          },
        ];

        const answers = await inquirer.prompt(questions);
        tagData = { ...tagData, ...answers };
      } else {
        tagData.description = options.description;
        tagData.color = options.color;
        tagData.parentId = options.parent;
      }

      try {
        const tag = (await apiClient.createTag(tagData)) as any;
        formatter.success(`Tag created successfully: ${tag.id}`);
        formatter.output(tag);
      } catch (error) {
        formatter.error(
          `Failed to create tag: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  tagCmd
    .command('update <id>')
    .description('Update a tag')
    .option('-n, --name <name>', 'tag name')
    .option('-d, --description <desc>', 'tag description')
    .option('-c, --color <color>', 'tag color (hex code)')
    .option('-p, --parent <parentId>', 'parent tag ID')
    .option('-i, --interactive', 'interactive mode')
    .action(async (id: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        // Get current tag data
        const currentTag = (await apiClient.getTag(id)) as any;
        if (!currentTag) {
          formatter.error(`Tag ${id} not found`);
          process.exit(1);
        }

        let updates: any = {};

        if (options.interactive) {
          const answers = await inquirer.prompt([
            {
              type: 'input',
              name: 'name',
              message: 'Tag name:',
              default: currentTag.name,
            },
            {
              type: 'input',
              name: 'description',
              message: 'Tag description:',
              default: currentTag.description || '',
            },
            {
              type: 'input',
              name: 'color',
              message: 'Tag color (hex code):',
              default: currentTag.color || '#007acc',
              validate: (input: string) => {
                if (!input) return true;
                return /^#[0-9A-Fa-f]{6}$/.test(input) || 'Invalid hex color format (use #RRGGBB)';
              },
            },
          ]);
          updates = answers;
        } else {
          // Use command line options
          if (options.name) updates.name = options.name;
          if (options.description) updates.description = options.description;
          if (options.color) updates.color = options.color;
          if (options.parent) updates.parentId = options.parent;
        }

        if (Object.keys(updates).length === 0) {
          formatter.warn('No updates specified');
          return;
        }

        const updatedTag = (await apiClient.updateTag(id, updates)) as any;
        formatter.success('Tag updated successfully');
        formatter.output(updatedTag);
      } catch (error) {
        formatter.error(
          `Failed to update tag: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  tagCmd
    .command('delete <id>')
    .alias('rm')
    .description('Delete a tag')
    .option('-f, --force', 'skip confirmation')
    .action(async (id: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        if (!options.force) {
          const tag = (await apiClient.getTag(id)) as any;
          if (!tag) {
            formatter.error(`Tag ${id} not found`);
            process.exit(1);
          }

          const { confirm } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirm',
              message: `Delete tag "${tag.name}"? This will remove it from all tasks.`,
              default: false,
            },
          ]);

          if (!confirm) {
            formatter.info('Delete cancelled');
            return;
          }
        }

        await apiClient.deleteTag(id);
        formatter.success(`Tag ${id} deleted successfully`);
      } catch (error) {
        formatter.error(
          `Failed to delete tag: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  tagCmd
    .command('add <taskId> <tags...>')
    .description('Add tags to a task')
    .action(async (taskId: string, tags: string[]) => {
      const { apiClient, formatter } = getComponents();

      try {
        await apiClient.addTagsToTask(taskId, tags);
        formatter.success(`Added tags [${tags.join(', ')}] to task ${taskId}`);
      } catch (error) {
        formatter.error(
          `Failed to add tags: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  tagCmd
    .command('remove <taskId> <tag>')
    .alias('rm-from-task')
    .description('Remove tag from a task')
    .action(async (taskId: string, tag: string) => {
      const { apiClient, formatter } = getComponents();

      try {
        await apiClient.removeTagFromTask(taskId, tag);
        formatter.success(`Removed tag "${tag}" from task ${taskId}`);
      } catch (error) {
        formatter.error(
          `Failed to remove tag: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  tagCmd
    .command('search <query>')
    .alias('find')
    .description('Search tags by name')
    .option('-l, --limit <number>', 'limit number of results', '10')
    .action(async (query: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        const tags = (await apiClient.searchTags(query)) as any;

        if (!tags || tags.length === 0) {
          formatter.info(`No tags found matching "${query}"`);
          return;
        }

        formatter.output(tags.slice(0, parseInt(options.limit)), {
          fields: ['id', 'name', 'color', 'description'],
          headers: ['ID', 'Name', 'Color', 'Description'],
        });
      } catch (error) {
        formatter.error(
          `Failed to search tags: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });

  tagCmd
    .command('merge <fromId> <toId>')
    .description('Merge one tag into another')
    .option('-f, --force', 'skip confirmation')
    .action(async (fromId: string, toId: string, options) => {
      const { apiClient, formatter } = getComponents();

      try {
        if (!options.force) {
          const fromTag = (await apiClient.getTag(fromId)) as any;
          const toTag = (await apiClient.getTag(toId)) as any;

          if (!fromTag || !toTag) {
            formatter.error('One or both tags not found');
            process.exit(1);
          }

          const { confirm } = await inquirer.prompt([
            {
              type: 'confirm',
              name: 'confirm',
              message: `Merge tag "${fromTag.name}" into "${toTag.name}"? This will delete "${fromTag.name}".`,
              default: false,
            },
          ]);

          if (!confirm) {
            formatter.info('Merge cancelled');
            return;
          }
        }

        await apiClient.mergeTags(fromId, toId);
        formatter.success(`Merged tag ${fromId} into ${toId}`);
      } catch (error) {
        formatter.error(
          `Failed to merge tags: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        process.exit(1);
      }
    });
}

// Helper function to display tag tree
function displayTagTree(
  tags: any[],
  allTags: any[],
  formatter: OutputFormatter,
  depth: number
): void {
  tags.forEach(tag => {
    const indent = '  '.repeat(depth);
    const name = tag.color ? `${tag.name} (${tag.color})` : tag.name;
    console.log(`${indent}${depth > 0 ? 'â””â”€ ' : ''}${name} (${tag.id})`);

    // Find and display children
    const children = allTags.filter((t: any) => t.parentId === tag.id);
    if (children.length > 0) {
      displayTagTree(children, allTags, formatter, depth + 1);
    }
  });
}

