import chalk from 'chalk';
import { formatDuration, formatProgressBar, formatKeyValue } from '../utils/formatter';
import type { TaskSize } from '../prompts/validators';

export interface TaskEstimate {
  size: TaskSize;
  minHours: number;
  maxHours: number;
  avgHours: number;
  confidence: 'low' | 'medium' | 'high';
  reasoning: string[];
}

export interface TaskForEstimation {
  title: string;
  description?: string;
  tags?: string[];
  subtaskCount?: number;
  dependencyCount?: number;
}

export interface EstimationConfig {
  sizeHours: {
    S: { min: number; max: number; avg: number };
    M: { min: number; max: number; avg: number };
    L: { min: number; max: number; avg: number };
    XL: { min: number; max: number; avg: number };
  };
  complexityFactors: {
    subtasks: number;
    dependencies: number;
    unknownTech: number;
    testing: number;
    documentation: number;
  };
  velocityMultiplier: number;
}

/**
 * Estimates task sizes and time requirements
 */
export class TaskSizeEstimator {
  private readonly config: EstimationConfig;

  private readonly historicalData: Map<string, { estimated: number; actual: number }>;

  constructor(config?: Partial<EstimationConfig>) {
    this.config = {
      sizeHours: {
        S: { min: 0.5, max: 2, avg: 1 },
        M: { min: 2, max: 4, avg: 3 },
        L: { min: 4, max: 8, avg: 6 },
        XL: { min: 8, max: 16, avg: 12 },
      },
      complexityFactors: {
        subtasks: 0.2, // 20% increase per subtask
        dependencies: 0.15, // 15% increase per dependency
        unknownTech: 0.5, // 50% increase for unknown technology
        testing: 0.3, // 30% increase for testing requirements
        documentation: 0.2, // 20% increase for documentation
      },
      velocityMultiplier: 1.0, // Adjusted based on team velocity
      ...config,
    };

    this.historicalData = new Map();
  }

  /**
   * Estimate time for a single task
   */
  estimateTime(task: TaskForEstimation): TaskEstimate {
    const suggestedSize = this.suggestTaskSize(task);
    const baseEstimate = this.config.sizeHours[suggestedSize];

    // Calculate complexity multiplier
    const complexityMultiplier = this.calculateComplexityMultiplier(task);

    // Apply complexity and velocity
    const minHours = baseEstimate.min * complexityMultiplier * this.config.velocityMultiplier;
    const maxHours = baseEstimate.max * complexityMultiplier * this.config.velocityMultiplier;
    const avgHours = baseEstimate.avg * complexityMultiplier * this.config.velocityMultiplier;

    // Determine confidence based on various factors
    const confidence = this.calculateConfidence(task, suggestedSize);

    // Generate reasoning
    const reasoning = this.generateReasoning(task, suggestedSize, complexityMultiplier);

    return {
      size: suggestedSize,
      minHours: Math.round(minHours * 10) / 10,
      maxHours: Math.round(maxHours * 10) / 10,
      avgHours: Math.round(avgHours * 10) / 10,
      confidence,
      reasoning,
    };
  }

  /**
   * Group tasks by size
   */
  groupTasksBySize<T extends { size?: TaskSize }>(tasks: T[]): Map<TaskSize | 'Unknown', T[]> {
    const groups = new Map<TaskSize | 'Unknown', T[]>();

    for (const task of tasks) {
      const size = task.size || 'Unknown';
      if (!groups.has(size)) {
        groups.set(size, []);
      }
      groups.get(size)!.push(task);
    }

    return groups;
  }

  /**
   * Display estimates in a formatted way
   */
  displayEstimates(estimates: Array<{ task: TaskForEstimation; estimate: TaskEstimate }>): void {
    console.log(chalk.cyan('\nðŸ“Š Task Size Estimates\n'));

    let totalMin = 0;
    let totalMax = 0;
    let totalAvg = 0;

    for (const { task, estimate } of estimates) {
      console.log(chalk.bold(`ðŸ“‹ ${task.title}`));
      console.log(formatKeyValue('  Size', this.formatSize(estimate.size)));
      console.log(formatKeyValue('  Time Range', `${estimate.minHours}h - ${estimate.maxHours}h`));
      console.log(formatKeyValue('  Average', `${estimate.avgHours}h`));
      console.log(formatKeyValue('  Confidence', this.formatConfidence(estimate.confidence)));

      if (estimate.reasoning.length > 0) {
        console.log(chalk.gray('  Reasoning:'));
        estimate.reasoning.forEach(reason => {
          console.log(chalk.gray(`    â€¢ ${reason}`));
        });
      }

      console.log();

      totalMin += estimate.minHours;
      totalMax += estimate.maxHours;
      totalAvg += estimate.avgHours;
    }

    // Summary
    console.log(chalk.gray('â”€'.repeat(50)));
    console.log(chalk.bold('Summary:'));
    console.log(formatKeyValue('Total Tasks', estimates.length.toString()));
    console.log(formatKeyValue('Total Time', `${totalMin}h - ${totalMax}h (avg: ${totalAvg}h)`));
    console.log(formatKeyValue('Duration', `${formatDuration(totalAvg * 60 * 60 * 1000)}`));

    // Size distribution
    const sizeGroups = this.groupTasksBySize(estimates.map(e => ({ size: e.estimate.size })));
    console.log('\nSize Distribution:');
    for (const [size, tasks] of sizeGroups) {
      const percentage = (tasks.length / estimates.length) * 100;
      console.log(
        `  ${this.formatSize(size as TaskSize)}: ${formatProgressBar(tasks.length, estimates.length, 20)} ${tasks.length} tasks`
      );
    }
  }

  /**
   * Suggest task size based on heuristics
   */
  suggestTaskSize(task: TaskForEstimation): TaskSize {
    let score = 0;

    // Title length heuristic
    if (task.title.length < 30) score += 1;
    else if (task.title.length < 50) score += 2;
    else score += 3;

    // Description complexity
    if (task.description) {
      const wordCount = task.description.split(/\s+/).length;
      if (wordCount < 20) score += 1;
      else if (wordCount < 50) score += 2;
      else if (wordCount < 100) score += 3;
      else score += 4;
    }

    // Subtasks
    if (task.subtaskCount) {
      if (task.subtaskCount <= 2) score += 1;
      else if (task.subtaskCount <= 5) score += 2;
      else score += 3;
    }

    // Dependencies
    if (task.dependencyCount) {
      if (task.dependencyCount === 1) score += 1;
      else if (task.dependencyCount <= 3) score += 2;
      else score += 3;
    }

    // Keywords in title/description
    const text = `${task.title} ${task.description || ''}`.toLowerCase();
    const complexKeywords = [
      'refactor',
      'migrate',
      'redesign',
      'architecture',
      'integration',
      'optimize',
    ];
    const simpleKeywords = ['fix', 'update', 'add', 'remove', 'change', 'typo'];

    if (complexKeywords.some(keyword => text.includes(keyword))) {
      score += 3;
    } else if (simpleKeywords.some(keyword => text.includes(keyword))) {
      score += 1;
    }

    // Tags
    if (task.tags) {
      const complexTags = ['backend', 'database', 'api', 'performance', 'security'];
      const hasComplexTag = task.tags.some(tag =>
        complexTags.some(complex => tag.toLowerCase().includes(complex))
      );
      if (hasComplexTag) score += 2;
    }

    // Map score to size
    if (score <= 3) return 'S';
    if (score <= 7) return 'M';
    if (score <= 12) return 'L';
    return 'XL';
  }

  /**
   * Update velocity based on historical data
   */
  updateVelocity(taskId: string, estimatedHours: number, actualHours: number): void {
    this.historicalData.set(taskId, { estimated: estimatedHours, actual: actualHours });

    // Recalculate velocity multiplier based on historical data
    if (this.historicalData.size >= 5) {
      let totalEstimated = 0;
      let totalActual = 0;

      for (const { estimated, actual } of this.historicalData.values()) {
        totalEstimated += estimated;
        totalActual += actual;
      }

      // New velocity is the ratio of actual to estimated
      this.config.velocityMultiplier = totalActual / totalEstimated;

      // Clamp to reasonable values
      this.config.velocityMultiplier = Math.max(0.5, Math.min(2.0, this.config.velocityMultiplier));
    }
  }

  /**
   * Get estimation accuracy report
   */
  getAccuracyReport(): {
    accuracy: number;
    overestimated: number;
    underestimated: number;
    samples: number;
  } {
    if (this.historicalData.size === 0) {
      return { accuracy: 0, overestimated: 0, underestimated: 0, samples: 0 };
    }

    let correctEstimates = 0;
    let overestimated = 0;
    let underestimated = 0;

    for (const { estimated, actual } of this.historicalData.values()) {
      const variance = Math.abs(estimated - actual) / estimated;
      if (variance <= 0.2) {
        // Within 20% is considered accurate
        correctEstimates++;
      } else if (estimated > actual) {
        overestimated++;
      } else {
        underestimated++;
      }
    }

    return {
      accuracy: (correctEstimates / this.historicalData.size) * 100,
      overestimated: (overestimated / this.historicalData.size) * 100,
      underestimated: (underestimated / this.historicalData.size) * 100,
      samples: this.historicalData.size,
    };
  }

  /**
   * Calculate complexity multiplier
   */
  private calculateComplexityMultiplier(task: TaskForEstimation): number {
    let multiplier = 1.0;

    if (task.subtaskCount) {
      multiplier += task.subtaskCount * this.config.complexityFactors.subtasks;
    }

    if (task.dependencyCount) {
      multiplier += task.dependencyCount * this.config.complexityFactors.dependencies;
    }

    // Check for complexity indicators in text
    const text = `${task.title} ${task.description || ''}`.toLowerCase();

    if (text.includes('unknown') || text.includes('research') || text.includes('investigate')) {
      multiplier += this.config.complexityFactors.unknownTech;
    }

    if (text.includes('test') || text.includes('testing') || text.includes('qa')) {
      multiplier += this.config.complexityFactors.testing;
    }

    if (text.includes('document') || text.includes('docs') || text.includes('readme')) {
      multiplier += this.config.complexityFactors.documentation;
    }

    return multiplier;
  }

  /**
   * Calculate confidence level
   */
  private calculateConfidence(task: TaskForEstimation, size: TaskSize): 'low' | 'medium' | 'high' {
    let confidenceScore = 5; // Start at medium

    // More information increases confidence
    if (task.description) confidenceScore += 1;
    if (task.tags && task.tags.length > 0) confidenceScore += 1;

    // Clear scope increases confidence
    const text = `${task.title} ${task.description || ''}`.toLowerCase();
    if (text.includes('specifically') || text.includes('exactly') || text.includes('only')) {
      confidenceScore += 1;
    }

    // Uncertainty decreases confidence
    if (text.includes('maybe') || text.includes('possibly') || text.includes('unknown')) {
      confidenceScore -= 2;
    }

    // Many dependencies decrease confidence
    if (task.dependencyCount && task.dependencyCount > 3) {
      confidenceScore -= 1;
    }

    // Historical accuracy affects confidence
    const accuracy = this.getAccuracyReport();
    if (accuracy.samples > 10) {
      if (accuracy.accuracy > 80) confidenceScore += 1;
      else if (accuracy.accuracy < 50) confidenceScore -= 1;
    }

    if (confidenceScore >= 7) return 'high';
    if (confidenceScore >= 4) return 'medium';
    return 'low';
  }

  /**
   * Generate reasoning for estimate
   */
  private generateReasoning(
    task: TaskForEstimation,
    size: TaskSize,
    complexityMultiplier: number
  ): string[] {
    const reasons: string[] = [];

    // Size reasoning
    reasons.push(`Base size ${size} based on task complexity analysis`);

    // Complexity factors
    if (task.subtaskCount) {
      reasons.push(
        `${task.subtaskCount} subtask${task.subtaskCount > 1 ? 's' : ''} add complexity`
      );
    }

    if (task.dependencyCount) {
      reasons.push(`${task.dependencyCount} dependencies may cause delays`);
    }

    if (complexityMultiplier > 1.3) {
      reasons.push(`Complexity multiplier of ${complexityMultiplier.toFixed(1)}x applied`);
    }

    // Historical data
    if (this.historicalData.size > 5) {
      const accuracy = this.getAccuracyReport();
      if (accuracy.overestimated > 50) {
        reasons.push('Historical data shows tendency to overestimate');
      } else if (accuracy.underestimated > 50) {
        reasons.push('Historical data shows tendency to underestimate');
      }
    }

    // Velocity adjustment
    if (this.config.velocityMultiplier !== 1.0) {
      reasons.push(`Team velocity factor of ${this.config.velocityMultiplier.toFixed(1)}x applied`);
    }

    return reasons;
  }

  /**
   * Format size for display
   */
  private formatSize(size: TaskSize): string {
    const sizeColors = {
      S: chalk.green,
      M: chalk.yellow,
      L: chalk.magenta,
      XL: chalk.red,
    };

    const sizeLabels = {
      S: 'Small',
      M: 'Medium',
      L: 'Large',
      XL: 'Extra Large',
    };

    return sizeColors[size](`${size} (${sizeLabels[size]})`);
  }

  /**
   * Format confidence for display
   */
  private formatConfidence(confidence: 'low' | 'medium' | 'high'): string {
    const confidenceColors = {
      low: chalk.red,
      medium: chalk.yellow,
      high: chalk.green,
    };

    return confidenceColors[confidence](confidence);
  }
}

import Table from 'cli-table3';
import chalk from 'chalk';

type OutputFormat = 'table' | 'json' | 'csv';

interface FormatterOptions {
  format: OutputFormat;
  verbose: boolean;
  quiet: boolean;
  color: boolean;
}

export class OutputFormatter {
  private readonly options: FormatterOptions = {
    format: 'table',
    verbose: false,
    quiet: false,
    color: true,
  };

  setFormat(format: OutputFormat): void {
    this.options.format = format;
  }

  setVerbose(verbose: boolean): void {
    this.options.verbose = verbose;
  }

  setQuiet(quiet: boolean): void {
    this.options.quiet = quiet;
  }

  setColor(color: boolean): void {
    this.options.color = color;
  }

  /**
   * Format and output data in the specified format
   */
  output<T = unknown>(data: T, options?: { headers?: string[]; fields?: string[] }): void {
    if (this.options.quiet && this.options.format !== 'json') {
      return;
    }

    switch (this.options.format) {
      case 'json':
        this.outputJson(data);
        break;
      case 'csv':
        this.outputCsv(data, options);
        break;
      case 'table':
      default:
        this.outputTable(data, options);
        break;
    }
  }

  /**
   * Output success message
   */
  success(message: string): void {
    if (!this.options.quiet) {
      console.log(this.colorize(message, 'green'));
    }
  }

  /**
   * Output error message
   */
  error(message: string): void {
    console.error(this.colorize(`Error: ${message}`, 'red'));
  }

  /**
   * Output warning message
   */
  warn(message: string): void {
    if (!this.options.quiet) {
      console.warn(this.colorize(`Warning: ${message}`, 'yellow'));
    }
  }

  /**
   * Output info message
   */
  info(message: string): void {
    if (this.options.verbose && !this.options.quiet) {
      console.log(this.colorize(message, 'cyan'));
    }
  }

  /**
   * Output debug message
   */
  debug(message: string): void {
    if (this.options.verbose && !this.options.quiet) {
      console.log(this.colorize(`Debug: ${message}`, 'gray'));
    }
  }

  /**
   * Output JSON format
   */
  private outputJson<T>(data: T): void {
    console.log(JSON.stringify(data, null, 2));
  }

  /**
   * Output CSV format
   */
  private outputCsv<T>(data: T, options?: { headers?: string[]; fields?: string[] }): void {
    if (!Array.isArray(data)) {
      data = [data] as T;
    }

    const items = Array.isArray(data) ? data : [data];
    if (items.length === 0) {
      return;
    }

    const fields = options?.fields || Object.keys(items[0]);
    const headers = options?.headers || fields;

    // Output headers
    console.log(headers.join(','));

    // Output rows
    items.forEach((item): void => {
      const values = fields.map((field): string => {
        const value = this.getNestedValue(item, field);
        const stringValue = this.formatValue(value);
        // Escape commas and quotes in CSV
        return `"${stringValue.replace(/"/g, '""')}"`;
      });
      console.log(values.join(','));
    });
  }

  /**
   * Output table format
   */
  private outputTable<T>(data: T, options?: { headers?: string[]; fields?: string[] }): void {
    if (!Array.isArray(data)) {
      // Single object - display as key-value pairs
      this.outputObjectTable(data);
      return;
    }

    const items = Array.isArray(data) ? data : [data];
    if (items.length === 0) {
      console.log(this.colorize('No items found', 'gray'));
      return;
    }

    const fields = options?.fields || this.getTableFields(items[0]);
    const headers = options?.headers || fields.map((field): string => this.formatHeader(field));

    const table = new Table({
      head: headers.map((h): string => this.colorize(h, 'cyan')),
      style: {
        'padding-left': 1,
        'padding-right': 1,
        head: this.options.color ? ['cyan'] : [],
      },
    });

    items.forEach((item): void => {
      const row = fields.map((field): string => {
        const value = this.getNestedValue(item, field);
        return this.formatTableValue(value, field);
      });
      table.push(row);
    });

    console.log(table.toString());
  }

  /**
   * Output single object as key-value table
   */
  private outputObjectTable<T>(obj: T): void {
    const table = new Table({
      style: {
        'padding-left': 1,
        'padding-right': 1,
      },
    });

    Object.entries(obj as Record<string, unknown>).forEach(([key, value]): void => {
      table.push([
        this.colorize(this.formatHeader(key), 'cyan'),
        this.formatTableValue(value, key),
      ]);
    });

    console.log(table.toString());
  }

  /**
   * Get nested value from object using dot notation
   */
  private getNestedValue(obj: unknown, path: string): unknown {
    return path
      .split('.')
      .reduce(
        (current: any, key): any => (current && current[key] !== undefined ? current[key] : ''),
        obj
      );
  }

  /**
   * Format value for display
   */
  private formatValue(value: unknown): string {
    if (value === null || value === undefined) {
      return '';
    }

    if (typeof value === 'boolean') {
      return value ? 'Yes' : 'No';
    }

    if (value instanceof Date) {
      return value.toLocaleString();
    }

    if (typeof value === 'object') {
      return JSON.stringify(value);
    }

    return String(value);
  }

  /**
   * Format value for table display with colors
   */
  private formatTableValue(value: unknown, field: string): string {
    const formatted = this.formatValue(value);

    if (!this.options.color) {
      return formatted;
    }

    // Apply field-specific coloring
    if (field.includes('status')) {
      if (formatted.toLowerCase().includes('completed')) {
        return chalk.green(formatted);
      }
      if (formatted.toLowerCase().includes('in_progress')) {
        return chalk.yellow(formatted);
      }
      if (formatted.toLowerCase().includes('todo')) {
        return chalk.blue(formatted);
      }
    }

    if (field.includes('priority')) {
      const num = parseInt(formatted);
      if (num >= 8) return chalk.red(formatted);
      if (num >= 5) return chalk.yellow(formatted);
      if (num >= 1) return chalk.green(formatted);
    }

    if (field.includes('date') || field.includes('time')) {
      return chalk.gray(formatted);
    }

    return formatted;
  }

  /**
   * Format header text
   */
  private formatHeader(field: string): string {
    return field
      .replace(/([A-Z])/g, ' $1')
      .replace(/^./, (str): string => str.toUpperCase())
      .trim();
  }

  /**
   * Get appropriate fields for table display
   */
  private getTableFields(obj: Record<string, unknown>): string[] {
    const allFields = Object.keys(obj);

    // Define field priority for common objects
    const priorities = {
      id: 1,
      title: 2,
      name: 2,
      status: 3,
      priority: 4,
      description: 5,
      createdAt: 8,
      updatedAt: 9,
    };

    // Sort fields by priority, then alphabetically
    return allFields.sort((a, b): number => {
      const priorityA = priorities[a as keyof typeof priorities] || 6;
      const priorityB = priorities[b as keyof typeof priorities] || 6;

      if (priorityA !== priorityB) {
        return priorityA - priorityB;
      }

      return a.localeCompare(b);
    });
  }

  /**
   * Apply color if color is enabled
   */
  private colorize(text: string, color: string): string {
    if (!this.options.color) {
      return text;
    }

    switch (color) {
      case 'red':
        return chalk.red(text);
      case 'green':
        return chalk.green(text);
      case 'yellow':
        return chalk.yellow(text);
      case 'blue':
        return chalk.blue(text);
      case 'cyan':
        return chalk.cyan(text);
      case 'gray':
        return chalk.gray(text);
      default:
        return text;
    }
  }

  /**
   * Create a progress bar
   */
  progressBar(current: number, total: number, width: number = 20): string {
    const percentage = Math.round((current / total) * 100);
    const filled = Math.round((current / total) * width);
    const empty = width - filled;

    const bar = 'â–ˆ'.repeat(filled) + 'â–‘'.repeat(empty);
    const text = `${bar} ${percentage}% (${current}/${total})`;

    return this.options.color ? chalk.cyan(text) : text;
  }

  /**
   * Format file size
   */
  formatSize(bytes: number): string {
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = 0;

    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }

    return `${size.toFixed(1)} ${units[unitIndex]}`;
  }

  /**
   * Format duration
   */
  formatDuration(ms: number): string {
    const seconds = Math.floor(ms / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) return `${days}d ${hours % 24}h`;
    if (hours > 0) return `${hours}h ${minutes % 60}m`;
    if (minutes > 0) return `${minutes}m ${seconds % 60}s`;
    return `${seconds}s`;
  }

  /**
   * Format backup schedule information
   */
  formatSchedule(schedule: any): string {
    const lines = [];

    // Header
    lines.push(this.formatHeader(`Schedule: ${schedule.name}`));
    lines.push('');

    // Basic info
    lines.push(`ID: ${schedule.id}`);
    lines.push(`Type: ${schedule.backupType?.toUpperCase() || 'N/A'}`);
    lines.push(`Status: ${schedule.enabled ? chalk.green('ENABLED') : chalk.red('DISABLED')}`);
    lines.push(`Cron: ${schedule.cronExpression || schedule.cron || 'N/A'}`);

    if (schedule.description) {
      lines.push(`Description: ${schedule.description}`);
    }

    // Timing info
    lines.push('');
    lines.push(this.formatHeader('Timing:'));
    lines.push(`Created: ${new Date(schedule.createdAt).toLocaleString()}`);
    lines.push(`Updated: ${new Date(schedule.updatedAt).toLocaleString()}`);

    if (schedule.lastRunAt) {
      lines.push(`Last Run: ${new Date(schedule.lastRunAt).toLocaleString()}`);
    }

    if (schedule.nextRunAt || schedule.next_run) {
      lines.push(`Next Run: ${new Date(schedule.nextRunAt || schedule.next_run).toLocaleString()}`);
    }

    // Statistics
    lines.push('');
    lines.push(this.formatHeader('Statistics:'));
    lines.push(`Total Runs: ${schedule.runCount || 0}`);
    lines.push(`Failures: ${schedule.failureCount || 0}`);

    if (schedule.runCount > 0) {
      const successRate = (
        ((schedule.runCount - (schedule.failureCount || 0)) / schedule.runCount) *
        100
      ).toFixed(1);
      lines.push(`Success Rate: ${successRate}%`);
    }

    // Configuration
    lines.push('');
    lines.push(this.formatHeader('Configuration:'));
    lines.push(`Retention: ${schedule.retentionDays || 30} days`);
    lines.push(`Compression: ${schedule.compressionEnabled ? 'Enabled' : 'Disabled'}`);
    lines.push(`Verification: ${schedule.verificationEnabled ? 'Enabled' : 'Disabled'}`);

    return lines.join('\n');
  }
}

#!/usr/bin/env node

import { Command } from 'commander';
import chalk from 'chalk';

import { ConfigManager } from './config';
import { ApiClient } from './client';
import { OutputFormatter } from './formatter';
import type { CliComponents } from './types';
import { addSecurityMiddleware } from './utils/secure-cli-wrapper';

import { registerTaskCommands } from './commands/tasks';
import { registerBoardCommands } from './commands/boards';
import { registerNoteCommands } from './commands/notes';
import { registerTagCommands } from './commands/tags';
import { registerConfigCommands } from './commands/config';
import { registerPriorityCommands } from './commands/priority';
import { registerContextCommands } from './commands/context';
import { registerSearchCommands } from './commands/search';
import { registerSubtaskCommands } from './commands/subtasks';
import { registerBackupCommands } from './commands/backup';
import { registerDatabaseCommands } from './commands/database';
import { registerRealtimeCommands } from './commands/realtime';
import { registerExportCommands } from './commands/export';
import { processTodosCommand } from './commands/process-todos';
// import { interactiveViewCommand } from './commands/interactive-view'; // Temporarily disabled due to Ink/React ESM issues
// import { dashboardCommand } from './commands/dashboard'; // Temporarily disabled due to Ink/React ESM issues
// import { dashboardDemoCommand } from './commands/dashboard-demo'; // Temporarily disabled due to Ink/React ESM issues
import packageJson from '../../package.json';

const program = new Command();

// Initialize global components
const config = new ConfigManager();
const apiClient = new ApiClient(config);
const formatter = new OutputFormatter();

// Make globally available to commands with proper typing
global.cliComponents = {
  config,
  apiClient,
  formatter,
} as CliComponents;

// Program configuration
program
  .name('kanban')
  .description('CLI for MCP Kanban Task Management System')
  .version(packageJson.version)
  .option('-v, --verbose', 'verbose output')
  .option('-q, --quiet', 'minimal output')
  .option('--format <type>', 'output format: table, json, csv', 'table')
  .option('--no-color', 'disable colored output')
  .hook('preAction', async thisCommand => {
    const options = thisCommand.opts();

    // Set formatter options
    formatter.setFormat(options['format']);
    formatter.setVerbose(options['verbose']);
    formatter.setQuiet(options['quiet']);
    formatter.setColor(!options['noColor']);

    // Validate configuration exists
    if (!config.exists()) {
      const command = thisCommand.name();
      const parentCommand = thisCommand.parent?.name();
      const grandParentCommand = thisCommand.parent?.parent?.name();

      // Allow config commands and help
      if (command !== 'config' && command !== 'help' && 
          parentCommand !== 'config' && grandParentCommand !== 'config') {
        console.error(chalk.red('No configuration found. Run "kanban config init" to get started.'));
        process.exit(1);
      }
    }
  });

// Add security middleware to the program
addSecurityMiddleware(program);

// Register command modules
registerTaskCommands(program);
registerBoardCommands(program);
registerNoteCommands(program);
registerTagCommands(program);
registerConfigCommands(program);
registerPriorityCommands(program);
registerContextCommands(program);
registerSearchCommands(program);
registerSubtaskCommands(program);
registerBackupCommands(program);
registerDatabaseCommands(program);
registerRealtimeCommands(program);
registerExportCommands(program);

// Register development commands
program.addCommand(processTodosCommand);
// program.addCommand(interactiveViewCommand); // Temporarily disabled due to Ink/React ESM issues
// program.addCommand(dashboardCommand); // Temporarily disabled due to Ink/React ESM issues
// program.addCommand(dashboardDemoCommand); // Temporarily disabled due to Ink/React ESM issues

// Enhanced global error handler
program.exitOverride(err => {
  if (err.code === 'commander.help') {
    process.exit(0);
  }
  if (err.code === 'commander.version') {
    process.exit(0);
  }

  // Handle different error types
  const errorMessage = formatError(err);
  console.error(errorMessage);

  if (program.opts()['verbose']) {
    console.error(chalk.gray('\nStack trace:'));
    console.error(chalk.gray(err.stack || 'No stack trace available'));
  }

  // Log error to file if possible
  logErrorToFile(err);

  process.exit(getExitCode(err));
});

/**
 * Format error message with appropriate styling
 */
function formatError(error: Error): string {
  const timestamp = new Date().toISOString();

  // Check error type and format accordingly
  if (error.name === 'PromptCancelledError') {
    return chalk.yellow('\nâš ï¸  Operation cancelled by user');
  }

  if (error.name === 'SpinnerError') {
    return chalk.red(`\nâŒ ${error.message}`);
  }

  if (error.message.includes('ECONNREFUSED') || error.message.includes('ENOTFOUND')) {
    return chalk.red('\nâŒ Connection Error: Unable to connect to the server') +
           chalk.gray('\n   â€¢ Check if the server is running') +
           chalk.gray('\n   â€¢ Verify the server URL in config: kanban config show') +
           chalk.gray('\n   â€¢ Test connection: kanban config test');
  }

  if (error.message.includes('401') || error.message.includes('Unauthorized')) {
    return chalk.red('\nâŒ Authentication Error: Invalid credentials') +
           chalk.gray('\n   â€¢ Check your API key: kanban config show') +
           chalk.gray('\n   â€¢ Update credentials: kanban config set auth.apiKey <key>');
  }

  if (error.message.includes('403') || error.message.includes('Forbidden')) {
    return chalk.red('\nâŒ Permission Error: Access denied') +
           chalk.gray('\n   â€¢ You may not have permission for this resource') +
           chalk.gray('\n   â€¢ Contact your administrator');
  }

  if (error.message.includes('404') || error.message.includes('Not Found')) {
    return chalk.red('\nâŒ Resource Not Found') +
           chalk.gray('\n   â€¢ Check if the resource ID is correct') +
           chalk.gray('\n   â€¢ List available resources first');
  }

  if (error.message.includes('timeout')) {
    return chalk.red('\nâŒ Request Timeout') +
           chalk.gray('\n   â€¢ The operation took too long') +
           chalk.gray('\n   â€¢ Try again or check server status');
  }

  if (error.message.includes('Security') || error.message.includes('blocked')) {
    return chalk.red('\nðŸš« Security Error: ') + error.message +
           chalk.gray('\n   â€¢ Input contained potentially dangerous content') +
           chalk.gray('\n   â€¢ Use: kanban security events (to see details)') +
           chalk.gray('\n   â€¢ Use: kanban security report (for full report)');
  }

  if (error.message.includes('sanitiz') || error.message.includes('Suspicious')) {
    return chalk.yellow('\nâš ï¸  Input Security Warning: ') + error.message +
           chalk.gray('\n   â€¢ Input was modified for security') +
           chalk.gray('\n   â€¢ Check: kanban security events');
  }

  // Generic error formatting
  return chalk.red('\nâŒ Error: ') + error.message +
         chalk.gray(`\n   Time: ${timestamp}`) +
         chalk.gray('\n   Use --verbose for more details');
}

/**
 * Get appropriate exit code based on error type
 */
function getExitCode(error: Error): number {
  if (error.name === 'PromptCancelledError') return 130; // SIGINT equivalent
  if (error.message.includes('ECONNREFUSED')) return 111; // Connection refused
  if (error.message.includes('401') || error.message.includes('Unauthorized')) return 401;
  if (error.message.includes('403') || error.message.includes('Forbidden')) return 403;
  if (error.message.includes('404') || error.message.includes('Not Found')) return 404;
  return 1; // Generic error
}

/**
 * Log error to file for debugging (optional)
 */
function logErrorToFile(error: Error): void {
  try {
    const fs = require('fs');
    const path = require('path');
    const os = require('os');

    const logDir = path.join(os.homedir(), '.config', 'mcp-kanban', 'logs');
    const logFile = path.join(logDir, 'cli-errors.log');

    // Create log directory if it doesn't exist
    if (!fs.existsSync(logDir)) {
      fs.mkdirSync(logDir, { recursive: true });
    }

    const logEntry = {
      timestamp: new Date().toISOString(),
      error: {
        name: error.name,
        message: error.message,
        stack: error.stack,
      },
      command: process.argv.slice(2).join(' '),
      nodeVersion: process.version,
      platform: process.platform,
    };

    fs.appendFileSync(logFile, JSON.stringify(logEntry) + '\n');
  } catch (logError) {
    // Silently fail if logging doesn't work
  }
}

// Process-level error handlers
process.on('uncaughtException', (error) => {
  console.error(chalk.red('\nðŸ’¥ Uncaught Exception:'));
  console.error(formatError(error));
  logErrorToFile(error);
  process.exit(1);
});

process.on('unhandledRejection', (reason) => {
  const error = reason instanceof Error ? reason : new Error(String(reason));
  console.error(chalk.red('\nðŸ’¥ Unhandled Promise Rejection:'));
  console.error(formatError(error));
  logErrorToFile(error);
  process.exit(1);
});

// Graceful shutdown handlers
process.on('SIGINT', () => {
  console.log(chalk.yellow('\nâš ï¸  Received SIGINT, shutting down gracefully...'));
  // Clean up spinners and other resources
  if (global.cliComponents?.apiClient) {
    // Cleanup any ongoing requests
  }
  process.exit(130);
});

process.on('SIGTERM', () => {
  console.log(chalk.yellow('\nâš ï¸  Received SIGTERM, shutting down gracefully...'));
  process.exit(143);
});

// Handle no command provided
if (process.argv.length <= 2) {
  program.help();
}

// Parse command line arguments
program.parse();

export { program };

/**
 * Unit tests for validator functions
 */

import {
  validateTaskTitle,
  validatePriority,
  validateTaskSize,
  validateEmail,
  validateUrl,
  validateGitRepoUrl,
  validateBoardName,
  validateColumnName,
  validateTagName,
  validateDate,
