import prompts from 'prompts';
import chalk from 'chalk';
import { validateBoardName, validateColumnName } from './validators';
import { spinner } from '../utils/spinner';
import { PromptCancelledError } from './task-prompts';

export interface BoardSetupInput {
  name: string;
  description?: string;
  columns: Array<{ name: string; order: number }>;
  isPublic?: boolean;
}

export interface ColumnInput {
  name: string;
  position?: 'start' | 'end' | 'after';
  afterColumn?: string;
}

/**
 * Quick board setup with prompts library
 */
export async function quickBoardSetup(
  defaults?: Partial<BoardSetupInput>
): Promise<BoardSetupInput> {
  console.log(chalk.cyan('\nðŸš€ Quick Board Setup\n'));

  // Board name and description
  const boardInfo = await prompts([
    {
      type: 'text',
      name: 'name',
      message: 'Board name:',
      initial: defaults?.name,
      validate: value => {
        const result = validateBoardName(value);
        return result === true ? true : result;
      },
    },
    {
      type: 'text',
      name: 'description',
      message: 'Description (optional):',
      initial: defaults?.description,
    },
  ]);

  if (!boardInfo.name) {
    throw new Error('Board setup cancelled');
  }

  // Column setup
  const { useTemplate } = await prompts({
    type: 'select',
    name: 'useTemplate',
    message: 'Column setup:',
    choices: [
      { title: 'Use template', value: 'template' },
      { title: 'Custom columns', value: 'custom' },
    ],
    initial: 0,
  });

  let columns: Array<{ name: string; order: number }> = [];

  if (useTemplate === 'template') {
    const { template } = await prompts({
      type: 'select',
      name: 'template',
      message: 'Choose template:',
      choices: [
        {
          title: 'Basic Kanban (To Do, In Progress, Done)',
          value: 'basic',
          description: 'Simple 3-column board',
        },
        {
          title: 'Scrum Board (Backlog, To Do, In Progress, Review, Done)',
          value: 'scrum',
          description: 'Standard Scrum workflow',
        },
        {
          title: 'Bug Tracking (New, Confirmed, In Progress, Testing, Resolved)',
          value: 'bugs',
          description: 'Bug tracking workflow',
        },
        {
          title: 'Content Pipeline (Ideas, Writing, Editing, Review, Published)',
          value: 'content',
          description: 'Content creation workflow',
        },
      ],
      initial: 0,
    });

    // Set columns based on template
    switch (template) {
      case 'basic':
        columns = [
          { name: 'To Do', order: 0 },
          { name: 'In Progress', order: 1 },
          { name: 'Done', order: 2 },
        ];
        break;
      case 'scrum':
        columns = [
          { name: 'Backlog', order: 0 },
          { name: 'To Do', order: 1 },
          { name: 'In Progress', order: 2 },
          { name: 'Review', order: 3 },
          { name: 'Done', order: 4 },
        ];
        break;
      case 'bugs':
        columns = [
          { name: 'New', order: 0 },
          { name: 'Confirmed', order: 1 },
          { name: 'In Progress', order: 2 },
          { name: 'Testing', order: 3 },
          { name: 'Resolved', order: 4 },
        ];
        break;
      case 'content':
        columns = [
          { name: 'Ideas', order: 0 },
          { name: 'Writing', order: 1 },
          { name: 'Editing', order: 2 },
          { name: 'Review', order: 3 },
          { name: 'Published', order: 4 },
        ];
        break;
    }
  } else {
    // Custom columns
    let addingColumns = true;
    let order = 0;

    while (addingColumns) {
      const { columnName } = await prompts({
        type: 'text',
        name: 'columnName',
        message: `Column ${order + 1} name (leave empty to finish):`,
        validate: value => {
          if (!value) return true; // Allow empty to finish
          const result = validateColumnName(value);
          return result === true ? true : result;
        },
      });

      if (!columnName) {
        addingColumns = false;
      } else {
        columns.push({ name: columnName, order });
        order++;
      }
    }

    if (columns.length === 0) {
      throw new Error('Board must have at least one column');
    }
  }

  // Public/Private setting
  const { isPublic } = await prompts({
    type: 'toggle',
    name: 'isPublic',
    message: 'Make board public?',
    initial: defaults?.isPublic ?? false,
    active: 'yes',
    inactive: 'no',
  });

  // Show summary
  console.log(chalk.green('\nâœ… Board Configuration:'));
  console.log(chalk.gray('â”€'.repeat(40)));
  console.log(`Name: ${chalk.bold(boardInfo.name)}`);
  if (boardInfo.description) {
    console.log(`Description: ${boardInfo.description}`);
  }
  console.log(`Visibility: ${isPublic ? 'Public' : 'Private'}`);
  console.log(`Columns: ${columns.map(c => c.name).join(' â†’ ')}`);
  console.log(chalk.gray('â”€'.repeat(40)));

  return {
    name: boardInfo.name,
    description: boardInfo.description || undefined,
    columns,
    isPublic,
  };
}

/**
 * Confirm action utility
 */
export async function confirmAction(message: string, defaultAnswer = false): Promise<boolean> {
  const response = await prompts({
    type: 'confirm',
    name: 'confirmed',
    message,
    initial: defaultAnswer,
  });

  return response.confirmed ?? false;
}

/**
 * Select from list utility
 */
export async function selectFromList<T extends { id: string; name: string }>(
  message: string,
  items: T[],
  options?: {
    allowMultiple?: boolean;
    showDescription?: (item: T) => string;
  }
): Promise<T | T[] | null> {
  if (items.length === 0) {
    console.log(chalk.yellow('No items available to select'));
    return null;
  }

  const choices = items.map(item => ({
    title: item.name,
    value: item.id,
    description: options?.showDescription?.(item),
  }));

  if (options?.allowMultiple) {
    const response = await prompts({
      type: 'multiselect',
      name: 'selected',
      message,
      choices,
      hint: '- Space to select. Return to submit',
    });

    if (!response.selected || response.selected.length === 0) {
      return null;
    }

    return items.filter(item => response.selected.includes(item.id));
  }
  const response = await prompts({
    type: 'select',
    name: 'selected',
    message,
    choices,
  });

  if (!response.selected) {
    return null;
  }

  return items.find(item => item.id === response.selected) || null;
}

/**
 * Add column to existing board prompt
 */
export async function addColumnPrompt(
  existingColumns: Array<{ id: string; name: string; order: number }>
): Promise<ColumnInput | null> {
  console.log(chalk.cyan('\nâž• Add New Column\n'));

  const response = await prompts([
    {
      type: 'text',
      name: 'name',
      message: 'Column name:',
      validate: value => {
        const result = validateColumnName(value);
        return result === true ? true : result;
      },
    },
    {
      type: 'select',
      name: 'position',
      message: 'Position:',
      choices: [
        { title: 'At the beginning', value: 'start' },
        { title: 'At the end', value: 'end' },
        { title: 'After specific column', value: 'after' },
      ],
      initial: 1, // Default to 'end'
    },
  ]);

  if (!response.name) {
    return null;
  }

  let afterColumn: string | undefined;

  if (response.position === 'after' && existingColumns.length > 0) {
    const selected = await selectFromList('Insert after which column?', existingColumns);

    if (selected && !Array.isArray(selected)) {
      afterColumn = selected.id;
    }
  }

  return {
    name: response.name,
    position: response.position,
    afterColumn,
  };
}

/**
 * Board settings prompt
 */
export async function boardSettingsPrompt(currentSettings: {
  name: string;
  description?: string;
  isPublic: boolean;
  defaultAssignee?: string;
  autoArchiveDays?: number;
}): Promise<Partial<typeof currentSettings>> {
  console.log(chalk.cyan('\nâš™ï¸  Board Settings\n'));

  const response = await prompts([
    {
      type: 'text',
      name: 'name',
      message: 'Board name:',
      initial: currentSettings.name,
      validate: value => {
        const result = validateBoardName(value);
        return result === true ? true : result;
      },
    },
    {
      type: 'text',
      name: 'description',
      message: 'Description:',
      initial: currentSettings.description,
    },
    {
      type: 'toggle',
      name: 'isPublic',
      message: 'Public board?',
      initial: currentSettings.isPublic,
      active: 'yes',
      inactive: 'no',
    },
    {
      type: 'text',
      name: 'defaultAssignee',
      message: 'Default assignee (optional):',
      initial: currentSettings.defaultAssignee,
    },
    {
      type: 'number',
      name: 'autoArchiveDays',
      message: 'Auto-archive completed tasks after (days, 0 to disable):',
      initial: currentSettings.autoArchiveDays || 0,
      min: 0,
      max: 365,
    },
  ]);

  // Filter out unchanged values
  const changes: Partial<typeof currentSettings> = {};

  if (response.name !== currentSettings.name) {
    changes.name = response.name;
  }
  if (response.description !== currentSettings.description) {
    changes.description = response.description || undefined;
  }
  if (response.isPublic !== currentSettings.isPublic) {
    changes.isPublic = response.isPublic;
  }
  if (response.defaultAssignee !== currentSettings.defaultAssignee) {
    changes.defaultAssignee = response.defaultAssignee || undefined;
  }
  if (response.autoArchiveDays !== currentSettings.autoArchiveDays) {
    changes.autoArchiveDays = response.autoArchiveDays || undefined;
  }

  return changes;
}

/**
 * Delete confirmation with safety check
 */
export async function confirmDelete(
  itemType: string,
  itemName: string,
  options?: {
    showWarning?: string;
    requireTyping?: boolean;
  }
): Promise<boolean> {
  console.log(chalk.red(`\nâš ï¸  Delete ${itemType}\n`));

  if (options?.showWarning) {
    console.log(chalk.yellow(options.showWarning));
    console.log();
  }

  console.log(`You are about to delete: ${chalk.bold(itemName)}`);
  console.log(chalk.gray('This action cannot be undone.\n'));

  if (options?.requireTyping) {
    const { confirmText } = await prompts({
      type: 'text',
      name: 'confirmText',
      message: `Type "${itemName}" to confirm:`,
      validate: value => (value === itemName ? true : `Please type exactly: ${itemName}`),
    });

    if (confirmText !== itemName) {
      return false;
    }
  }

  return confirmAction(`Are you sure you want to delete this ${itemType}?`, false);
}

import type { PromptOptions } from 'enquirer';
import { prompt } from 'enquirer';
import chalk from 'chalk';
import type { Priority, TaskSize } from './validators';
import {
  validateTaskTitle,
  validateDate,
  validateTimeEstimate,
  PRIORITIES,
  TASK_SIZES,
} from './validators';
import { TaskSizeEstimator } from '../estimation/task-size-estimator';

interface TaskEstimation {
  size: TaskSize;
  avgHours: number;
  confidence: string;
  reasoning: string[];
}

interface FormatterInterface {
  info: (message: string) => void;
  success: (message: string) => void;
  warn: (message: string) => void;
  error: (message: string) => void;
}

// Simple formatter for prompts - falls back to console if no proper formatter available
const simpleFormatter: FormatterInterface = {
  info: (message: string): void => console.log(chalk.cyan(message)),
  success: (message: string): void => console.log(chalk.green(message)),
  warn: (message: string): void => console.log(chalk.yellow(message)),
  error: (message: string): void => console.log(chalk.red(message)),
};

/**
 * Error thrown when a prompt is cancelled
 */
export class PromptCancelledError extends Error {
  constructor(message = 'Prompt was cancelled by user') {
    super(message);
    this.name = 'PromptCancelledError';
  }
}

/**
 * Wrapper for prompt that handles cancellation
 */
async function safePrompt<T>(promptConfig: PromptOptions | PromptOptions[]): Promise<T> {
  try {
    return await prompt<T>(promptConfig);
  } catch (error) {
    // Check if it's a cancellation (Ctrl+C, ESC, etc.)
    if (error instanceof Error) {
      const errorMessage = error.message.toLowerCase();
      if (
        errorMessage.includes('cancel') ||
        errorMessage.includes('abort') ||
        errorMessage.includes('interrupt') ||
        error.name === 'SIGINT'
      ) {
        throw new PromptCancelledError(`Operation cancelled: ${error.message}`);
      }
    }
    // Re-throw other errors
    throw error;
  }
}

export interface TaskInput {
  title: string;
  description?: string;
  priority?: Priority;
  size?: TaskSize;
  assignee?: string;
  due_date?: string;
  estimated_hours?: number;
  tags?: string[];
}

export interface MoveTaskInput {
  taskId: string;
  targetColumn: string;
  position?: number;
}

export interface BulkActionInput {
  taskIds: string[];
  action: 'move' | 'delete' | 'archive' | 'assign' | 'tag';
  params?: Record<string, unknown>;
}

/**
 * Create task interactive prompt
 */
export async function createTaskPrompt(defaults?: Partial<TaskInput>): Promise<TaskInput> {
  try {
    simpleFormatter.info('\nðŸ“ Create New Task\n');
    simpleFormatter.info(chalk.gray('Press Ctrl+C to cancel at any time\n'));

    // Create estimator instance
    const estimator = new TaskSizeEstimator();

    // Get basic info first for estimation
    const basicInfo = await safePrompt<{ title: string; description?: string }>([
      {
        type: 'input',
        name: 'title',
        message: 'Task title:',
        initial: defaults?.title,
        validate: validateTaskTitle,
      },
      {
        type: 'text',
        name: 'description',
        message: 'Description (optional):',
        initial: defaults?.description,
        multiline: true,
        hint: 'Press Ctrl+D when done',
      },
    ]);

    // Generate size estimation
    let suggestedSize: TaskSize | undefined;
    let estimation: TaskEstimation | undefined;

    if (basicInfo.title) {
      try {
        estimation = estimator.estimateTime({
          title: basicInfo.title,
          description: basicInfo.description,
        }) as TaskEstimation;
        suggestedSize = estimation.size;

        // Show estimation
        simpleFormatter.info(chalk.cyan('\nðŸ¤– AI Size Estimation:'));
        simpleFormatter.info(
          chalk.yellow(`  Suggested Size: ${suggestedSize} (${estimation.avgHours} hours)`)
        );
        simpleFormatter.info(chalk.gray(`  Confidence: ${estimation.confidence}`));
        if (estimation.reasoning?.length > 0) {
          simpleFormatter.info(chalk.gray('  Reasoning:'));
          estimation.reasoning.forEach((reason: string) => {
            simpleFormatter.info(chalk.gray(`    â€¢ ${reason}`));
          });
        }
        simpleFormatter.info('');
      } catch (error) {
        simpleFormatter.warn('âš ï¸  Size estimation unavailable\n');
      }
    }

    const additionalInfo = await safePrompt<Omit<TaskInput, 'title' | 'description'>>([
      {
        type: 'select',
        name: 'priority',
        message: 'Priority:',
        choices: [
          { name: 'P1', value: 'P1', hint: 'Critical - Must be done ASAP' },
          { name: 'P2', value: 'P2', hint: 'High - Important and urgent' },
          { name: 'P3', value: 'P3', hint: 'Medium - Important but not urgent' },
          { name: 'P4', value: 'P4', hint: 'Low - Nice to have' },
          { name: 'P5', value: 'P5', hint: 'Very Low - Backlog' },
          { name: 'Skip', value: undefined, hint: 'No priority' },
        ],
        initial: defaults?.priority ? PRIORITIES.indexOf(defaults.priority) : 2,
      },
      {
        type: 'select',
        name: 'size',
        message: suggestedSize ? `Task size (AI suggests: ${suggestedSize}):` : 'Task size:',
        choices: [
          {
            name:
              suggestedSize === 'S' ? `S â­ (AI suggested - ${estimation?.avgHours ?? 1}h)` : 'S',
            value: 'S',
            hint: 'Small - Less than 2 hours',
          },
          {
            name:
              suggestedSize === 'M' ? `M â­ (AI suggested - ${estimation?.avgHours ?? 3}h)` : 'M',
            value: 'M',
            hint: 'Medium - 2-4 hours',
          },
          {
            name:
              suggestedSize === 'L' ? `L â­ (AI suggested - ${estimation?.avgHours ?? 6}h)` : 'L',
            value: 'L',
            hint: 'Large - 4-8 hours',
          },
          {
            name:
              suggestedSize === 'XL'
                ? `XL â­ (AI suggested - ${estimation?.avgHours ?? 12}h)`
                : 'XL',
            value: 'XL',
            hint: 'Extra Large - More than 8 hours',
          },
          { name: 'Skip', value: undefined, hint: 'No size estimate' },
        ],
        initial: ((): number => {
          if (suggestedSize) {
            return TASK_SIZES.indexOf(suggestedSize);
          }
          if (defaults?.size) {
            return TASK_SIZES.indexOf(defaults.size);
          }
          return 1;
        })(),
      },
      {
        type: 'input',
        name: 'assignee',
        message: 'Assignee (optional):',
        initial: defaults?.assignee,
        hint: 'Username or email',
      },
      {
        type: 'input',
        name: 'due_date',
        message: 'Due date (optional):',
        initial: defaults?.due_date,
        hint: 'Format: YYYY-MM-DD',
        validate: validateDate,
      },
      {
        type: 'numeral',
        name: 'estimated_hours',
        message: 'Estimated hours (optional):',
        initial: defaults?.estimated_hours,
        float: true,
        validate: (value: string): string | boolean => {
          if (!value) return true;
          return validateTimeEstimate(value);
        },
      },
      {
        type: 'list',
        name: 'tags',
        message: 'Tags (comma-separated, optional):',
        initial: defaults?.tags?.join(', '),
        separator: ',',
        hint: 'e.g., frontend, bug, urgent',
      },
    ]);

    // Merge basicInfo and additionalInfo
    const response = {
      title: basicInfo.title,
      description: basicInfo.description,
      ...additionalInfo,
    };

    // Clean up the response
    const cleanedResponse: TaskInput = {
      title: response.title,
    };

    if (response.description?.trim()) {
      cleanedResponse.description = response.description.trim();
    }
    if (response.priority && response.priority !== 'undefined') {
      cleanedResponse.priority = response.priority as Priority;
    }
    if (response.size && response.size !== 'undefined') {
      cleanedResponse.size = response.size as TaskSize;
    }
    if (response.assignee?.trim()) {
      cleanedResponse.assignee = response.assignee.trim();
    }
    if (response.due_date?.trim()) {
      cleanedResponse.due_date = response.due_date.trim();
    }
    if (response.estimated_hours) {
      cleanedResponse.estimated_hours = response.estimated_hours;
    }
    if (response.tags && response.tags.length > 0) {
      cleanedResponse.tags = response.tags
        .map((tag: string) => tag.trim())
        .filter((tag: string) => tag.length > 0);
    }

    return cleanedResponse;
  } catch (error) {
    if (error instanceof PromptCancelledError) {
      simpleFormatter.warn('\nâš ï¸  Task creation cancelled\n');
      throw error;
    }
    simpleFormatter.error(
      `\nâŒ Failed to create task: ${error instanceof Error ? error.message : String(error)}`
    );
    throw error;
  }
}

/**
 * Move task interactive prompt
 */
export async function moveTaskPrompt(
  taskId: string,
  availableColumns: Array<{ id: string; name: string; taskCount: number }>
): Promise<MoveTaskInput> {
  try {
    simpleFormatter.info(chalk.cyan(`\nðŸ”„ Move Task ${taskId}\n`));
    simpleFormatter.info(chalk.gray('Press Ctrl+C to cancel at any time\n'));

    const response = await safePrompt<{
      targetColumn: string;
      position: string;
    }>([
      {
        type: 'select',
        name: 'targetColumn',
        message: 'Move to column:',
        choices: availableColumns.map(col => ({
          name: `${col.name} (${col.taskCount} tasks)`,
          value: col.id,
        })),
      },
      {
        type: 'select',
        name: 'position',
        message: 'Position in column:',
        choices: [
          { name: 'Top', value: 'top' },
          { name: 'Bottom', value: 'bottom' },
          { name: 'Specific position', value: 'specific' },
        ],
      },
    ]);

    let position: number | undefined;

    if (response.position === 'specific') {
      const targetCol = availableColumns.find(c => c.id === response.targetColumn);
      const maxPosition = targetCol?.taskCount ?? 0;

      const posResponse = await safePrompt<{ position: number }>({
        type: 'numeral',
        name: 'position',
        message: `Position (1-${maxPosition + 1}):`,
        min: 1,
        max: maxPosition + 1,
        initial: 1,
      });

      position = posResponse.position - 1; // Convert to 0-based index
    } else if (response.position === 'top') {
      position = 0;
    }
    // 'bottom' leaves position undefined, which means append

    return {
      taskId,
      targetColumn: response.targetColumn,
      position,
    };
  } catch (error) {
    if (error instanceof PromptCancelledError) {
      simpleFormatter.warn('\nâš ï¸  Move operation cancelled\n');
      throw error;
    }
    simpleFormatter.error(
      `\nâŒ Failed to move task: ${error instanceof Error ? error.message : String(error)}`
    );
    throw error;
  }
}

/**
 * Bulk task action prompt
 */
export async function bulkTaskActionPrompt(
  tasks: Array<{ id: string; title: string; status: string }>
): Promise<BulkActionInput | null> {
  try {
    simpleFormatter.info(chalk.cyan(`\nðŸ“¦ Bulk Action for ${tasks.length} tasks\n`));
    simpleFormatter.info(chalk.gray('Press Ctrl+C to cancel at any time\n'));

    // Show selected tasks
    simpleFormatter.info(chalk.gray('Selected tasks:'));
    tasks.forEach((task, index) => {
      if (index < 5) {
        simpleFormatter.info(chalk.gray(`  - [${task.id}] ${task.title}`));
      }
    });
    if (tasks.length > 5) {
      simpleFormatter.info(chalk.gray(`  ... and ${tasks.length - 5} more`));
    }
    simpleFormatter.info('');

    const { action } = await safePrompt<{ action: string }>({
      type: 'select',
      name: 'action',
      message: 'Choose bulk action:',
      choices: [
        { name: 'Move to column', value: 'move' },
        { name: 'Assign to user', value: 'assign' },
        { name: 'Add tags', value: 'tag' },
        { name: 'Archive tasks', value: 'archive' },
        { name: 'Delete tasks', value: 'delete' },
        { name: 'Cancel', value: 'cancel' },
      ],
    });

    if (action === 'cancel') {
      return null;
    }

    const taskIds = tasks.map(t => t.id);
    const params: Record<string, unknown> = {};

    switch (action) {
      case 'move': {
        const { column } = await safePrompt<{ column: string }>({
          type: 'input',
          name: 'column',
          message: 'Target column name:',
          validate: (value: string) => (value.trim() ? true : 'Column name required'),
        });
        params.column = column;
        break;
      }

      case 'assign': {
        const { assignee } = await safePrompt<{ assignee: string }>({
          type: 'input',
          name: 'assignee',
          message: 'Assign to (username or email):',
          validate: (value: string) => (value.trim() ? true : 'Assignee required'),
        });
        params.assignee = assignee;
        break;
      }

      case 'tag': {
        const { tags } = await safePrompt<{ tags: string[] }>({
          type: 'list',
          name: 'tags',
          message: 'Add tags (comma-separated):',
          separator: ',',
          validate: (value: string[]) => (value.length > 0 ? true : 'At least one tag required'),
        });
        params.tags = tags.map(t => t.trim()).filter(t => t.length > 0);
        break;
      }

      case 'delete': {
        const { confirm } = await safePrompt<{ confirm: boolean }>({
          type: 'confirm',
          name: 'confirm',
          message: chalk.red(`Are you sure you want to delete ${tasks.length} tasks?`),
          initial: false,
        });
        if (!confirm) {
          return null;
        }
        break;
      }

      case 'archive': {
        const { confirm } = await safePrompt<{ confirm: boolean }>({
          type: 'confirm',
          name: 'confirm',
          message: `Archive ${tasks.length} tasks?`,
          initial: true,
        });
        if (!confirm) {
          return null;
        }
        break;
      }

      default:
        // Should never reach here due to action validation, but required for ESLint
        throw new Error(`Unknown action: ${action}`);
    }

    return {
      taskIds,
      action: action as BulkActionInput['action'],
      params: Object.keys(params).length > 0 ? params : undefined,
    };
  } catch (error) {
    if (error instanceof PromptCancelledError) {
      simpleFormatter.warn('\nâš ï¸  Bulk action cancelled\n');
      return null;
    }
    simpleFormatter.error(
      `\nâŒ Failed to execute bulk action: ${error instanceof Error ? error.message : String(error)}`
    );
    throw error;
  }
}

/**
 * Task filter prompt
 */
export async function taskFilterPrompt(): Promise<{
  status?: string[];
  priority?: Priority[];
  assignee?: string;
  tags?: string[];
  dateRange?: { start: string; end: string };
}> {
  try {
    simpleFormatter.info(chalk.cyan('\nðŸ” Filter Tasks\n'));
    simpleFormatter.info(chalk.gray('Press Ctrl+C to cancel at any time\n'));

    const response = await safePrompt<{
      filterBy: string[];
    }>({
      type: 'multiselect',
      name: 'filterBy',
      message: 'Filter by:',
      choices: [
        { name: 'Status', value: 'status' },
        { name: 'Priority', value: 'priority' },
        { name: 'Assignee', value: 'assignee' },
        { name: 'Tags', value: 'tags' },
        { name: 'Date range', value: 'dateRange' },
      ],
      hint: 'Space to select, Enter to continue',
    });

    const filters: Record<string, unknown> = {};

    if (response.filterBy.includes('status')) {
      const { status } = await safePrompt<{ status: string[] }>({
        type: 'multiselect',
        name: 'status',
        message: 'Select statuses:',
        choices: ['todo', 'in_progress', 'done', 'blocked', 'cancelled'],
      });
      if (status.length > 0) filters.status = status;
    }

    if (response.filterBy.includes('priority')) {
      const { priority } = await safePrompt<{ priority: Priority[] }>({
        type: 'multiselect',
        name: 'priority',
        message: 'Select priorities:',
        choices: PRIORITIES.map(p => ({ name: p, value: p })),
      });
      if (priority.length > 0) filters.priority = priority;
    }

    if (response.filterBy.includes('assignee')) {
      const { assignee } = await safePrompt<{ assignee: string }>({
        type: 'input',
        name: 'assignee',
        message: 'Assignee (username or email):',
      });
      if (assignee.trim()) filters.assignee = assignee.trim();
    }

    if (response.filterBy.includes('tags')) {
      const { tags } = await safePrompt<{ tags: string[] }>({
        type: 'list',
        name: 'tags',
        message: 'Tags (comma-separated):',
        separator: ',',
      });
      if (tags.length > 0) {
        filters.tags = tags.map(t => t.trim()).filter(t => t.length > 0);
      }
    }

    if (response.filterBy.includes('dateRange')) {
      const dateRange = await safePrompt<{ start: string; end: string }>([
        {
          type: 'input',
          name: 'start',
          message: 'Start date (YYYY-MM-DD):',
          validate: validateDate,
        },
        {
          type: 'input',
          name: 'end',
          message: 'End date (YYYY-MM-DD):',
          validate: validateDate,
        },
      ]);
      if (dateRange.start && dateRange.end) {
        filters.dateRange = dateRange;
      }
    }

    return filters;
  } catch (error) {
    if (error instanceof PromptCancelledError) {
      simpleFormatter.warn('\nâš ï¸  Filter cancelled\n');
      return {};
    }
    simpleFormatter.error(
      `\nâŒ Failed to apply filters: ${error instanceof Error ? error.message : String(error)}`
    );
    throw error;
  }
}

