import { z } from 'zod';
import { 
  sanitizeTaskTitle, 
  sanitizeDescription, 
  sanitizeName, 
  sanitizeTag, 
  sanitizeEmail, 
  sanitizeUrl,
  createSafePromptValidator,
  detectSuspicious,
  inputSanitizer
} from '../utils/input-sanitizer';

/**
 * Input validation functions for CLI prompts with comprehensive sanitization
 * Enhanced for TASK-119: Input sanitization for all prompts
 */

// Task size options
export const TASK_SIZES = ['S', 'M', 'L', 'XL'] as const;
export type TaskSize = (typeof TASK_SIZES)[number];

// Priority options
export const PRIORITIES = ['P1', 'P2', 'P3', 'P4', 'P5'] as const;
export type Priority = (typeof PRIORITIES)[number];

// Status options
export const STATUSES = ['todo', 'in_progress', 'done', 'blocked', 'cancelled'] as const;
export type Status = (typeof STATUSES)[number];

/**
 * Validate task title with comprehensive sanitization
 */
export const validateTaskTitle = createSafePromptValidator(
  sanitizeTaskTitle,
  (input: string): true | string => {
    const trimmed = input.trim();

    if (!trimmed) {
      return 'Task title cannot be empty';
    }

    if (trimmed.length < 3) {
      return 'Task title must be at least 3 characters long';
    }

    if (trimmed.length > 200) {
      return 'Task title must be less than 200 characters';
    }

    // Additional security checks
    const suspiciousCheck = detectSuspicious(input);
    if (suspiciousCheck.suspicious) {
      return `Security issue: ${suspiciousCheck.patterns.join(', ')} detected in title`;
    }

    return true;
  }
);

/**
 * Validate priority
 */
export function validatePriority(input: string): true | string {
  const upperInput = input.toUpperCase();

  if (!PRIORITIES.includes(upperInput as Priority)) {
    return `Priority must be one of: ${PRIORITIES.join(', ')}`;
  }

  return true;
}

/**
 * Validate task size
 */
export function validateTaskSize(input: string): true | string {
  const upperInput = input.toUpperCase();

  if (!TASK_SIZES.includes(upperInput as TaskSize)) {
    return `Task size must be one of: ${TASK_SIZES.join(', ')}`;
  }

  return true;
}

/**
 * Validate email address with sanitization
 */
export const validateEmail = createSafePromptValidator(
  sanitizeEmail,
  (input: string): true | string => {
    const trimmed = input.trim();

    if (!trimmed) {
      return 'Email address cannot be empty';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(trimmed)) {
      return 'Please enter a valid email address';
    }

    return true;
  }
);

/**
 * Validate URL with sanitization
 */
export const validateUrl = createSafePromptValidator(
  sanitizeUrl,
  (input: string): true | string => {
    const trimmed = input.trim();

    if (!trimmed) {
      return 'URL cannot be empty';
    }

    try {
      const url = new URL(trimmed);

      // Additional security checks for URLs
      if (url.protocol === 'javascript:' || url.protocol === 'data:' || url.protocol === 'vbscript:') {
        return 'Unsafe URL protocol detected';
      }

      return true;
    } catch {
      return 'Please enter a valid URL';
    }
  }
);

/**
 * Validate git repository URL
 */
export function validateGitRepoUrl(input: string): true | string {
  const urlValidation = validateUrl(input);
  if (urlValidation !== true) {
    return urlValidation;
  }

  const gitUrlRegex =
    /^(https?:\/\/(github\.com|gitlab\.com|bitbucket\.org)\/[\w-]+\/[\w-]+|git@(github\.com|gitlab\.com|bitbucket\.org):[\w-]+\/[\w-]+\.git)$/;

  if (!gitUrlRegex.test(input)) {
    return 'Please enter a valid Git repository URL (GitHub, GitLab, or Bitbucket)';
  }

  return true;
}

/**
 * Validate board name with sanitization
 */
export const validateBoardName = createSafePromptValidator(
  (input: string) => sanitizeName(input, 50),
  (input: string): true | string => {
    const trimmed = input.trim();

    if (!trimmed) {
      return 'Board name cannot be empty';
    }

    if (trimmed.length < 2) {
      return 'Board name must be at least 2 characters long';
    }

    if (trimmed.length > 50) {
      return 'Board name must be less than 50 characters';
    }

    return true;
  }
);

/**
 * Validate column name with sanitization
 */
export const validateColumnName = createSafePromptValidator(
  (input: string) => sanitizeName(input, 30),
  (input: string): true | string => {
    const trimmed = input.trim();

    if (!trimmed) {
      return 'Column name cannot be empty';
    }

    if (trimmed.length > 30) {
      return 'Column name must be less than 30 characters';
    }

    return true;
  }
);

/**
 * Validate tag name with sanitization
 */
export const validateTagName = createSafePromptValidator(
  sanitizeTag,
  (input: string): true | string => {
    const trimmed = input.trim();

    if (!trimmed) {
      return 'Tag name cannot be empty';
    }

    if (trimmed.length > 20) {
      return 'Tag name must be less than 20 characters';
    }

    return true;
  }
);

/**
 * Validate date input
 */
export function validateDate(input: string): true | string {
  const trimmed = input.trim();

  if (!trimmed) {
    return true; // Date is optional
  }

  // Accept various date formats
  const dateFormats = [
    /^\d{4}-\d{2}-\d{2}$/, // YYYY-MM-DD
    /^\d{2}\/\d{2}\/\d{4}$/, // MM/DD/YYYY
    /^\d{2}-\d{2}-\d{4}$/, // DD-MM-YYYY
  ];

  const matchesFormat = dateFormats.some(format => format.test(trimmed));
  if (!matchesFormat) {
    return 'Please enter a date in format YYYY-MM-DD, MM/DD/YYYY, or DD-MM-YYYY';
  }

  const date = new Date(trimmed);
  if (isNaN(date.getTime())) {
    return 'Please enter a valid date';
  }

  return true;
}

/**
 * Validate time estimate (hours)
 */
export function validateTimeEstimate(input: string): true | string {
  const trimmed = input.trim();

  if (!trimmed) {
    return true; // Time estimate is optional
  }

  const num = parseFloat(trimmed);
  if (isNaN(num)) {
    return 'Please enter a valid number';
  }

  if (num <= 0) {
    return 'Time estimate must be greater than 0';
  }

  if (num > 999) {
    return 'Time estimate seems too high. Please enter a value less than 1000 hours';
  }

  return true;
}

/**
 * Validate percentage
 */
export function validatePercentage(input: string): true | string {
  const trimmed = input.trim();

  if (!trimmed) {
    return 'Percentage cannot be empty';
  }

  const num = parseInt(trimmed, 10);
  if (isNaN(num)) {
    return 'Please enter a valid number';
  }

  if (num < 0 || num > 100) {
    return 'Percentage must be between 0 and 100';
  }

  return true;
}

/**
 * Create a custom validator with min/max length
 */
export function createLengthValidator(
  fieldName: string,
  minLength: number,
  maxLength: number
): (input: string) => true | string {
  return (input: string) => {
    const trimmed = input.trim();

    if (!trimmed && minLength > 0) {
      return `${fieldName} cannot be empty`;
    }

    if (trimmed.length < minLength) {
      return `${fieldName} must be at least ${minLength} characters long`;
    }

    if (trimmed.length > maxLength) {
      return `${fieldName} must be less than ${maxLength} characters`;
    }

    return true;
  };
}

/**
 * Create a custom enum validator
 */
export function createEnumValidator<T extends readonly string[]>(
  fieldName: string,
  validOptions: T
): (input: string) => true | string {
  return (input: string) => {
    if (!validOptions.includes(input)) {
      return `${fieldName} must be one of: ${validOptions.join(', ')}`;
    }
    return true;
  };
}

/**
 * Zod schemas for more complex validation
 */

export const TaskSchema = z.object({
  title: z.string().min(3).max(200),
  description: z.string().optional(),
  priority: z.enum(PRIORITIES).optional(),
  size: z.enum(TASK_SIZES).optional(),
  assignee: z.string().optional(),
  due_date: z.string().datetime().optional(),
  tags: z.array(z.string()).optional(),
});

export const BoardSchema = z.object({
  name: z
    .string()
    .min(2)
    .max(50)
    .regex(/^[\w\s-]+$/),
  description: z.string().optional(),
  columns: z.array(
    z.object({
      name: z.string().min(1).max(30),
      order: z.number().int().min(0),
    })
  ),
});

export type TaskInput = z.infer<typeof TaskSchema>;
export type BoardInput = z.infer<typeof BoardSchema>;

/**
 * Additional sanitized validators for descriptions and other text inputs
 */

/**
 * Validate task description with sanitization
 */
export const validateTaskDescription = createSafePromptValidator(
  sanitizeDescription,
  (input: string): true | string => {
    const trimmed = input.trim();

    if (trimmed.length > 2000) {
      return 'Description must be less than 2000 characters';
    }

    return true;
  }
);

/**
 * Validate board description with sanitization  
 */
export const validateBoardDescription = createSafePromptValidator(
  sanitizeDescription,
  (input: string): true | string => {
    const trimmed = input.trim();

    if (trimmed.length > 500) {
      return 'Board description must be less than 500 characters';
    }

    return true;
  }
);

/**
 * Validate assignee name with sanitization
 */
export const validateAssignee = createSafePromptValidator(
  (input: string) => sanitizeName(input, 100),
  (input: string): true | string => {
    const trimmed = input.trim();

    if (trimmed.length > 100) {
      return 'Assignee name must be less than 100 characters';
    }

    return true;
  }
);

/**
 * Create safe versions of existing Zod schemas with sanitization
 */
export const SafeTaskSchema = z.object({
  title: z.string().transform(val => sanitizeTaskTitle(val).sanitized).pipe(z.string().min(3).max(200)),
  description: z.string().optional().transform(val => val ? sanitizeDescription(val).sanitized : val),
  priority: z.enum(PRIORITIES).optional(),
  size: z.enum(TASK_SIZES).optional(),
  assignee: z.string().optional().transform(val => val ? sanitizeName(val, 100).sanitized : val),
  due_date: z.string().optional(),
  tags: z.array(z.string().transform(val => sanitizeTag(val).sanitized)).optional(),
});

export const SafeBoardSchema = z.object({
  name: z.string().transform(val => sanitizeName(val, 50).sanitized).pipe(
    z.string().min(2).max(50)
  ),
  description: z.string().optional().transform(val => val ? sanitizeDescription(val).sanitized : val),
  columns: z.array(
    z.object({
      name: z.string().transform(val => sanitizeName(val, 30).sanitized).pipe(z.string().min(1).max(30)),
      order: z.number().int().min(0),
    })
  ),
});

export type SafeTaskInput = z.infer<typeof SafeTaskSchema>;
export type SafeBoardInput = z.infer<typeof SafeBoardSchema>;

/**
 * Utility function to validate and sanitize any input
 */
export function validateAndSanitizeInput(
  input: string,
  type: 'title' | 'description' | 'name' | 'tag' | 'email' | 'url' | 'assignee'
): { valid: boolean; sanitized: string; error?: string; warnings?: string[] } {
  try {
    let validator: (input: string) => true | string;

    switch (type) {
      case 'title':
        validator = validateTaskTitle;
        break;
      case 'description':
        validator = validateTaskDescription;
        break;
      case 'name':
        validator = validateBoardName;
        break;
      case 'tag':
        validator = validateTagName;
        break;
      case 'email':
        validator = validateEmail;
        break;
      case 'url':
        validator = validateUrl;
        break;
      case 'assignee':
        validator = validateAssignee;
        break;
      default:
        return { valid: false, sanitized: input, error: 'Unknown validation type' };
    }

    const result = validator(input);
    if (result === true) {
      // Get the sanitized version
      let sanitized: string;
      switch (type) {
        case 'title':
          sanitized = sanitizeTaskTitle(input).sanitized;
          break;
        case 'description':
          sanitized = sanitizeDescription(input).sanitized;
          break;
        case 'name':
          sanitized = sanitizeName(input).sanitized;
          break;
        case 'tag':
          sanitized = sanitizeTag(input).sanitized;
          break;
        case 'email':
          sanitized = sanitizeEmail(input).sanitized;
          break;
        case 'url':
          sanitized = sanitizeUrl(input).sanitized;
          break;
        case 'assignee':
          sanitized = sanitizeName(input, 100).sanitized;
          break;
        default:
          sanitized = input;
      }

      return { 
        valid: true, 
        sanitized,
        warnings: sanitized !== input ? ['Input was sanitized'] : undefined
      };
    } else {
      return { valid: false, sanitized: input, error: result };
    }
  } catch (error) {
    return { 
      valid: false, 
      sanitized: input, 
      error: `Validation error: ${error instanceof Error ? error.message : 'Unknown error'}` 
    };
  }
}

import type { ApiClient } from '../client';
import type { DashboardData } from '../utils/dashboard-manager';

/**
 * Service to fetch and transform data for dashboard components
 */
export class DashboardDataService {
  constructor(private readonly apiClient: ApiClient) {}

  /**
   * Fetch complete dashboard data from API
   */
  async fetchDashboardData(): Promise<DashboardData> {
    try {
      const [tasks, boards, activity] = await Promise.all([
        this.fetchTasks(),
        this.fetchBoards(),
        this.fetchActivity(),
      ]);

      return {
        tasks: this.transformTaskData(tasks),
        velocity: await this.calculateVelocity(),
        teamMembers: await this.fetchTeamMembers(),
        burndown: await this.calculateBurndown(),
        activity: this.transformActivityData(activity),
      };
    } catch (error) {
      console.warn('Failed to fetch dashboard data, using sample data:', error.message);
      return this.generateSampleData();
    }
  }

  /**
   * Fetch tasks from API
   */
  private async fetchTasks(): Promise<any[]> {
    try {
      const response = await this.apiClient.get('/tasks', {
        include_archived: false,
        limit: 1000,
      });
      return response.data || [];
    } catch (error) {
      throw new Error(`Failed to fetch tasks: ${error.message}`);
    }
  }

  /**
   * Fetch boards from API
   */
  private async fetchBoards(): Promise<any[]> {
    try {
      const response = await this.apiClient.get('/boards');
      return response.data || [];
    } catch (error) {
      throw new Error(`Failed to fetch boards: ${error.message}`);
    }
  }

  /**
   * Fetch activity from API
   */
  private async fetchActivity(): Promise<any[]> {
    try {
      const response = await this.apiClient.get('/activity', {
        limit: 20,
        order: 'desc',
      });
      return response.data || [];
    } catch (error) {
      throw new Error(`Failed to fetch activity: ${error.message}`);
    }
  }

  /**
   * Transform task data for dashboard display
   */
  private transformTaskData(tasks: any[]): DashboardData['tasks'] {
    const total = tasks.length;
    const byStatus: Record<string, number> = {};
    const byPriority: Record<string, number> = {};
    let completed = 0;
    let overdue = 0;

    const now = new Date();

    tasks.forEach(task => {
      // Count by status
      const status = task.status || 'todo';
      byStatus[status] = (byStatus[status] || 0) + 1;

      // Count by priority
      const priority = task.priority || 'P3';
      byPriority[priority] = (byPriority[priority] || 0) + 1;

      // Count completed
      if (status === 'done' || status === 'completed') {
        completed++;
      }

      // Count overdue
      if (task.due_date && new Date(task.due_date) < now && status !== 'done') {
        overdue++;
      }
    });

    return {
      total,
      byStatus,
      byPriority,
      completed,
      overdue,
    };
  }

  /**
   * Calculate velocity data (tasks completed per week)
   */
  private async calculateVelocity(): Promise<DashboardData['velocity']> {
    try {
      const endDate = new Date();
      const startDate = new Date();
      startDate.setDate(endDate.getDate() - 8 * 7); // 8 weeks back

      const response = await this.apiClient.get('/analytics/velocity', {
        start_date: startDate.toISOString(),
        end_date: endDate.toISOString(),
        group_by: 'week',
      });

      return (
        response.data?.map((item: any, index: number) => ({
          period: `W${index + 1}`,
          completed: item.completed_count || 0,
        })) || this.generateSampleVelocity()
      );
    } catch (error) {
      return this.generateSampleVelocity();
    }
  }

  /**
   * Fetch team member data
   */
  private async fetchTeamMembers(): Promise<DashboardData['teamMembers']> {
    try {
      const response = await this.apiClient.get('/analytics/team-workload');

      return (
        response.data?.map((member: any) => ({
          name: member.name || member.username,
          taskCount: member.active_tasks || 0,
          load: member.workload_percentage || 0,
        })) || this.generateSampleTeamMembers()
      );
    } catch (error) {
      return this.generateSampleTeamMembers();
    }
  }

  /**
   * Calculate burndown chart data
   */
  private async calculateBurndown(): Promise<DashboardData['burndown']> {
    try {
      const response = await this.apiClient.get('/analytics/burndown');

      return (
        response.data?.map((item: any) => ({
          day: item.day,
          remaining: item.remaining_tasks || 0,
          ideal: item.ideal_remaining || 0,
        })) || this.generateSampleBurndown()
      );
    } catch (error) {
      return this.generateSampleBurndown();
    }
  }

  /**
   * Transform activity data for display
   */
  private transformActivityData(activities: any[]): DashboardData['activity'] {
    return activities.slice(0, 10).map(activity => ({
      timestamp: new Date(activity.created_at).toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
      }),
      event: this.formatActivityEvent(activity),
      user: activity.user?.name || activity.user?.username || 'System',
    }));
  }

  /**
   * Format activity event for display
   */
  private formatActivityEvent(activity: any): string {
    const action = activity.action || 'updated';
    const entityType = activity.entity_type || 'task';
    const entityName = activity.entity_name || `${entityType} #${activity.entity_id}`;

    switch (action) {
      case 'create':
        return `Created ${entityType}: ${entityName}`;
      case 'update':
        return `Updated ${entityType}: ${entityName}`;
      case 'delete':
        return `Deleted ${entityType}: ${entityName}`;
      case 'move':
        return `Moved ${entityName} to ${activity.details?.new_status}`;
      case 'assign':
        return `Assigned ${entityName} to ${activity.details?.assignee}`;
      case 'comment':
        return `Commented on ${entityName}`;
      default:
        return `${action} ${entityName}`;
    }
  }

  /**
   * Generate sample velocity data as fallback
   */
  private generateSampleVelocity(): DashboardData['velocity'] {
    return [
      { period: 'W1', completed: 12 },
      { period: 'W2', completed: 15 },
      { period: 'W3', completed: 18 },
      { period: 'W4', completed: 14 },
      { period: 'W5', completed: 20 },
      { period: 'W6', completed: 16 },
      { period: 'W7', completed: 22 },
      { period: 'W8', completed: 19 },
    ];
  }

  /**
   * Generate sample team data as fallback
   */
  private generateSampleTeamMembers(): DashboardData['teamMembers'] {
    return [
      { name: 'Alice', taskCount: 8, load: 85 },
      { name: 'Bob', taskCount: 6, load: 70 },
      { name: 'Charlie', taskCount: 10, load: 95 },
      { name: 'Diana', taskCount: 7, load: 75 },
    ];
  }

  /**
   * Generate sample burndown data as fallback
   */
  private generateSampleBurndown(): DashboardData['burndown'] {
    return [
      { day: 'Day 1', remaining: 45, ideal: 45 },
      { day: 'Day 2', remaining: 42, ideal: 40 },
      { day: 'Day 3', remaining: 38, ideal: 35 },
      { day: 'Day 4', remaining: 35, ideal: 30 },
      { day: 'Day 5', remaining: 30, ideal: 25 },
      { day: 'Day 6', remaining: 28, ideal: 20 },
      { day: 'Day 7', remaining: 25, ideal: 15 },
      { day: 'Day 8', remaining: 20, ideal: 10 },
      { day: 'Day 9', remaining: 15, ideal: 5 },
      { day: 'Day 10', remaining: 12, ideal: 0 },
    ];
  }

  /**
   * Generate complete sample data as fallback
   */
  private generateSampleData(): DashboardData {
    return {
      tasks: {
        total: 45,
        byStatus: {
          todo: 18,
          in_progress: 12,
