import React, { useState, useEffect } from 'react';
import { Box, Text, useInput, useApp } from 'ink';
import chalk from 'chalk';
import type { Task } from './TaskList';

export interface Column {
  id: string;
  name: string;
  tasks: Task[];
  limit?: number;
}

export interface Board {
  id: string;
  name: string;
  description?: string;
  columns: Column[];
}

export interface BoardViewProps {
  board: Board;
  onTaskSelect?: (task: Task, columnId: string) => void;
  onColumnSelect?: (column: Column) => void;
  onKeyPress?: (
    key: string,
    context: { selectedTask: Task | null; selectedColumn: Column | null }
  ) => void;
  showWIPLimits?: boolean;
  maxColumnHeight?: number;
  columnWidth?: number;
}

export const BoardView: React.FC<BoardViewProps> = ({
  board,
  onTaskSelect,
  onColumnSelect,
  onKeyPress,
  showWIPLimits = true,
  maxColumnHeight = 8,
  columnWidth = 25,
}) => {
  const [selectedColumn, setSelectedColumn] = useState(0);
  const [selectedTask, setSelectedTask] = useState(0);
  const [scrollOffsets, setScrollOffsets] = useState<Map<string, number>>(new Map());
  const { exit } = useApp();

  // Ensure selection is within bounds
  useEffect(() => {
    if (selectedColumn >= board.columns.length && board.columns.length > 0) {
      setSelectedColumn(board.columns.length - 1);
    }
    if (selectedColumn < 0) {
      setSelectedColumn(0);
    }
  }, [board.columns.length, selectedColumn]);

  useEffect(() => {
    const currentColumn = board.columns[selectedColumn];
    if (currentColumn && selectedTask >= currentColumn.tasks.length) {
      setSelectedTask(Math.max(0, currentColumn.tasks.length - 1));
    }
    if (selectedTask < 0) {
      setSelectedTask(0);
    }
  }, [board.columns, selectedColumn, selectedTask]);

  // Update scroll offset for current column
  useEffect(() => {
    const currentColumn = board.columns[selectedColumn];
    if (!currentColumn) return;

    const currentOffset = scrollOffsets.get(currentColumn.id) || 0;

    if (selectedTask < currentOffset) {
      setScrollOffsets(prev => new Map(prev.set(currentColumn.id, selectedTask)));
    } else if (selectedTask >= currentOffset + maxColumnHeight) {
      setScrollOffsets(
        prev => new Map(prev.set(currentColumn.id, selectedTask - maxColumnHeight + 1))
      );
    }
  }, [selectedColumn, selectedTask, maxColumnHeight, board.columns, scrollOffsets]);

  useInput((input, key) => {
    const currentColumn = board.columns[selectedColumn];
    const currentTask = currentColumn?.tasks[selectedTask];

    if (key.leftArrow || input === 'h') {
      setSelectedColumn(prev => Math.max(0, prev - 1));
      setSelectedTask(0); // Reset task selection when changing columns
    } else if (key.rightArrow || input === 'l') {
      setSelectedColumn(prev => Math.min(board.columns.length - 1, prev + 1));
      setSelectedTask(0); // Reset task selection when changing columns
    } else if (key.upArrow || input === 'k') {
      if (currentColumn) {
        setSelectedTask(prev => Math.max(0, prev - 1));
      }
    } else if (key.downArrow || input === 'j') {
      if (currentColumn) {
        setSelectedTask(prev => Math.min(currentColumn.tasks.length - 1, prev + 1));
      }
    } else if (key.return) {
      if (currentTask && currentColumn) {
        onTaskSelect?.(currentTask, currentColumn.id);
      } else if (currentColumn) {
        onColumnSelect?.(currentColumn);
      }
    } else if (input === 'q') {
      exit();
    } else {
      onKeyPress?.(input, {
        selectedTask: currentTask || null,
        selectedColumn: currentColumn || null,
      });
    }
  });

  const formatTaskStatus = (status: Task['status']): string => {
    const statusMap = {
      todo: chalk.gray('â—‹'),
      in_progress: chalk.yellow('â—'),
      done: chalk.green('â—'),
      blocked: chalk.red('âœ•'),
    };
    return statusMap[status];
  };

  const formatPriority = (priority?: Task['priority']): string => {
    if (!priority) return '';

    const priorityColors = {
      P1: chalk.red,
      P2: chalk.yellow,
      P3: chalk.blue,
      P4: chalk.green,
      P5: chalk.gray,
    };

    return priorityColors[priority](`[${priority}]`);
  };

  const truncateText = (text: string, maxLength: number): string => {
    if (text.length <= maxLength) return text;
    return `${text.substring(0, maxLength - 3)}...`;
  };

  const getWIPStatus = (column: Column): { isOverLimit: boolean; status: string } => {
    if (!column.limit) return { isOverLimit: false, status: '' };

    const taskCount = column.tasks.length;
    const isOverLimit = taskCount > column.limit;

    return {
      isOverLimit,
      status: isOverLimit
        ? chalk.red(`${taskCount}/${column.limit}`)
        : chalk.green(`${taskCount}/${column.limit}`),
    };
  };

  const renderColumnHeader = (column: Column, columnIndex: number): React.ReactNode => {
    const isSelected = columnIndex === selectedColumn;
    const wipStatus = showWIPLimits ? getWIPStatus(column) : null;

    const headerColor = isSelected ? chalk.bgCyan.black : chalk.cyan;
    const borderChar = isSelected ? 'â•' : 'â”€';

    const title = truncateText(column.name, columnWidth - 4);
    const taskCount = wipStatus ? wipStatus.status : `${column.tasks.length}`;

    return (
      <Box flexDirection="column" width={columnWidth}>
        <Text>{borderChar.repeat(columnWidth)}</Text>
        <Text>{headerColor(` ${title} (${taskCount}) `)}</Text>
        <Text>{borderChar.repeat(columnWidth)}</Text>
      </Box>
    );
  };

  const renderTask = (
    task: Task,
    taskIndex: number,
    columnIndex: number,
    isTaskSelected: boolean
  ): React.ReactNode => {
    const statusIcon = formatTaskStatus(task.status);
    const priority = formatPriority(task.priority);

    const backgroundColor = isTaskSelected
      ? chalk.bgBlue.white
      : task.status === 'done'
        ? chalk.gray
        : chalk.white;

    const taskTitle = truncateText(task.title, columnWidth - 8);
    const taskId = truncateText(task.id, 8);

    return (
      <Box key={task.id} flexDirection="column" width={columnWidth}>
        <Text>
          {isTaskSelected ? 'â–¶' : ' '} {statusIcon} {backgroundColor(`[${taskId}]`)}
        </Text>
        <Text>{backgroundColor(`  ${taskTitle}`)}</Text>
        {priority && <Text>{backgroundColor(`  ${priority}`)}</Text>}
        {task.assignee && (
          <Text color="gray">{`  @${truncateText(task.assignee, columnWidth - 4)}`}</Text>
        )}
        <Text> </Text> {/* Spacing between tasks */}
      </Box>
    );
  };

  const renderColumn = (column: Column, columnIndex: number): React.ReactNode => {
    const isColumnSelected = columnIndex === selectedColumn;
    const scrollOffset = scrollOffsets.get(column.id) || 0;
    const visibleTasks = column.tasks.slice(scrollOffset, scrollOffset + maxColumnHeight);
    const hasMoreAbove = scrollOffset > 0;
    const hasMoreBelow = scrollOffset + maxColumnHeight < column.tasks.length;

    return (
      <Box key={column.id} flexDirection="column" width={columnWidth} marginRight={1}>
        {renderColumnHeader(column, columnIndex)}

        {/* Scroll indicator - above */}
        {hasMoreAbove && (
          <Box justifyContent="center" width={columnWidth}>
            <Text color="gray">â¬† {scrollOffset} more</Text>
          </Box>
        )}

        {/* Tasks */}
        <Box flexDirection="column" minHeight={maxColumnHeight * 3}>
          {column.tasks.length === 0 ? (
            <Box marginTop={1} justifyContent="center" width={columnWidth}>
              <Text color="gray">Empty</Text>
            </Box>
          ) : (
            visibleTasks.map((task, visibleIndex) => {
              const actualTaskIndex = scrollOffset + visibleIndex;
              const isTaskSelected = isColumnSelected && actualTaskIndex === selectedTask;
              return renderTask(task, actualTaskIndex, columnIndex, isTaskSelected);
            })
          )}
        </Box>

        {/* Scroll indicator - below */}
        {hasMoreBelow && (
          <Box justifyContent="center" width={columnWidth}>
            <Text color="gray">â¬‡ {column.tasks.length - scrollOffset - maxColumnHeight} more</Text>
          </Box>
        )}
      </Box>
    );
  };

  const renderBoardStats = (): React.ReactNode => {
    const totalTasks = board.columns.reduce((sum, col) => sum + col.tasks.length, 0);
    const completedTasks = board.columns.reduce(
      (sum, col) => sum + col.tasks.filter(t => t.status === 'done').length,
      0
    );
    const blockedTasks = board.columns.reduce(
      (sum, col) => sum + col.tasks.filter(t => t.status === 'blocked').length,
      0
    );

    const completionRate = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

    return (
      <Box flexDirection="column" marginTop={1}>
        <Text color="gray">{'â•'.repeat(80)}</Text>
        <Box>
          <Text>
            ðŸ“Š Total: {totalTasks} | âœ… Complete: {chalk.green(completedTasks)} (
            {chalk.cyan(`${completionRate}%`)}) | ðŸš« Blocked: {chalk.red(blockedTasks)}
          </Text>
        </Box>
      </Box>
    );
  };

  const renderHelp = (): React.ReactNode => (
    <Box marginTop={1}>
      <Text color="gray">
        â†/â†’ or h/l: Switch columns | â†‘/â†“ or j/k: Navigate tasks | Enter: Select | q: Quit
      </Text>
    </Box>
  );

  return (
    <Box flexDirection="column">
      {/* Board header */}
      <Box marginBottom={1}>
        <Text bold color="cyan">
          ðŸ—ï¸ {board.name}
        </Text>
      </Box>

      {board.description && (
        <Box marginBottom={1}>
          <Text color="gray">{board.description}</Text>
        </Box>
      )}

      {/* Board columns */}
      <Box flexDirection="row">
        {board.columns.map((column, index) => renderColumn(column, index))}
      </Box>

      {/* Board statistics */}
      {renderBoardStats()}

      {/* Help text */}
      {renderHelp()}
    </Box>
  );
};

export default BoardView;

import React, { useState, useEffect } from 'react';
import { Box, Text, useInput, useApp } from 'ink';
import chalk from 'chalk';

export interface Task {
  id: string;
  title: string;
  status: 'todo' | 'in_progress' | 'done' | 'blocked';
  priority?: 'P1' | 'P2' | 'P3' | 'P4' | 'P5';
  assignee?: string;
  tags?: string[];
  due_date?: string;
}

export interface TaskListProps {
  tasks: Task[];
  title?: string;
  onTaskSelect?: (task: Task) => void;
  onKeyPress?: (key: string, selectedTask: Task | null) => void;
  showSelection?: boolean;
  maxHeight?: number;
  filterStatus?: string[];
  showStats?: boolean;
}

export const TaskList: React.FC<TaskListProps> = ({
  tasks,
  title = 'Tasks',
  onTaskSelect,
  onKeyPress,
  showSelection = true,
  maxHeight = 10,
  filterStatus,
  showStats = true,
}) => {
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [scrollOffset, setScrollOffset] = useState(0);
  const { exit } = useApp();

  // Filter tasks based on status filter
  const filteredTasks = filterStatus
    ? tasks.filter(task => filterStatus.includes(task.status))
    : tasks;

  // Ensure selected index is within bounds
  useEffect(() => {
    if (selectedIndex >= filteredTasks.length && filteredTasks.length > 0) {
      setSelectedIndex(filteredTasks.length - 1);
    }
    if (selectedIndex < 0) {
      setSelectedIndex(0);
    }
  }, [filteredTasks.length, selectedIndex]);

  // Update scroll offset based on selection
  useEffect(() => {
    if (selectedIndex < scrollOffset) {
      setScrollOffset(selectedIndex);
    } else if (selectedIndex >= scrollOffset + maxHeight) {
      setScrollOffset(selectedIndex - maxHeight + 1);
    }
  }, [selectedIndex, maxHeight]);

  useInput((input, key) => {
    if (!showSelection) return;

    if (key.upArrow || input === 'k') {
      setSelectedIndex(prev => Math.max(0, prev - 1));
    } else if (key.downArrow || input === 'j') {
      setSelectedIndex(prev => Math.min(filteredTasks.length - 1, prev + 1));
    } else if (key.pageUp || (key.ctrl && input === 'u')) {
      // Page up - jump by maxHeight or to top
      setSelectedIndex(prev => Math.max(0, prev - maxHeight));
    } else if (key.pageDown || (key.ctrl && input === 'd')) {
      // Page down - jump by maxHeight or to bottom
      setSelectedIndex(prev => Math.min(filteredTasks.length - 1, prev + maxHeight));
    } else if (input === 'g') {
      // Go to top (Vim style)
      setSelectedIndex(0);
    } else if (input === 'G') {
      // Go to bottom (Vim style)
      setSelectedIndex(filteredTasks.length - 1);
    } else if (input === 'h' || key.leftArrow) {
      // Quick status filter - previous status
      const statusOrder = ['todo', 'in_progress', 'done', 'blocked'];
      const currentTask = filteredTasks[selectedIndex];
      if (currentTask) {
        const currentIndex = statusOrder.indexOf(currentTask.status);
        const prevStatus = statusOrder[Math.max(0, currentIndex - 1)];
        onKeyPress?.(`filter:${prevStatus}`, currentTask);
      }
    } else if (input === 'l' || key.rightArrow) {
      // Quick status filter - next status
      const statusOrder = ['todo', 'in_progress', 'done', 'blocked'];
      const currentTask = filteredTasks[selectedIndex];
      if (currentTask) {
        const currentIndex = statusOrder.indexOf(currentTask.status);
        const nextStatus = statusOrder[Math.min(statusOrder.length - 1, currentIndex + 1)];
        onKeyPress?.(`filter:${nextStatus}`, currentTask);
      }
    } else if (input === '/') {
      // Search mode trigger
      onKeyPress?.('search', filteredTasks[selectedIndex] || null);
    } else if (input === 'r') {
      // Refresh
      onKeyPress?.('refresh', filteredTasks[selectedIndex] || null);
    } else if (input === 'd') {
      // Delete/archive selected task
      if (filteredTasks[selectedIndex]) {
        onKeyPress?.('delete', filteredTasks[selectedIndex]);
      }
    } else if (input === 'e') {
      // Edit selected task
      if (filteredTasks[selectedIndex]) {
        onKeyPress?.('edit', filteredTasks[selectedIndex]);
      }
    } else if (input === 'n') {
      // Create new task
      onKeyPress?.('new', filteredTasks[selectedIndex] || null);
    } else if (input === '?') {
      // Show help
      onKeyPress?.('help', filteredTasks[selectedIndex] || null);
    } else if (key.return) {
      if (filteredTasks[selectedIndex]) {
        onTaskSelect?.(filteredTasks[selectedIndex]);
      }
    } else if (input === 'q') {
      exit();
    } else {
      onKeyPress?.(input, filteredTasks[selectedIndex] || null);
    }
  });

  const formatTaskStatus = (status: Task['status']): string => {
    const statusMap = {
      todo: chalk.gray('â—‹'),
      in_progress: chalk.yellow('â—'),
      done: chalk.green('â—'),
      blocked: chalk.red('âœ•'),
    };
    return statusMap[status];
  };

  const formatPriority = (priority?: Task['priority']): string => {
    if (!priority) return '';

    const priorityColors = {
      P1: chalk.red,
      P2: chalk.yellow,
      P3: chalk.blue,
      P4: chalk.green,
      P5: chalk.gray,
    };

    return priorityColors[priority](priority);
  };

  const formatTags = (tags?: string[]): string => {
    if (!tags || tags.length === 0) return '';
    return tags.map(tag => chalk.magenta(`#${tag}`)).join(' ');
  };

  const formatDueDate = (dueDate?: string): string => {
    if (!dueDate) return '';

    const date = new Date(dueDate);
    const now = new Date();
    const isOverdue = date < now;
    const dateStr = date.toLocaleDateString();

    return isOverdue ? chalk.red(`â° ${dateStr}`) : chalk.gray(`ðŸ“… ${dateStr}`);
  };

  const renderTaskItem = (task: Task, index: number, isSelected: boolean): React.ReactNode => {
    const statusIcon = formatTaskStatus(task.status);
    const priority = formatPriority(task.priority);
    const tags = formatTags(task.tags);
    const dueDate = formatDueDate(task.due_date);

    const titleColor = isSelected
      ? chalk.bgBlue.white
      : task.status === 'done'
        ? chalk.gray
        : chalk.white;

    const prefix = isSelected ? 'â–¶ ' : '  ';

    return (
      <Box key={task.id} flexDirection="column">
        <Box>
          <Text>
            {prefix}
            {statusIcon} {titleColor(`[${task.id}] ${task.title}`)}
            {priority && ` ${priority}`}
          </Text>
        </Box>
        {(task.assignee || tags || dueDate) && (
          <Box marginLeft={4}>
            <Text color="gray">
              {task.assignee && chalk.cyan(`@${task.assignee}`)}
              {task.assignee && (tags || dueDate) && ' | '}
              {tags}
              {tags && dueDate && ' | '}
              {dueDate}
            </Text>
          </Box>
        )}
      </Box>
    );
  };

  const renderStats = (): React.ReactNode => {
    if (!showStats) return null;

    const stats = {
      total: filteredTasks.length,
      todo: filteredTasks.filter(t => t.status === 'todo').length,
      inProgress: filteredTasks.filter(t => t.status === 'in_progress').length,
      done: filteredTasks.filter(t => t.status === 'done').length,
      blocked: filteredTasks.filter(t => t.status === 'blocked').length,
    };

    const completionRate = stats.total > 0 ? Math.round((stats.done / stats.total) * 100) : 0;

    return (
      <Box flexDirection="column" marginTop={1}>
        <Text color="gray">{'â”€'.repeat(50)}</Text>
        <Box>
          <Text color="gray">
            Total: {stats.total} | Todo: {chalk.gray(stats.todo)} | In Progress:{' '}
            {chalk.yellow(stats.inProgress)} | Done: {chalk.green(stats.done)} | Blocked:{' '}
            {chalk.red(stats.blocked)} | Complete: {chalk.cyan(`${completionRate}%`)}
          </Text>
        </Box>
      </Box>
    );
  };

  const renderHelpText = (): React.ReactNode => {
    if (!showSelection) return null;

    return (
      <Box flexDirection="column" marginTop={1}>
        <Text color="gray">
          Navigation: â†‘/â†“ j/k | Page: PgUp/PgDn Ctrl+u/d | Jump: g(top) G(bottom)
        </Text>
        <Text color="gray">
          Actions: Enter(select) n(new) e(edit) d(delete) r(refresh) /(search) ?(help) q(quit)
        </Text>
        <Text color="gray">
          Filters: â†/â†’ h/l (cycle status filters)
        </Text>
      </Box>
    );
  };

  // Calculate visible tasks
  const visibleTasks = filteredTasks.slice(scrollOffset, scrollOffset + maxHeight);
  const hasMoreAbove = scrollOffset > 0;
  const hasMoreBelow = scrollOffset + maxHeight < filteredTasks.length;

  return (
    <Box flexDirection="column">
      {/* Header */}
      <Box marginBottom={1}>
        <Text bold color="cyan">
          ðŸ“‹ {title} ({filteredTasks.length})
        </Text>
      </Box>

      {/* Scroll indicator - above */}
      {hasMoreAbove && (
        <Box justifyContent="center">
          <Text color="gray">â¬† {scrollOffset} more above</Text>
        </Box>
      )}

      {/* Task list */}
      <Box flexDirection="column">
        {filteredTasks.length === 0 ? (
          <Text color="gray">No tasks found</Text>
        ) : (
          visibleTasks.map((task, visibleIndex) => {
            const actualIndex = scrollOffset + visibleIndex;
            const isSelected = showSelection && actualIndex === selectedIndex;
            return renderTaskItem(task, actualIndex, isSelected);
          })
        )}
      </Box>

      {/* Scroll indicator - below */}
      {hasMoreBelow && (
        <Box justifyContent="center">
          <Text color="gray">â¬‡ {filteredTasks.length - scrollOffset - maxHeight} more below</Text>
        </Box>
      )}

      {/* Stats */}
      {renderStats()}

      {/* Help text */}
      {renderHelpText()}
    </Box>
  );
};

export default TaskList;

