// Theme registry
export const themes = {
  default: defaultTheme,
  dark: darkTheme,
  light: lightTheme,
  'high-contrast': highContrastTheme,
};

export type ThemeName = keyof typeof themes;

// Theme utilities
export class ThemeManager {
  private currentTheme: Theme = defaultTheme;

  setTheme(name: ThemeName): void {
    this.currentTheme = themes[name];
  }

  getTheme(): Theme {
    return this.currentTheme;
  }

  formatStatus(status: string, text?: string): string {
    const statusConfig = this.currentTheme.status[status as keyof typeof this.currentTheme.status];
    if (!statusConfig) return text || status;

    const { icon } = statusConfig;
    const coloredIcon = statusConfig.color(icon);

    return text ? `${coloredIcon} ${statusConfig.color(text)}` : coloredIcon;
  }

  formatPriority(priority: string, text?: string): string {
    const colorFn = this.currentTheme.priority[priority as keyof typeof this.currentTheme.priority];
    if (!colorFn) return text || priority;

    return colorFn(text || priority);
  }

  formatBorder(type: 'light' | 'heavy' | 'double', length: number): string {
    const char = this.currentTheme.borders[type];
    return char.repeat(length);
  }

  getIcon(name: keyof Theme['icons']): string {
    return this.currentTheme.icons[name];
  }

  applyColor(colorName: keyof Theme['colors'], text: string): string {
    return this.currentTheme.colors[colorName](text);
  }
}

// Export singleton instance
export const themeManager = new ThemeManager();

/**
 * Unit tests for date/time formatting functions
 */

import {
  formatRelativeTimeNatural,
  formatDueDate,
  formatSmartDateTime,
  formatTimeRange,
  formatTimestamp,
  formatISODate,
  formatDateShort,
  formatWorkingHours,
} from '../formatter';

describe('Date/Time Formatting Functions', () => {
  const mockDate = new Date('2024-01-15T10:30:00Z');

  beforeAll(() => {
    // Mock the current date for consistent tests
    jest.useFakeTimers();
    jest.setSystemTime(mockDate);
  });

  afterAll(() => {
    jest.useRealTimers();
  });

  describe('formatRelativeTimeNatural', () => {
    it('should format relative time using date-fns', () => {
      const oneHourAgo = new Date(mockDate.getTime() - 60 * 60 * 1000);
      const result = formatRelativeTimeNatural(oneHourAgo);
      expect(result).toContain('ago');
    });

    it('should handle string dates', () => {
      const result = formatRelativeTimeNatural('2024-01-15T09:30:00Z');
      expect(result).toContain('ago');
    });
  });

  describe('formatDueDate', () => {
    it('should format today dates with color', () => {
      const result = formatDueDate(mockDate);
      expect(result).toContain('Due today');
    });

    it('should format tomorrow dates', () => {
      const tomorrow = new Date(mockDate.getTime() + 24 * 60 * 60 * 1000);
      const result = formatDueDate(tomorrow);
      expect(result).toContain('Due tomorrow');
    });

    it('should mark overdue dates', () => {
      const yesterday = new Date(mockDate.getTime() - 24 * 60 * 60 * 1000);
      const result = formatDueDate(yesterday);
      expect(result).toContain('Overdue');
    });
  });

  describe('formatSmartDateTime', () => {
    it('should show only time for today', () => {
      const result = formatSmartDateTime(mockDate);
      expect(result).toMatch(/^\d{2}:\d{2}$/);
    });

    it('should show "Yesterday" for yesterday dates', () => {
      const yesterday = new Date(mockDate.getTime() - 24 * 60 * 60 * 1000);
      const result = formatSmartDateTime(yesterday);
      expect(result).toContain('Yesterday');
    });

    it('should show "Tomorrow" for tomorrow dates', () => {
      const tomorrow = new Date(mockDate.getTime() + 24 * 60 * 60 * 1000);
      const result = formatSmartDateTime(tomorrow);
      expect(result).toContain('Tomorrow');
    });
  });

  describe('formatTimeRange', () => {
    it('should format same day ranges', () => {
      const start = new Date(mockDate.getTime());
      const end = new Date(mockDate.getTime() + 2 * 60 * 60 * 1000); // 2 hours later

      const result = formatTimeRange(start, end);
      expect(result).toContain('Today');
      expect(result).toContain('-');
    });

    it('should format cross-day ranges', () => {
      const start = mockDate;
      const end = new Date(mockDate.getTime() + 25 * 60 * 60 * 1000); // Next day

      const result = formatTimeRange(start, end);
      expect(result).toContain('-');
    });
  });

  describe('formatTimestamp', () => {
    it('should format with milliseconds', () => {
      const result = formatTimestamp(mockDate);
      expect(result).toMatch(/\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}\.\d{3}/);
    });

    it('should use current time when no date provided', () => {
      const result = formatTimestamp();
      expect(result).toMatch(/\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}\.\d{3}/);
    });
  });

  describe('formatISODate', () => {
    it('should format as ISO string', () => {
      const result = formatISODate(mockDate);
      expect(result).toBe(mockDate.toISOString());
    });

    it('should handle string dates', () => {
      const dateStr = '2024-01-15T10:30:00Z';
      const result = formatISODate(dateStr);
      expect(result).toBe(new Date(dateStr).toISOString());
    });
  });

  describe('formatDateShort', () => {
    it('should return "Today" for today', () => {
      const result = formatDateShort(mockDate);
      expect(result).toBe('Today');
    });

    it('should return "Yesterday" for yesterday', () => {
      const yesterday = new Date(mockDate.getTime() - 24 * 60 * 60 * 1000);
      const result = formatDateShort(yesterday);
      expect(result).toBe('Yesterday');
    });

    it('should return "Tomorrow" for tomorrow', () => {
      const tomorrow = new Date(mockDate.getTime() + 24 * 60 * 60 * 1000);
      const result = formatDateShort(tomorrow);
      expect(result).toBe('Tomorrow');
    });

    it('should format other dates appropriately', () => {
      const farDate = new Date('2025-05-20T10:30:00Z');
      const result = formatDateShort(farDate);
      expect(result).toContain('May');
    });
  });

  describe('formatWorkingHours', () => {
    it('should format minutes for less than 1 hour', () => {
      const result = formatWorkingHours(0.5);
      expect(result).toBe('30min');
    });

    it('should format hours for less than 8 hours', () => {
      const result = formatWorkingHours(3.5);
      expect(result).toBe('3.5h');
    });

    it('should format full days', () => {
      const result = formatWorkingHours(16);
      expect(result).toBe('2d');
    });

    it('should format days and hours', () => {
      const result = formatWorkingHours(12.5);
      expect(result).toBe('1d 4.5h');
    });
  });
});

/**
 * Unit tests for SpinnerManager
 * Tests comprehensive error handling, validation, and lifecycle management
 */

import ora from 'ora';
import { SpinnerManager, SpinnerError } from '../spinner';

// Mock ora
jest.mock('ora');

describe('SpinnerManager', () => {
  let spinnerManager: SpinnerManager;
  let mockSpinner: any;
  let consoleSpy: jest.SpyInstance;

  beforeEach(() => {
    mockSpinner = {
      start: jest.fn().mockReturnThis(),
      stop: jest.fn().mockReturnThis(),
      succeed: jest.fn().mockReturnThis(),
      fail: jest.fn().mockReturnThis(),
      warn: jest.fn().mockReturnThis(),
      info: jest.fn().mockReturnThis(),
      clear: jest.fn().mockReturnThis(),
      render: jest.fn().mockReturnThis(),
      text: 'test text',
      color: 'cyan',
      isSpinning: false,
    };

    (ora as jest.Mock).mockReturnValue(mockSpinner);
    consoleSpy = jest.spyOn(console, 'warn').mockImplementation();
    spinnerManager = new SpinnerManager();
  });

  afterEach(() => {
    jest.clearAllMocks();
    consoleSpy.mockRestore();
    if (spinnerManager && !spinnerManager['destroyed']) {
      spinnerManager.destroy();
    }
  });

  describe('SpinnerError', () => {
    it('should create a SpinnerError with message and code', () => {
      const error = new SpinnerError('Test message', 'TEST_CODE');
      expect(error.message).toBe('Test message');
      expect(error.code).toBe('TEST_CODE');
      expect(error.name).toBe('SpinnerError');
    });
  });

  describe('validation', () => {
    it('should throw error for non-string text', () => {
      expect(() => spinnerManager.start(123 as any)).toThrow(SpinnerError);
      expect(() => spinnerManager.start(123 as any)).toThrow('Text must be a string');
    });

    it('should throw error for empty text', () => {
      expect(() => spinnerManager.start('')).toThrow(SpinnerError);
      expect(() => spinnerManager.start('')).toThrow('Text cannot be empty');
    });

    it('should truncate text that is too long', () => {
      const longText = 'a'.repeat(250);
      spinnerManager.start(longText);

      expect(ora).toHaveBeenCalledWith({
        text: 'a'.repeat(197) + '...',
        color: 'cyan',
        spinner: 'dots',
        hideCursor: true,
      });
    });

    it('should trim whitespace from text', () => {
      spinnerManager.start('  test text  ');

      expect(ora).toHaveBeenCalledWith({
        text: 'test text',
        color: 'cyan',
        spinner: 'dots',
        hideCursor: true,
      });
    });

    it('should throw error when accessing destroyed spinner', () => {
      spinnerManager.destroy();
      expect(() => spinnerManager.start('test')).toThrow(SpinnerError);
      expect(() => spinnerManager.start('test')).toThrow('SpinnerManager has been destroyed');
    });
  });

  describe('start', () => {
    it('should create and start a spinner with valid text', () => {
      spinnerManager.start('Loading...');

      expect(ora).toHaveBeenCalledWith({
        text: 'Loading...',
        color: 'cyan',
        spinner: 'dots',
        hideCursor: true,
      });
      expect(mockSpinner.start).toHaveBeenCalled();
      expect(spinnerManager.isActive()).toBe(true);
    });

    it('should stop existing spinner before starting new one', () => {
      spinnerManager.start('First spinner');
      spinnerManager.start('Second spinner');

      expect(mockSpinner.stop).toHaveBeenCalled();
      expect(ora).toHaveBeenCalledTimes(2);
    });

    it('should handle ora creation failure', () => {
      (ora as jest.Mock).mockReturnValue(null);

      expect(() => spinnerManager.start('test')).toThrow(SpinnerError);
      expect(() => spinnerManager.start('test')).toThrow('Failed to create spinner instance');
    });

    it('should handle ora throwing error', () => {
      (ora as jest.Mock).mockImplementation(() => {
        throw new Error('Ora error');
      });

      expect(() => spinnerManager.start('test')).toThrow(SpinnerError);
      expect(() => spinnerManager.start('test')).toThrow('Failed to create spinner instance');
    });
  });

  describe('update', () => {
    it('should update spinner text when active', () => {
      spinnerManager.start('Initial text');
      spinnerManager.update('Updated text');

      expect(mockSpinner.text).toBe('Updated text');
    });

    it('should throw error if no spinner exists', () => {
      expect(() => spinnerManager.update('test')).toThrow(SpinnerError);
      expect(() => spinnerManager.update('test')).toThrow('No active spinner to update');
    });

    it('should throw error if spinner not running', () => {
      spinnerManager.start('test');
      spinnerManager['isSpinning'] = false; // Simulate stopped state

      expect(() => spinnerManager.update('new text')).toThrow(SpinnerError);
      expect(() => spinnerManager.update('new text')).toThrow('Spinner is not currently running');
    });

    it('should handle update errors gracefully', () => {
      spinnerManager.start('test');

      // The current implementation doesn't throw on text assignment errors
      // because it uses safeOperation wrapper that logs and continues
      expect(() => spinnerManager.update('new text')).not.toThrow();
    });
  });

  describe('stop methods', () => {
    beforeEach(() => {
      spinnerManager.start('test');
    });

    it('should call succeed with custom text', () => {
      spinnerManager.succeed('Success message');
      expect(mockSpinner.succeed).toHaveBeenCalledWith('Success message');
      expect(spinnerManager.isActive()).toBe(false);
    });

    it('should call succeed with existing text if no text provided', () => {
      spinnerManager.succeed();
      expect(mockSpinner.succeed).toHaveBeenCalledWith('test text');
    });

    it('should call fail with custom text', () => {
      spinnerManager.fail('Error message');
      expect(mockSpinner.fail).toHaveBeenCalledWith('Error message');
      expect(spinnerManager.isActive()).toBe(false);
    });

    it('should call warn with custom text', () => {
      spinnerManager.warn('Warning message');
      expect(mockSpinner.warn).toHaveBeenCalledWith('Warning message');
      expect(spinnerManager.isActive()).toBe(false);
    });

    it('should call info with custom text', () => {
      spinnerManager.info('Info message');
      expect(mockSpinner.info).toHaveBeenCalledWith('Info message');
      expect(spinnerManager.isActive()).toBe(false);
    });

    it('should call stop without text', () => {
      spinnerManager.stop();
      expect(mockSpinner.stop).toHaveBeenCalled();
      expect(spinnerManager.isActive()).toBe(false);
    });

    it('should handle stop errors gracefully', () => {
      mockSpinner.succeed.mockImplementation(() => {
        throw new Error('Stop error');
      });

      spinnerManager.succeed('test');
      expect(consoleSpy).toHaveBeenCalledWith(expect.stringContaining('Spinner operation failed'));
      expect(spinnerManager.isActive()).toBe(false);
    });

    it('should silently ignore stop when no spinner exists', () => {
      const newManager = new SpinnerManager();
      expect(() => newManager.stop()).not.toThrow();
    });

    it('should warn when stopping destroyed spinner', () => {
      spinnerManager.destroy();
      spinnerManager['stopSpinner']('stop');
      expect(consoleSpy).toHaveBeenCalledWith('Attempted to stop destroyed spinner');
    });
  });

  describe('withSpinner', () => {
    it('should execute promise with spinner for success', async () => {
      const testPromise = Promise.resolve('success result');

      const result = await spinnerManager.withSpinner('Processing...', testPromise);

      expect(ora).toHaveBeenCalledWith({
        text: 'Processing...',
        color: 'cyan',
        spinner: 'dots',
        hideCursor: true,
      });
      expect(mockSpinner.start).toHaveBeenCalled();
      expect(mockSpinner.succeed).toHaveBeenCalled();
      expect(result).toBe('success result');
    });

    it('should handle promise rejection', async () => {
      const testError = new Error('Test error');
      const testPromise = Promise.reject(testError);

      await expect(spinnerManager.withSpinner('Processing...', testPromise)).rejects.toThrow('Test error');
      expect(mockSpinner.fail).toHaveBeenCalledWith(expect.stringContaining('Failed: Processing...'));
    });

    it('should use custom success and fail text', async () => {
      const testPromise = Promise.resolve('result');

      await spinnerManager.withSpinner('Processing...', testPromise, {
        successText: 'Custom success',
        failText: 'Custom fail',
      });

      expect(mockSpinner.succeed).toHaveBeenCalledWith('Custom success');
    });

    it('should handle timeout', async () => {
      const slowPromise = new Promise((resolve) => setTimeout(resolve, 1000));

      await expect(
        spinnerManager.withSpinner('Processing...', slowPromise, { timeout: 100 })
      ).rejects.toThrow('Operation timed out after 100ms');

      expect(mockSpinner.fail).toHaveBeenCalled();
