    // Auto-remove after 3 seconds
    setTimeout(() => {
      this.screen.remove(notificationBox);
      this.screen.render();
    }, 3000);
  }

  /**
   * Clean up resources
   */
  destroy(): void {
    this.stopAutoRefresh();
    this.clearWidgets();
    this.screen.destroy();
  }
}

import chalk from 'chalk';
import { format, isToday, isTomorrow, isYesterday, isThisWeek, isThisYear, formatDistanceToNow } from 'date-fns';

/**
 * Utility functions for formatting CLI output
 */

/**
 * Format file size in human-readable format
 */
export function formatFileSize(bytes: number): string {
  const units = ['B', 'KB', 'MB', 'GB', 'TB'];
  let size = bytes;
  let unitIndex = 0;

  while (size >= 1024 && unitIndex < units.length - 1) {
    size /= 1024;
    unitIndex++;
  }

  return `${size.toFixed(1)} ${units[unitIndex]}`;
}

/**
 * Format duration in human-readable format
 */
export function formatDuration(ms: number): string {
  if (ms < 1000) return `${ms}ms`;

  const seconds = Math.floor(ms / 1000);
  const minutes = Math.floor(seconds / 60);
  const hours = Math.floor(minutes / 60);
  const days = Math.floor(hours / 24);

  if (days > 0) return `${days}d ${hours % 24}h`;
  if (hours > 0) return `${hours}h ${minutes % 60}m`;
  if (minutes > 0) return `${minutes}m ${seconds % 60}s`;
  return `${seconds}s`;
}

/**
 * Format date/time in various formats
 */
export function formatDateTime(date: Date | string, formatStr = 'yyyy-MM-dd HH:mm:ss'): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date;
  return format(dateObj, formatStr);
}

/**
 * Format relative time (e.g., "2 hours ago")
 */
export function formatRelativeTime(date: Date | string): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date;
  const now = new Date();
  const diffMs = now.getTime() - dateObj.getTime();

  const minutes = Math.floor(diffMs / 60000);
  const hours = Math.floor(minutes / 60);
  const days = Math.floor(hours / 24);

  if (days > 0) return `${days} day${days > 1 ? 's' : ''} ago`;
  if (hours > 0) return `${hours} hour${hours > 1 ? 's' : ''} ago`;
  if (minutes > 0) return `${minutes} minute${minutes > 1 ? 's' : ''} ago`;
  return 'just now';
}

/**
 * Format relative time using date-fns (more natural language)
 */
export function formatRelativeTimeNatural(date: Date | string): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date;
  return formatDistanceToNow(dateObj, { addSuffix: true });
}

/**
 * Format date for due dates with smart context
 */
export function formatDueDate(date: Date | string): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date;

  if (isToday(dateObj)) {
    return chalk.yellow('Due today');
  }

  if (isTomorrow(dateObj)) {
    return chalk.yellow('Due tomorrow');
  }

  if (isYesterday(dateObj)) {
    return chalk.red('Overdue (yesterday)');
  }

  const now = new Date();
  if (dateObj < now) {
    return chalk.red(`Overdue (${formatDistanceToNow(dateObj)} ago)`);
  }

  if (isThisWeek(dateObj)) {
    return `Due ${format(dateObj, 'EEEE')}`;
  }

  if (isThisYear(dateObj)) {
    return `Due ${format(dateObj, 'MMM d')}`;
  }

  return `Due ${format(dateObj, 'MMM d, yyyy')}`;
}

/**
 * Format date with time if it's today, date only otherwise
 */
export function formatSmartDateTime(date: Date | string): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date;

  if (isToday(dateObj)) {
    return format(dateObj, 'HH:mm');
  }

  if (isYesterday(dateObj)) {
    return `Yesterday ${format(dateObj, 'HH:mm')}`;
  }

  if (isTomorrow(dateObj)) {
    return `Tomorrow ${format(dateObj, 'HH:mm')}`;
  }

  if (isThisWeek(dateObj)) {
    return format(dateObj, 'EEE HH:mm');
  }

  if (isThisYear(dateObj)) {
    return format(dateObj, 'MMM d HH:mm');
  }

  return format(dateObj, 'MMM d, yyyy HH:mm');
}

/**
 * Format time range
 */
export function formatTimeRange(startDate: Date | string, endDate: Date | string): string {
  const start = typeof startDate === 'string' ? new Date(startDate) : startDate;
  const end = typeof endDate === 'string' ? new Date(endDate) : endDate;

  // Same day
  if (isToday(start) && isToday(end)) {
    return `Today ${format(start, 'HH:mm')} - ${format(end, 'HH:mm')}`;
  }

  // Same date
  if (format(start, 'yyyy-MM-dd') === format(end, 'yyyy-MM-dd')) {
    return `${format(start, 'MMM d')} ${format(start, 'HH:mm')} - ${format(end, 'HH:mm')}`;
  }

  // Different dates
  return `${formatSmartDateTime(start)} - ${formatSmartDateTime(end)}`;
}

/**
 * Format timestamp for logs and debugging
 */
export function formatTimestamp(date?: Date | string): string {
  const dateObj = date ? (typeof date === 'string' ? new Date(date) : date) : new Date();
  return format(dateObj, 'yyyy-MM-dd HH:mm:ss.SSS');
}

/**
 * Format ISO date for API calls
 */
export function formatISODate(date: Date | string): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date;
  return dateObj.toISOString();
}

/**
 * Format date for human display (short format)
 */
export function formatDateShort(date: Date | string): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date;

  if (isToday(dateObj)) return 'Today';
  if (isYesterday(dateObj)) return 'Yesterday';
  if (isTomorrow(dateObj)) return 'Tomorrow';
  if (isThisWeek(dateObj)) return format(dateObj, 'EEEE');
  if (isThisYear(dateObj)) return format(dateObj, 'MMM d');

  return format(dateObj, 'MMM d, yyyy');
}

/**
 * Format working hours (business time)
 */
export function formatWorkingHours(hours: number): string {
  if (hours < 1) {
    const minutes = Math.round(hours * 60);
    return `${minutes}min`;
  }

  if (hours < 8) {
    return `${hours.toFixed(1)}h`;
  }

  const days = Math.floor(hours / 8);
  const remainingHours = hours % 8;

  if (remainingHours === 0) {
    return `${days}d`;
  }

  return `${days}d ${remainingHours.toFixed(1)}h`;
}

/**
 * Format task priority with color
 */
export function formatPriority(priority: string): string {
  const colors = {
    P1: chalk.red,
    P2: chalk.yellow,
    P3: chalk.blue,
    P4: chalk.green,
    P5: chalk.gray,
    CRITICAL: chalk.bgRed.white,
    HIGH: chalk.red,
    MEDIUM: chalk.yellow,
    LOW: chalk.green,
  };

  const colorFn = colors[priority as keyof typeof colors] || chalk.white;
  return colorFn(priority);
}

/**
 * Format task status with color and icon
 */
export function formatStatus(status: string): string {
  const statusMap = {
    todo: { icon: 'â—‹', color: chalk.gray },
    in_progress: { icon: 'â—', color: chalk.yellow },
    'in-progress': { icon: 'â—', color: chalk.yellow },
    done: { icon: 'â—', color: chalk.green },
    completed: { icon: 'âœ“', color: chalk.green },
    blocked: { icon: 'âœ•', color: chalk.red },
    cancelled: { icon: 'âŠ˜', color: chalk.gray },
  };

  const config = statusMap[status.toLowerCase() as keyof typeof statusMap] || {
    icon: '?',
    color: chalk.white,
  };

  return `${config.color(config.icon)} ${config.color(status)}`;
}

/**
 * Format percentage with color coding
 */
export function formatPercentage(value: number, total: number): string {
  const percentage = total > 0 ? (value / total) * 100 : 0;
  const rounded = Math.round(percentage);

  let color = chalk.green;
  if (rounded < 25) color = chalk.red;
  else if (rounded < 50) color = chalk.yellow;
  else if (rounded < 75) color = chalk.blue;

  return color(`${rounded}%`);
}

/**
 * Format board column with task count
 */
export function formatBoardColumn(name: string, taskCount: number, maxWidth = 20): string {
  const truncatedName = name.length > maxWidth ? `${name.substring(0, maxWidth - 3)}...` : name;

  return `${chalk.bold(truncatedName)} (${taskCount})`;
}

/**
 * Format task list item
 */
export function formatTaskListItem(task: {
  id: string;
  title: string;
  priority?: string;
  status?: string;
  assignee?: string;
}): string {
  const parts = [chalk.dim(`[${task.id}]`), task.title];

  if (task.priority) {
    parts.push(formatPriority(task.priority));
  }

  if (task.status) {
    parts.push(formatStatus(task.status));
  }

  if (task.assignee) {
    parts.push(chalk.cyan(`@${task.assignee}`));
  }

  return parts.join(' ');
}

/**
 * Format error message with stack trace
 */
export function formatError(error: Error | string, showStack = false): string {
  if (typeof error === 'string') {
    return chalk.red(`âœ– ${error}`);
  }

  const message = chalk.red(`âœ– ${error.message}`);

  if (showStack && error.stack) {
    const stack = error.stack
      .split('\n')
      .slice(1)
      .map(line => chalk.gray(`  ${line.trim()}`))
      .join('\n');

    return `${message}\n${stack}`;
  }

  return message;
}

/**
 * Format success message
 */
export function formatSuccess(message: string): string {
  return chalk.green(`âœ“ ${message}`);
}

/**
 * Format warning message
 */
export function formatWarning(message: string): string {
  return chalk.yellow(`âš  ${message}`);
}

/**
 * Format info message
 */
export function formatInfo(message: string): string {
  return chalk.blue(`â„¹ ${message}`);
}

/**
 * Create a progress bar string
 */
export function formatProgressBar(current: number, total: number, width = 20): string {
  const percentage = total > 0 ? current / total : 0;
  const filled = Math.round(width * percentage);
  const empty = width - filled;

  const bar = 'â–ˆ'.repeat(filled) + 'â–‘'.repeat(empty);
  const percentStr = formatPercentage(current, total);

  return `[${bar}] ${percentStr} (${current}/${total})`;
}

/**
 * Format key-value pairs for display
 */
export function formatKeyValue(key: string, value: any, keyWidth = 15): string {
  const paddedKey = key.padEnd(keyWidth);
  return `${chalk.gray(paddedKey)} ${value}`;
}

/**
 * Format a divider line
 */
export function formatDivider(char = 'â”€', width = 60): string {
  return chalk.gray(char.repeat(width));
}

/**
 * Format a header with dividers
 */
export function formatHeader(title: string, width = 60): string {
  const padding = Math.max(0, width - title.length - 2);
  const leftPad = Math.floor(padding / 2);
  const rightPad = padding - leftPad;

  return [
    formatDivider('â•', width),
    `${' '.repeat(leftPad)}${chalk.bold(title)}${' '.repeat(rightPad)}`,
    formatDivider('â•', width),
  ].join('\n');
}

/**
 * Truncate text with ellipsis
 */
export function truncate(text: string, maxLength: number): string {
  if (text.length <= maxLength) return text;
  return `${text.substring(0, maxLength - 3)}...`;
}

/**
 * Wrap text to specified width
 */
export function wrapText(text: string, width: number): string[] {
  const words = text.split(' ');
  const lines: string[] = [];
  let currentLine = '';

  for (const word of words) {
    if (`${currentLine} ${word}`.trim().length <= width) {
      currentLine = `${currentLine} ${word}`.trim();
    } else {
      if (currentLine) lines.push(currentLine);
      currentLine = word;
    }
  }

  if (currentLine) lines.push(currentLine);
  return lines;
}

import DOMPurify from 'dompurify';
import { JSDOM } from 'jsdom';

/**
 * Comprehensive input sanitization utility for CLI prompts
 * Addresses TASK-119: Implement input sanitization for all prompts
 */

export interface SanitizationOptions {
  allowHtml?: boolean;
  maxLength?: number;
  allowedCharacters?: RegExp;
  stripControlChars?: boolean;
  normalizeWhitespace?: boolean;
  preventInjection?: boolean;
  escapeSpecialChars?: boolean;
}

export interface SanitizationResult {
  sanitized: string;
  modified: boolean;
  warnings: string[];
  originalLength: number;
  sanitizedLength: number;
}

/**
 * Main input sanitizer class with security-focused features
 */
export class InputSanitizer {
  private static instance: InputSanitizer;
  private purify: any;

  constructor() {
    // Initialize DOMPurify with JSDOM for server-side sanitization
    const window = new JSDOM('').window;
    this.purify = DOMPurify(window as any);

    // Configure DOMPurify for strict sanitization
    this.purify.addHook('beforeSanitizeElements', (node) => {
      // Remove all script tags and event handlers
      if (node.nodeName === 'SCRIPT' || node.nodeName === 'IFRAME') {
        node.remove();
      }
    });
  }

  static getInstance(): InputSanitizer {
    if (!InputSanitizer.instance) {
      InputSanitizer.instance = new InputSanitizer();
    }
    return InputSanitizer.instance;
  }

  /**
   * Sanitize text input with comprehensive security checks
   */
  sanitizeText(input: string, options: SanitizationOptions = {}): SanitizationResult {
    const {
      allowHtml = false,
      maxLength = 1000,
      stripControlChars = true,
      normalizeWhitespace = true,
      preventInjection = true,
      escapeSpecialChars = true,
    } = options;

    let sanitized = input;
    const warnings: string[] = [];
    const originalLength = input.length;

    // 1. Basic safety checks
    if (typeof sanitized !== 'string') {
      sanitized = String(sanitized);
      warnings.push('Input was converted to string');
    }

    // 2. Length validation
    if (sanitized.length > maxLength) {
      sanitized = sanitized.substring(0, maxLength);
      warnings.push(`Input truncated to ${maxLength} characters`);
    }

    // 3. Strip control characters (except common whitespace)
    if (stripControlChars) {
      const controlCharRegex = /[\x00-\x08\x0B\x0C\x0E-\x1F\x7F-\x9F]/g;
      const beforeStrip = sanitized;
      sanitized = sanitized.replace(controlCharRegex, '');
      if (beforeStrip !== sanitized) {
        warnings.push('Control characters removed');
      }
    }

    // 4. Command injection prevention
    if (preventInjection) {
      sanitized = this.preventCommandInjection(sanitized, warnings);
    }

    // 5. HTML sanitization
    if (!allowHtml) {
      sanitized = this.purify.sanitize(sanitized, { ALLOWED_TAGS: [] });
    } else {
      sanitized = this.purify.sanitize(sanitized);
    }

    // 6. Escape special characters for CLI safety
    if (escapeSpecialChars) {
      sanitized = this.escapeCliSpecialChars(sanitized, warnings);
    }

    // 7. Normalize whitespace
    if (normalizeWhitespace) {
      const beforeNormalize = sanitized;
      sanitized = sanitized
        .replace(/\s+/g, ' ') // Multiple spaces to single
        .replace(/^\s+|\s+$/g, '') // Trim
        .replace(/\t/g, '    '); // Tabs to spaces
      if (beforeNormalize !== sanitized) {
        warnings.push('Whitespace normalized');
      }
    }

    // 8. Additional character restrictions
    if (options.allowedCharacters) {
      const beforeFilter = sanitized;
      sanitized = sanitized.replace(new RegExp(`[^${options.allowedCharacters.source}]`, 'g'), '');
      if (beforeFilter !== sanitized) {
        warnings.push('Invalid characters removed');
      }
    }

    return {
      sanitized,
      modified: sanitized !== input,
      warnings,
      originalLength,
      sanitizedLength: sanitized.length,
    };
  }

  /**
   * Prevent command injection attacks
   */
  private preventCommandInjection(input: string, warnings: string[]): string {
    const dangerousPatterns = [
      // Shell injection patterns
      /[;&|`$(){}[\]\\]/g,
      // Path traversal
      /\.\.[\/\\]/g,
      // Null bytes
      /\x00/g,
      // Process substitution
      /<\(/g,
      />\(/g,
      // Command substitution
      /\$\(/g,
      // Backticks
      /`/g,
    ];

    let sanitized = input;
    let hasInjectionAttempt = false;

    dangerousPatterns.forEach(pattern => {
      if (pattern.test(sanitized)) {
        sanitized = sanitized.replace(pattern, '');
        hasInjectionAttempt = true;
      }
    });

    if (hasInjectionAttempt) {
      warnings.push('Potential command injection patterns removed');
    }

    return sanitized;
  }

  /**
   * Escape CLI special characters
   */
  private escapeCliSpecialChars(input: string, warnings: string[]): string {
    const beforeEscape = input;

    // Escape common shell metacharacters that are safe to keep but should be escaped
    const escapeMap: Record<string, string> = {
      '"': '\\"',
      "'": "\\'",
      '\\': '\\\\',
    };

    let escaped = input;
    Object.entries(escapeMap).forEach(([char, replacement]) => {
      escaped = escaped.replace(new RegExp(`\\${char}`, 'g'), replacement);
    });

    if (beforeEscape !== escaped) {
      warnings.push('Special characters escaped');
    }

    return escaped;
  }

  /**
   * Sanitize file path inputs
   */
  sanitizeFilePath(input: string): SanitizationResult {
    return this.sanitizeText(input, {
      allowHtml: false,
      maxLength: 255,
      stripControlChars: true,
      normalizeWhitespace: true,
      preventInjection: true,
      escapeSpecialChars: false, // File paths may need some special chars
      allowedCharacters: /[\w\-.\s\/\\:]/,
    });
  }

  /**
   * Sanitize URL inputs
   */
  sanitizeUrl(input: string): SanitizationResult {
    const result = this.sanitizeText(input, {
      allowHtml: false,
      maxLength: 2048,
      stripControlChars: true,
      normalizeWhitespace: true,
      preventInjection: true,
      escapeSpecialChars: false,
      allowedCharacters: /[\w\-._~:/?#[\]@!$&'()*+,;=%]/,
    });

    // Additional URL validation
    try {
      new URL(result.sanitized);
    } catch {
      result.warnings.push('Invalid URL format detected');
      result.sanitized = '';
    }

    return result;
  }

  /**
   * Sanitize email inputs
   */
  sanitizeEmail(input: string): SanitizationResult {
    return this.sanitizeText(input, {
      allowHtml: false,
      maxLength: 254, // RFC 5321 limit
      stripControlChars: true,
      normalizeWhitespace: true,
      preventInjection: true,
      escapeSpecialChars: false,
      allowedCharacters: /[\w\-_.@+]/,
    });
  }

  /**
   * Sanitize task title with specific rules
   */
  sanitizeTaskTitle(input: string): SanitizationResult {
    return this.sanitizeText(input, {
      allowHtml: false,
      maxLength: 200,
      stripControlChars: true,
      normalizeWhitespace: true,
      preventInjection: true,
      escapeSpecialChars: true,
      allowedCharacters: /[\w\s\-_.()[\]!?@#%&+=]/,
    });
  }

  /**
   * Sanitize description/long text inputs
   */
  sanitizeDescription(input: string): SanitizationResult {
    return this.sanitizeText(input, {
      allowHtml: false,
      maxLength: 2000,
      stripControlChars: true,
      normalizeWhitespace: true,
      preventInjection: true,
      escapeSpecialChars: false, // Descriptions can have more varied content
      allowedCharacters: /[\w\s\-_.()[\]!?@#%&+=:;,."']/,
    });
  }

  /**
   * Sanitize board/column names
   */
  sanitizeName(input: string, maxLength: number = 50): SanitizationResult {
    return this.sanitizeText(input, {
      allowHtml: false,
      maxLength,
      stripControlChars: true,
      normalizeWhitespace: true,
      preventInjection: true,
      escapeSpecialChars: true,
      allowedCharacters: /[\w\s\-_]/,
    });
  }

  /**
   * Sanitize tag names (more restrictive)
   */
  sanitizeTag(input: string): SanitizationResult {
    return this.sanitizeText(input, {
      allowHtml: false,
      maxLength: 20,
      stripControlChars: true,
      normalizeWhitespace: true,
      preventInjection: true,
      escapeSpecialChars: true,
      allowedCharacters: /[\w\-_]/,
    });
  }

  /**
   * Sanitize command-line arguments
   */
  sanitizeCliArgument(input: string): SanitizationResult {
    return this.sanitizeText(input, {
      allowHtml: false,
      maxLength: 500,
      stripControlChars: true,
      normalizeWhitespace: false, // Preserve exact spacing in CLI args
      preventInjection: true,
      escapeSpecialChars: true,
    });
  }

  /**
   * Batch sanitize multiple inputs
   */
  sanitizeBatch(
    inputs: Record<string, string>,
    sanitizers: Record<string, (input: string) => SanitizationResult>
  ): Record<string, SanitizationResult> {
    const results: Record<string, SanitizationResult> = {};

    Object.entries(inputs).forEach(([key, value]) => {
      const sanitizer = sanitizers[key] || this.sanitizeText.bind(this);
      results[key] = sanitizer(value);
    });

    return results;
  }

  /**
   * Check if input contains suspicious patterns
   */
  detectSuspiciousPatterns(input: string): { suspicious: boolean; patterns: string[] } {
    const suspiciousPatterns = [
      { pattern: /javascript:/i, name: 'JavaScript protocol' },
      { pattern: /data:/i, name: 'Data protocol' },
      { pattern: /vbscript:/i, name: 'VBScript protocol' },
      { pattern: /<script/i, name: 'Script tag' },
      { pattern: /on\w+\s*=/i, name: 'Event handler' },
      { pattern: /eval\s*\(/i, name: 'Eval function' },
      { pattern: /expression\s*\(/i, name: 'CSS expression' },
      { pattern: /\$\{.*\}/g, name: 'Template literal injection' },
      { pattern: /\{\{.*\}\}/g, name: 'Template engine injection' },
      { pattern: /rm\s+-rf/i, name: 'Destructive command' },
      { pattern: /curl\s+.*\|\s*sh/i, name: 'Remote script execution' },
    ];

    const detectedPatterns: string[] = [];

    suspiciousPatterns.forEach(({ pattern, name }) => {
      if (pattern.test(input)) {
        detectedPatterns.push(name);
      }
    });

    return {
      suspicious: detectedPatterns.length > 0,
      patterns: detectedPatterns,
    };
  }

  /**
   * Generate security report for input
   */
  generateSecurityReport(input: string): {
    safe: boolean;
    score: number; // 0-100, higher is safer
    issues: string[];
    recommendations: string[];
  } {
    const issues: string[] = [];
    const recommendations: string[] = [];
    let score = 100;

    // Check length
    if (input.length > 1000) {
      issues.push('Input exceeds recommended length');
      recommendations.push('Consider shortening the input');
      score -= 10;
    }

    // Check for suspicious patterns
    const suspiciousCheck = this.detectSuspiciousPatterns(input);
    if (suspiciousCheck.suspicious) {
      issues.push(`Suspicious patterns detected: ${suspiciousCheck.patterns.join(', ')}`);
      recommendations.push('Remove or escape suspicious content');
      score -= 30;
    }

    // Check for control characters
    if (/[\x00-\x08\x0B\x0C\x0E-\x1F\x7F-\x9F]/.test(input)) {
      issues.push('Contains control characters');
      recommendations.push('Remove non-printable characters');
      score -= 15;
    }

    // Check for excessive special characters
    const specialCharCount = (input.match(/[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/g) || []).length;
    const specialCharRatio = specialCharCount / input.length;
    if (specialCharRatio > 0.3) {
      issues.push('High ratio of special characters');
      recommendations.push('Review special character usage');
      score -= 10;
    }

    return {
      safe: score >= 70,
      score: Math.max(0, score),
      issues,
      recommendations,
    };
  }
}

// Export singleton instance
export const inputSanitizer = InputSanitizer.getInstance();

// Utility functions for quick access
export const sanitizeTaskTitle = (input: string) => inputSanitizer.sanitizeTaskTitle(input);
export const sanitizeDescription = (input: string) => inputSanitizer.sanitizeDescription(input);
export const sanitizeName = (input: string, maxLength?: number) => inputSanitizer.sanitizeName(input, maxLength);
export const sanitizeTag = (input: string) => inputSanitizer.sanitizeTag(input);
export const sanitizeEmail = (input: string) => inputSanitizer.sanitizeEmail(input);
export const sanitizeUrl = (input: string) => inputSanitizer.sanitizeUrl(input);
export const sanitizeFilePath = (input: string) => inputSanitizer.sanitizeFilePath(input);
export const detectSuspicious = (input: string) => inputSanitizer.detectSuspiciousPatterns(input);

/**
 * Safe prompt wrapper that automatically sanitizes input
 */
export function createSafePromptValidator(
  sanitizeFunction: (input: string) => SanitizationResult,
  additionalValidation?: (input: string) => true | string
) {
  return (input: string): true | string => {
    // First, sanitize the input
    const sanitized = sanitizeFunction(input);

    // Check if input was modified significantly
    if (sanitized.modified && sanitized.warnings.length > 0) {
      return `Input modified during sanitization: ${sanitized.warnings.join(', ')}. Please try again.`;
    }

    // Check security score
    const securityReport = inputSanitizer.generateSecurityReport(input);
    if (!securityReport.safe) {
      return `Security issues detected: ${securityReport.issues.join(', ')}`;
    }

    // Run additional validation if provided
    if (additionalValidation) {
      return additionalValidation(sanitized.sanitized);
    }

    return true;
  };
}

import chalk from 'chalk';

export interface KeyboardShortcut {
  key: string;
  description: string;
  action: () => Promise<void> | void;
  global?: boolean;
}

/**
 * Global keyboard shortcut handler for CLI applications
 */
export class KeyboardHandler {
  private shortcuts: Map<string, KeyboardShortcut> = new Map();
  private isActive = false;
  private helpVisible = false;
  private refreshCallback?: () => Promise<void> | void;

  constructor() {
    this.setupDefaultShortcuts();
  }

  /**
   * Register a new keyboard shortcut
   */
  register(shortcut: KeyboardShortcut): void {
    this.shortcuts.set(shortcut.key, shortcut);
  }

  /**
   * Remove a keyboard shortcut
   */
  unregister(key: string): void {
    this.shortcuts.delete(key);
  }

  /**
   * Activate global keyboard listening
   */
  activate(): void {
    if (this.isActive) return;

    this.isActive = true;

    // Set up raw mode for real-time key capture
    if (process.stdin.isTTY) {
      process.stdin.setRawMode(true);
      process.stdin.resume();
      process.stdin.setEncoding('utf8');
      process.stdin.on('data', this.handleKeyPress.bind(this));
    }
  }

  /**
   * Deactivate global keyboard listening
   */
  deactivate(): void {
    if (!this.isActive) return;

    this.isActive = false;

    if (process.stdin.isTTY) {
      process.stdin.setRawMode(false);
      process.stdin.pause();
      process.stdin.removeAllListeners('data');
    }
  }

  /**
   * Handle key press events
   */
  private async handleKeyPress(key: string): Promise<void> {
    const keyCode = key.charCodeAt(0);

    // Handle Ctrl+C (exit)
    if (keyCode === 3) {
      console.log(chalk.yellow('\nâš ï¸  Interrupted by user'));
      process.exit(130);
    }

    // Handle specific key combinations
    const shortcut = this.shortcuts.get(key);
    if (shortcut) {
      try {
        await shortcut.action();
      } catch (error) {
        console.error(chalk.red(`Error executing shortcut ${key}:`), error);
      }
    }
  }

  /**
   * Show help overlay with all available shortcuts
   */
  showHelp(): void {
    if (this.helpVisible) {
      this.hideHelp();
      return;
    }

    this.helpVisible = true;
    console.clear();

    console.log(chalk.cyan.bold('\nðŸ“‹ Keyboard Shortcuts\n'));
    console.log(chalk.gray('â”€'.repeat(50)));

    const globalShortcuts = Array.from(this.shortcuts.values())
      .filter(s => s.global)
      .sort((a, b) => a.key.localeCompare(b.key));

    const localShortcuts = Array.from(this.shortcuts.values())
      .filter(s => !s.global)
      .sort((a, b) => a.key.localeCompare(b.key));

    if (globalShortcuts.length > 0) {
      console.log(chalk.yellow.bold('\nGlobal Shortcuts:'));
      globalShortcuts.forEach(shortcut => {
        const keyDisplay = this.formatKeyDisplay(shortcut.key);
        console.log(`  ${keyDisplay} - ${shortcut.description}`);
      });
    }

    if (localShortcuts.length > 0) {
      console.log(chalk.yellow.bold('\nContext Shortcuts:'));
      localShortcuts.forEach(shortcut => {
        const keyDisplay = this.formatKeyDisplay(shortcut.key);
        console.log(`  ${keyDisplay} - ${shortcut.description}`);
      });
    }

    console.log(chalk.gray('\nâ”€'.repeat(50)));
    console.log(chalk.gray('Press ? again to hide help, Ctrl+C to exit\n'));
  }

  /**
   * Hide help overlay
   */
  hideHelp(): void {
    if (!this.helpVisible) return;

    this.helpVisible = false;
    console.clear();
    console.log(chalk.gray('Help hidden. Press ? to show again.\n'));
  }

  /**
   * Format key display for help
   */
  private formatKeyDisplay(key: string): string {
    const keyCode = key.charCodeAt(0);

    if (keyCode === 3) return chalk.cyan('Ctrl+C');
    if (keyCode === 18) return chalk.cyan('Ctrl+R');
    if (keyCode === 6) return chalk.cyan('Ctrl+F');
    if (keyCode === 14) return chalk.cyan('Ctrl+N');
    if (key === '?') return chalk.cyan('?');
    if (key === 'q') return chalk.cyan('q');
    if (key === 'r') return chalk.cyan('r');
    if (key === '/') return chalk.cyan('/');

    return chalk.cyan(key);
  }

  /**
   * Set up default global shortcuts
   */
  private setupDefaultShortcuts(): void {
    // Help shortcut
    this.register({
      key: '?',
      description: 'Show/hide keyboard shortcuts help',
      action: () => this.showHelp(),
      global: true,
    });

    // Refresh shortcut (Ctrl+R)
    this.register({
      key: String.fromCharCode(18), // Ctrl+R
      description: 'Refresh current view',
      action: async () => {
        await this.executeRefresh();
      },
      global: true,
    });

    // Search shortcut (Ctrl+F or /)
    this.register({
      key: '/',
      description: 'Search/filter',
      action: async () => {
        console.log(chalk.cyan('ðŸ” Search mode (not implemented)'));
      },
      global: true,
    });

    // New item shortcut (Ctrl+N)
    this.register({
      key: String.fromCharCode(14), // Ctrl+N
      description: 'Create new item',
      action: async () => {
        console.log(chalk.cyan('ðŸ“ Create new item (not implemented)'));
      },
      global: true,
    });

    // Quit shortcut
    this.register({
      key: 'q',
      description: 'Quit application',
      action: () => {
        console.log(chalk.yellow('ðŸ‘‹ Goodbye!'));
        process.exit(0);
      },
      global: true,
    });
  }

  /**
   * Get all registered shortcuts
   */
  getShortcuts(): KeyboardShortcut[] {
    return Array.from(this.shortcuts.values());
  }

  /**
   * Check if handler is currently active
   */
  isActivated(): boolean {
    return this.isActive;
  }

  /**
   * Set refresh callback for Ctrl+R functionality
   */
  setRefreshCallback(callback: () => Promise<void> | void): void {
    this.refreshCallback = callback;
  }

  /**
   * Execute refresh action
   */
  private async executeRefresh(): Promise<void> {
    if (this.refreshCallback) {
      try {
        console.log(chalk.cyan('ðŸ”„ Refreshing...'));
        await this.refreshCallback();
        console.log(chalk.green('âœ… Refreshed'));
      } catch (error) {
        console.error(chalk.red('âŒ Refresh failed:'), error instanceof Error ? error.message : String(error));
      }
    } else {
      console.log(chalk.yellow('âš ï¸  No refresh action available in current context'));
    }
  }

  /**
   * Create a scoped keyboard handler for specific contexts
   */
  createScope(contextName: string): ScopedKeyboardHandler {
    return new ScopedKeyboardHandler(this, contextName);
  }
}

/**
 * Scoped keyboard handler for specific UI contexts
 */
export class ScopedKeyboardHandler {
  private parent: KeyboardHandler;
  private contextName: string;
  private scopedShortcuts: Map<string, KeyboardShortcut> = new Map();

  constructor(parent: KeyboardHandler, contextName: string) {
    this.parent = parent;
    this.contextName = contextName;
  }

  /**
   * Register a context-specific shortcut
   */
  register(shortcut: Omit<KeyboardShortcut, 'global'>): void {
    const scopedShortcut: KeyboardShortcut = {
      ...shortcut,
      global: false,
    };

    this.scopedShortcuts.set(shortcut.key, scopedShortcut);
    this.parent.register(scopedShortcut);
  }

  /**
   * Activate this scope (removes other scope shortcuts)
   */
  activate(): void {
    // Clean up previous scope shortcuts
    this.parent.getShortcuts()
      .filter(s => !s.global)
      .forEach(s => this.parent.unregister(s.key));

    // Add this scope's shortcuts
    this.scopedShortcuts.forEach(shortcut => {
      this.parent.register(shortcut);
    });
  }

  /**
   * Deactivate this scope
   */
  deactivate(): void {
    this.scopedShortcuts.forEach((_, key) => {
      this.parent.unregister(key);
    });
  }
}

// Export singleton instance
export const keyboardHandler = new KeyboardHandler();

// Cleanup on process exit
process.on('exit', () => {
  keyboardHandler.deactivate();
});

process.on('SIGINT', () => {
  keyboardHandler.deactivate();
  process.exit(130);
});

process.on('SIGTERM', () => {
  keyboardHandler.deactivate();
  process.exit(143);
});

