/**
 * Secure CLI Wrapper
 * Integrates command injection prevention and input sanitization for the entire CLI
 * Addresses TASK-120: Command injection prevention
 */

import { Command } from 'commander';
import { commandInjectionPrevention, validateCommand } from './command-injection-prevention';
import { inputSanitizer } from './input-sanitizer';
import chalk from 'chalk';

export interface SecurityConfig {
  enableInputSanitization: boolean;
  enableCommandValidation: boolean;
  logSecurityEvents: boolean;
  strictMode: boolean;
  maxArgumentLength: number;
  allowedCommands: string[];
  blockedPatterns: RegExp[];
}

export interface SecurityEvent {
  timestamp: Date;
  type: 'input_sanitized' | 'command_blocked' | 'suspicious_pattern' | 'validation_failed';
  details: string;
  input: string;
  risk: 'low' | 'medium' | 'high' | 'critical';
}

/**
 * Secure CLI wrapper with comprehensive protection
 */
export class SecureCliWrapper {
  private static instance: SecureCliWrapper;
  private securityEvents: SecurityEvent[] = [];
  private config: SecurityConfig;

  constructor(config: Partial<SecurityConfig> = {}) {
    this.config = {
      enableInputSanitization: true,
      enableCommandValidation: true,
      logSecurityEvents: true,
      strictMode: false,
      maxArgumentLength: 1000,
      allowedCommands: [],
      blockedPatterns: [
        /rm\s+-rf/i,
        /curl.*\|.*sh/i,
        /wget.*\|.*sh/i,
        /eval\s*\(/i,
        /exec\s*\(/i,
        /system\s*\(/i,
        /passthru\s*\(/i,
        /shell_exec\s*\(/i,
        /`[^`]*`/,
        /\$\([^)]*\)/,
        /<\([^)]*\)/,
      ],
      ...config,
    };

    // Configure command injection prevention with CLI-specific settings
    if (this.config.allowedCommands.length > 0) {
      this.config.allowedCommands.forEach(cmd => {
        commandInjectionPrevention.addAllowedCommand(cmd);
      });
    }
  }

  static getInstance(config?: Partial<SecurityConfig>): SecureCliWrapper {
    if (!SecureCliWrapper.instance) {
      SecureCliWrapper.instance = new SecureCliWrapper(config);
    }
    return SecureCliWrapper.instance;
  }

  /**
   * Sanitize and validate CLI arguments
   */
  sanitizeArguments(args: string[]): { sanitized: string[]; warnings: string[] } {
    const sanitized: string[] = [];
    const warnings: string[] = [];

    args.forEach((arg, index) => {
      // Check length
      if (arg.length > this.config.maxArgumentLength) {
        this.logSecurityEvent({
          type: 'validation_failed',
          details: `Argument ${index} exceeds maximum length`,
          input: arg.substring(0, 100) + '...',
          risk: 'medium',
        });
        if (this.config.strictMode) {
          throw new Error(`Argument ${index} exceeds maximum length (${this.config.maxArgumentLength})`);
        }
        warnings.push(`Argument ${index} truncated`);
        arg = arg.substring(0, this.config.maxArgumentLength);
      }

      // Check for blocked patterns
      const blockedPattern = this.config.blockedPatterns.find(pattern => pattern.test(arg));
      if (blockedPattern) {
        this.logSecurityEvent({
          type: 'command_blocked',
          details: `Blocked pattern detected: ${blockedPattern.source}`,
          input: arg,
          risk: 'high',
        });
        if (this.config.strictMode) {
          throw new Error(`Blocked pattern detected in argument ${index}: ${blockedPattern.source}`);
        }
        warnings.push(`Blocked pattern removed from argument ${index}`);
        arg = arg.replace(blockedPattern, '');
      }

      // Sanitize input if enabled
      if (this.config.enableInputSanitization) {
        const sanitizationResult = inputSanitizer.sanitizeCliArgument(arg);
        if (sanitizationResult.modified) {
          this.logSecurityEvent({
            type: 'input_sanitized',
            details: `Argument ${index} sanitized: ${sanitizationResult.warnings.join(', ')}`,
            input: arg,
            risk: 'low',
          });
          warnings.push(`Argument ${index} sanitized`);
        }
        sanitized.push(sanitizationResult.sanitized);
      } else {
        sanitized.push(arg);
      }
    });

    return { sanitized, warnings };
  }

  /**
   * Create a secure command wrapper
   */
  createSecureCommand(name: string, description: string): Command {
    const command = new Command(name);
    command.description(description);

    // Override the action method to add security checks
    const originalAction = command.action.bind(command);
    command.action = (fn: (...args: any[]) => void | Promise<void>) => {
      return originalAction(async (...args: any[]) => {
        try {
          // Extract options and command arguments
          const options = args[args.length - 1];
          const commandArgs = args.slice(0, -1);

          // Validate command arguments
          if (this.config.enableCommandValidation && commandArgs.length > 0) {
            const stringArgs = commandArgs.map(arg => String(arg));
            const validation = validateCommand(name, stringArgs);

            if (!validation.safe) {
              this.logSecurityEvent({
                type: 'command_blocked',
                details: `Command validation failed: ${validation.blockedPatterns.join(', ')}`,
                input: `${name} ${stringArgs.join(' ')}`,
                risk: 'critical',
              });
              throw new Error(`Command blocked: ${validation.blockedPatterns.join(', ')}`);
            }

            if (validation.warnings.length > 0) {
              this.logSecurityEvent({
                type: 'suspicious_pattern',
                details: `Command warnings: ${validation.warnings.join(', ')}`,
                input: `${name} ${stringArgs.join(' ')}`,
                risk: 'medium',
              });
              if (this.config.logSecurityEvents) {
                console.warn(chalk.yellow(`âš ï¸  Security warnings: ${validation.warnings.join(', ')}`));
              }
            }
          }

          // Check for suspicious patterns in options
          if (options && typeof options === 'object') {
            for (const [key, value] of Object.entries(options)) {
              if (typeof value === 'string') {
                const suspicious = inputSanitizer.detectSuspiciousPatterns(value);
                if (suspicious.suspicious) {
                  this.logSecurityEvent({
                    type: 'suspicious_pattern',
                    details: `Suspicious pattern in option ${key}: ${suspicious.patterns.join(', ')}`,
                    input: value,
                    risk: 'medium',
                  });
                  if (this.config.strictMode) {
                    throw new Error(`Suspicious pattern detected in option ${key}`);
                  }
                }
              }
            }
          }

          // Execute the original function
          return await fn(...args);
        } catch (error) {
          if (error instanceof Error && error.message.includes('blocked')) {
            console.error(chalk.red(`ðŸš« Security: ${error.message}`));
            process.exit(1);
          }
          throw error;
        }
      });
    };

    return command;
  }

  /**
   * Wrap an existing command with security features
   */
  secureCommand(command: Command): Command {
    // Store original action
    const originalAction = command._actionHandler;

    if (originalAction) {
      command.action(async (...args: any[]) => {
        try {
          // Apply security checks
          const sanitizedArgs = this.sanitizeArguments(
            args.filter(arg => typeof arg === 'string')
          );

          if (sanitizedArgs.warnings.length > 0 && this.config.logSecurityEvents) {
            console.warn(chalk.yellow(`âš ï¸  Security: ${sanitizedArgs.warnings.join(', ')}`));
          }

          // Execute original action with sanitized args
          return await originalAction(...args);
        } catch (error) {
          if (error instanceof Error && error.message.includes('blocked')) {
            console.error(chalk.red(`ðŸš« Security: ${error.message}`));
            process.exit(1);
          }
          throw error;
        }
      });
    }

    return command;
  }

  /**
   * Log security events
   */
  private logSecurityEvent(event: Omit<SecurityEvent, 'timestamp'>): void {
    const fullEvent: SecurityEvent = {
      ...event,
      timestamp: new Date(),
    };

    this.securityEvents.push(fullEvent);

    // Keep only last 1000 events
    if (this.securityEvents.length > 1000) {
      this.securityEvents = this.securityEvents.slice(-1000);
    }

    if (this.config.logSecurityEvents) {
      const color = {
        low: chalk.blue,
        medium: chalk.yellow,
        high: chalk.magenta,
        critical: chalk.red,
      }[event.risk];

      console.log(
        color(`ðŸ”’ Security [${event.risk.toUpperCase()}]: ${event.type} - ${event.details}`)
      );
    }
  }

  /**
   * Get security events
   */
  getSecurityEvents(limit?: number): SecurityEvent[] {
    const events = this.securityEvents.slice().reverse();
    return limit ? events.slice(0, limit) : events;
  }

  /**
   * Get security statistics
   */
  getSecurityStats(): {
    totalEvents: number;
    eventsByType: Record<string, number>;
    eventsByRisk: Record<string, number>;
    lastEvent?: SecurityEvent;
  } {
    const eventsByType: Record<string, number> = {};
    const eventsByRisk: Record<string, number> = {};

    this.securityEvents.forEach(event => {
      eventsByType[event.type] = (eventsByType[event.type] || 0) + 1;
      eventsByRisk[event.risk] = (eventsByRisk[event.risk] || 0) + 1;
    });

    return {
      totalEvents: this.securityEvents.length,
      eventsByType,
      eventsByRisk,
      lastEvent: this.securityEvents[this.securityEvents.length - 1],
    };
  }

  /**
   * Clear security events
   */
  clearSecurityEvents(): void {
    this.securityEvents = [];
  }

  /**
   * Update security configuration
   */
  updateConfig(newConfig: Partial<SecurityConfig>): void {
    this.config = { ...this.config, ...newConfig };
  }

  /**
   * Get current security configuration
   */
  getConfig(): SecurityConfig {
    return { ...this.config };
  }

  /**
   * Validate program arguments before execution
   */
  validateProgramArgs(argv: string[]): { safe: boolean; sanitized: string[]; warnings: string[] } {
    const sanitizedResult = this.sanitizeArguments(argv);

    // Additional validation for the entire command line
    const fullCommand = sanitizedResult.sanitized.join(' ');
    const suspicious = inputSanitizer.detectSuspiciousPatterns(fullCommand);

    if (suspicious.suspicious) {
      this.logSecurityEvent({
        type: 'suspicious_pattern',
        details: `Command line contains suspicious patterns: ${suspicious.patterns.join(', ')}`,
        input: fullCommand,
        risk: 'high',
      });

      if (this.config.strictMode) {
        return {
          safe: false,
          sanitized: sanitizedResult.sanitized,
          warnings: [...sanitizedResult.warnings, 'Suspicious patterns detected'],
        };
      }
    }

    return {
      safe: true,
      sanitized: sanitizedResult.sanitized,
      warnings: sanitizedResult.warnings,
    };
  }

  /**
   * Create security report
   */
  generateSecurityReport(): string {
    const stats = this.getSecurityStats();
    const recentEvents = this.getSecurityEvents(10);

    let report = `
ðŸ”’ CLI Security Report
===================

Total Security Events: ${stats.totalEvents}

Events by Type:
${Object.entries(stats.eventsByType)
  .map(([type, count]) => `  ${type}: ${count}`)
  .join('\n')}

Events by Risk Level:
${Object.entries(stats.eventsByRisk)
  .map(([risk, count]) => `  ${risk}: ${count}`)
  .join('\n')}

Recent Events (last 10):
${recentEvents
  .map(
    event =>
      `  ${event.timestamp.toISOString()} [${event.risk.toUpperCase()}] ${event.type}: ${event.details}`
  )
  .join('\n')}

Security Configuration:
  Input Sanitization: ${this.config.enableInputSanitization ? 'Enabled' : 'Disabled'}
  Command Validation: ${this.config.enableCommandValidation ? 'Enabled' : 'Disabled'}
  Strict Mode: ${this.config.strictMode ? 'Enabled' : 'Disabled'}
  Max Argument Length: ${this.config.maxArgumentLength}
  Allowed Commands: ${this.config.allowedCommands.length}
  Blocked Patterns: ${this.config.blockedPatterns.length}
`;

    return report;
  }
}

// Export singleton instance and utilities
export const secureCliWrapper = SecureCliWrapper.getInstance({
  enableInputSanitization: true,
  enableCommandValidation: true,
  logSecurityEvents: process.env.NODE_ENV !== 'production',
  strictMode: false,
  maxArgumentLength: 1000,
  allowedCommands: [
    'git', 'npm', 'yarn', 'pnpm', 'node', 'tsc', 'eslint', 'prettier',
    'cat', 'ls', 'grep', 'find', 'head', 'tail', 'wc', 'sort', 'uniq',
  ],
});

export const createSecureCommand = (name: string, description: string) =>
  secureCliWrapper.createSecureCommand(name, description);

export const secureCommand = (command: Command) => secureCliWrapper.secureCommand(command);

export const validateProgramArgs = (argv: string[]) => secureCliWrapper.validateProgramArgs(argv);

/**
 * Security middleware for Commander.js programs
 */
export function addSecurityMiddleware(program: Command): Command {
  // Add global security options
  program
    .option('--security-report', 'show security report')
    .option('--security-strict', 'enable strict security mode')
    .option('--security-events [limit]', 'show recent security events');

  // Hook into the program's action handling
  const originalParse = program.parse.bind(program);
  program.parse = function(argv?: readonly string[], options?: any) {
    const args = argv || process.argv;

    // Validate arguments before parsing
    const validation = validateProgramArgs(args.slice(2));

    if (!validation.safe) {
      console.error(chalk.red('ðŸš« Security: Command blocked due to security concerns'));
      console.error(chalk.yellow('Warnings:', validation.warnings.join(', ')));
      process.exit(1);
    }

    if (validation.warnings.length > 0) {
      console.warn(chalk.yellow('âš ï¸  Security warnings:', validation.warnings.join(', ')));
    }

    return originalParse.call(this, argv, options);
  };

  // Add security command
  const securityCmd = program.command('security').description('Security management commands');

  securityCmd
    .command('report')
    .description('Show security report')
    .action(() => {
      console.log(secureCliWrapper.generateSecurityReport());
    });

  securityCmd
    .command('events [limit]')
    .description('Show recent security events')
    .action((limit?: string) => {
      const events = secureCliWrapper.getSecurityEvents(limit ? parseInt(limit) : 20);
      if (events.length === 0) {
        console.log(chalk.green('âœ… No security events recorded'));
        return;
      }

      console.log(chalk.blue('ðŸ”’ Recent Security Events:'));
      events.forEach(event => {
        const color = {
          low: chalk.blue,
          medium: chalk.yellow,
          high: chalk.magenta,
          critical: chalk.red,
        }[event.risk];

        console.log(
          `${event.timestamp.toISOString()} ${color(`[${event.risk.toUpperCase()}]`)} ${
            event.type
          }: ${event.details}`
        );
      });
    });

  securityCmd
    .command('clear')
    .description('Clear security event log')
    .action(() => {
      secureCliWrapper.clearSecurityEvents();
      console.log(chalk.green('âœ… Security events cleared'));
    });

  securityCmd
    .command('config')
    .description('Show security configuration')
    .action(() => {
      const config = secureCliWrapper.getConfig();
      console.log(chalk.blue('ðŸ”§ Security Configuration:'));
      console.log(JSON.stringify(config, null, 2));
    });

  return program;
}

import type { Ora } from 'ora';
import ora from 'ora';

/**
 * Error thrown by SpinnerManager
 */
export class SpinnerError extends Error {
  constructor(message: string, public code: string) {
    super(message);
    this.name = 'SpinnerError';
  }
}

/**
 * Manages loading spinners for CLI operations with comprehensive error handling
 */
export class SpinnerManager {
  private spinner: Ora | null = null;

  private isSpinning = false;

  private destroyed = false;

  private readonly maxTextLength = 200;

  /**
   * Validate text input
   */
  private validateText(text: string): string {
    if (this.destroyed) {
      throw new SpinnerError('SpinnerManager has been destroyed', 'DESTROYED');
    }

    if (typeof text !== 'string') {
      throw new SpinnerError('Text must be a string', 'INVALID_TYPE');
    }

    if (text.length === 0) {
      throw new SpinnerError('Text cannot be empty', 'EMPTY_TEXT');
    }

    if (text.length > this.maxTextLength) {
      return text.substring(0, this.maxTextLength - 3) + '...';
    }

    return text.trim();
  }

  /**
   * Safe spinner operation wrapper
   */
  private safeOperation<T>(operation: () => T, fallback: T): T {
    try {
      return operation();
    } catch (error) {
      console.warn(`Spinner operation failed: ${error instanceof Error ? error.message : String(error)}`);
      return fallback;
    }
  }

  /**
   * Start a new spinner with the given text
   */
  start(text: string): void {
    try {
      const validatedText = this.validateText(text);

      if (this.isSpinning) {
        this.stop();
      }

      this.spinner = this.safeOperation(() => 
        ora({
          text: validatedText,
          color: 'cyan',
          spinner: 'dots',
          hideCursor: true,
        }).start(),
        null
      );

      if (this.spinner) {
        this.isSpinning = true;
      } else {
        throw new SpinnerError('Failed to create spinner instance', 'CREATION_FAILED');
      }
    } catch (error) {
      if (error instanceof SpinnerError) {
        throw error;
      }
      throw new SpinnerError(`Failed to start spinner: ${error instanceof Error ? error.message : String(error)}`, 'START_FAILED');
    }
  }

  /**
   * Update the spinner text
   */
  update(text: string): void {
    try {
      const validatedText = this.validateText(text);

      if (!this.spinner) {
        throw new SpinnerError('No active spinner to update', 'NO_SPINNER');
      }

      if (!this.isSpinning) {
        throw new SpinnerError('Spinner is not currently running', 'NOT_RUNNING');
      }

      this.safeOperation(() => {
        this.spinner!.text = validatedText;
        return true;
      }, false);
    } catch (error) {
      if (error instanceof SpinnerError) {
        throw error;
      }
      throw new SpinnerError(`Failed to update spinner: ${error instanceof Error ? error.message : String(error)}`, 'UPDATE_FAILED');
    }
  }

  /**
   * Common stop logic with error handling
   */
  private stopSpinner(method: 'succeed' | 'fail' | 'warn' | 'info' | 'stop', text?: string): void {
    try {
      if (this.destroyed) {
        console.warn('Attempted to stop destroyed spinner');
        return;
      }

      if (!this.spinner || !this.isSpinning) {
        // Silently ignore - not an error condition
        return;
      }

      const finalText = text ? this.validateText(text) : this.spinner.text;

      this.safeOperation(() => {
        if (method === 'stop') {
          this.spinner!.stop();
        } else {
          this.spinner![method](finalText);
        }
        return true;
      }, false);

      this.cleanup();
    } catch (error) {
      console.warn(`Failed to stop spinner: ${error instanceof Error ? error.message : String(error)}`);
      this.forceCleanup();
    }
  }

  /**
   * Clean up spinner state
   */
  private cleanup(): void {
    this.isSpinning = false;
    this.spinner = null;
  }

  /**
   * Force cleanup in case of errors
   */
  private forceCleanup(): void {
    try {
      if (this.spinner) {
        this.spinner.stop();
      }
    } catch {
      // Ignore errors during force cleanup
    }
    this.cleanup();
  }

  /**
   * Stop the spinner with success
   */
  succeed(text?: string): void {
    this.stopSpinner('succeed', text);
  }

  /**
   * Stop the spinner with failure
   */
  fail(text?: string): void {
    this.stopSpinner('fail', text);
  }

  /**
   * Stop the spinner with warning
   */
  warn(text?: string): void {
    this.stopSpinner('warn', text);
  }

  /**
   * Stop the spinner with info
   */
  info(text?: string): void {
    this.stopSpinner('info', text);
  }

  /**
   * Stop the spinner without any status
   */
  stop(): void {
    this.stopSpinner('stop');
  }

  /**
   * Check if spinner is currently active
   */
  isActive(): boolean {
    return this.isSpinning && !this.destroyed;
  }

  /**
   * Destroy the spinner manager (cleanup)
   */
  destroy(): void {
    this.forceCleanup();
    this.destroyed = true;
  }

  /**
   * Create a promise that shows a spinner while executing
   */
  async withSpinner<T>(
    text: string,
    promise: Promise<T>,
    options?: {
      successText?: string;
      failText?: string;
      timeout?: number;
    }
  ): Promise<T> {
    if (!promise || typeof promise.then !== 'function') {
      throw new SpinnerError('Promise is required and must be a valid Promise', 'INVALID_PROMISE');
    }

    this.start(text);

    const timeout = options?.timeout;
    let timeoutId: NodeJS.Timeout | undefined;

    try {
      let racePromise: Promise<T>;

      if (timeout && timeout > 0) {
        const timeoutPromise = new Promise<never>((_, reject) => {
          timeoutId = setTimeout(() => {
            reject(new SpinnerError(`Operation timed out after ${timeout}ms`, 'TIMEOUT'));
          }, timeout);
        });

        racePromise = Promise.race([promise, timeoutPromise]);
      } else {
        racePromise = promise;
      }

      const result = await racePromise;
      if (timeoutId) clearTimeout(timeoutId);
      this.succeed(options?.successText);
      return result;
    } catch (error) {
      if (timeoutId) clearTimeout(timeoutId);

      const errorMessage = error instanceof Error ? error.message : String(error);
      const failText = options?.failText || `Failed: ${text} - ${errorMessage}`;

      this.fail(failText);
      throw error;
    }
  }

  /**
   * Create a spinner for a multi-step process
   */
  async withSteps(
    steps: Array<{
      text: string;
      action: () => Promise<any>;
      successText?: string;
      failText?: string;
      timeout?: number;
      skipOnError?: boolean;
    }>,
    options?: {
      stopOnError?: boolean;
      showProgress?: boolean;
    }
  ): Promise<{ results: any[]; errors: Error[] }> {
    if (!Array.isArray(steps) || steps.length === 0) {
      throw new SpinnerError('Steps must be a non-empty array', 'INVALID_STEPS');
    }

    const { stopOnError = true, showProgress = true } = options || {};
    const results: any[] = [];
    const errors: Error[] = [];

    for (let i = 0; i < steps.length; i++) {
      const step = steps[i];

      if (!step || typeof step.action !== 'function') {
        const error = new SpinnerError(`Invalid step at index ${i}: action must be a function`, 'INVALID_STEP');
        errors.push(error);

        if (stopOnError && !step?.skipOnError) {
          throw error;
        }
        continue;
      }

      try {
        const progressText = showProgress 
          ? `[${i + 1}/${steps.length}] ${step.text}` 
          : step.text;

        const result = await this.withSpinner(
          progressText,
          step.action(),
          {
            successText: step.successText,
            failText: step.failText,
            timeout: step.timeout,
          }
        );

        results.push(result);
      } catch (error) {
        const stepError = error instanceof Error ? error : new Error(String(error));
        errors.push(stepError);

        if (stopOnError && !step.skipOnError) {
          throw stepError;
        }
      }
    }

    return { results, errors };
  }
}

// Export singleton instance
export const spinner = new SpinnerManager();

// Cleanup on process termination
const cleanup = () => {
  spinner.destroy();
};

process.on('exit', cleanup);
process.on('SIGINT', cleanup);
process.on('SIGTERM', cleanup);
process.on('SIGUSR1', cleanup);
process.on('SIGUSR2', cleanup);
process.on('uncaughtException', cleanup);
process.on('unhandledRejection', cleanup);

import chalk from 'chalk';
import Table from 'cli-table3';
import {
  formatStatus,
  formatPriority,
  formatRelativeTime,
  formatDateTime,
  truncate,
  formatProgressBar,
  formatKeyValue,
  formatDivider,
  formatHeader,
} from './formatter';

export interface TaskDetails {
  id: string;
  title: string;
  description?: string;
  status: string;
  priority?: string;
  assignee?: string;
  reporter?: string;
  created_at: string;
  updated_at: string;
  due_date?: string;
  estimated_hours?: number;
  actual_hours?: number;
  tags?: string[];
  subtasks?: TaskDetails[];
  dependencies?: string[];
  comments_count?: number;
  attachments_count?: number;
  completion_percentage?: number;
}

export interface TaskListOptions {
  showDescription?: boolean;
  showDates?: boolean;
  showAssignee?: boolean;
  showTags?: boolean;
  sortBy?: 'priority' | 'due_date' | 'created' | 'updated' | 'status';
  groupBy?: 'status' | 'priority' | 'assignee' | 'none';
  maxItems?: number;
  format?: 'table' | 'list' | 'compact';
}

/**
 * Format task lists for CLI display
 */
export class TaskListFormatter {
  /**
   * Format a list of tasks as a table
   */
  formatTaskTable(tasks: TaskDetails[], options: TaskListOptions = {}): string {
    const {
      showDescription = false,
      showDates = true,
      showAssignee = true,
      showTags = false,
      sortBy = 'priority',
    } = options;

    // Sort tasks
    const sortedTasks = this.sortTasks(tasks, sortBy);

    // Build table headers
    const headers = ['ID', 'Title', 'Status', 'Priority'];
    if (showAssignee) headers.push('Assignee');
    if (showDates) headers.push('Due Date');
    if (showTags) headers.push('Tags');

    // Create table
    const table = new Table({
      head: headers,
      style: { head: ['cyan'] },
      colWidths: this.calculateTableWidths(headers, showDescription),
    });

    // Add rows
    for (const task of sortedTasks) {
      const row: string[] = [
        task.id,
        truncate(task.title, 40),
        formatStatus(task.status),
        task.priority ? formatPriority(task.priority) : '-',
      ];

      if (showAssignee) {
        row.push(task.assignee ? chalk.cyan(`@${task.assignee}`) : '-');
      }

      if (showDates) {
        if (task.due_date) {
          const dueDate = new Date(task.due_date);
          const isOverdue = dueDate < new Date() && task.status !== 'done';
          const dateStr = formatDateTime(dueDate, 'MMM dd');
          row.push(isOverdue ? chalk.red(dateStr) : dateStr);
        } else {
          row.push('-');
        }
      }

      if (showTags) {
        row.push(task.tags ? task.tags.map(t => chalk.magenta(`#${t}`)).join(' ') : '-');
      }

      table.push(row);

      // Add description row if enabled
      if (showDescription && task.description) {
        const descRow = [
          {
            colSpan: headers.length,
            content: chalk.gray(`  ${truncate(task.description, 100)}`),
          },
        ];
        table.push(descRow);
      }
    }

    return table.toString();
  }

  /**
   * Format tasks as a detailed list
   */
  formatTaskList(tasks: TaskDetails[], options: TaskListOptions = {}): string {
    const { groupBy = 'none', maxItems } = options;
    const output: string[] = [];

    if (groupBy !== 'none') {
      const grouped = this.groupTasks(tasks, groupBy);

      for (const [group, groupTasks] of grouped) {
        output.push(chalk.bold.underline(`\n${group} (${groupTasks.length})`));

        const tasksToShow = maxItems ? groupTasks.slice(0, maxItems) : groupTasks;
        for (const task of tasksToShow) {
          output.push(this.formatTaskListItem(task));
        }

        if (maxItems && groupTasks.length > maxItems) {
          output.push(chalk.gray(`  ... and ${groupTasks.length - maxItems} more`));
        }
      }
    } else {
      const tasksToShow = maxItems ? tasks.slice(0, maxItems) : tasks;
      for (const task of tasksToShow) {
        output.push(this.formatTaskListItem(task));
      }

      if (maxItems && tasks.length > maxItems) {
        output.push(chalk.gray(`\n... and ${tasks.length - maxItems} more tasks`));
      }
    }

    return output.join('\n');
  }

  /**
   * Format a single task list item
   */
  private formatTaskListItem(task: TaskDetails): string {
    const lines: string[] = [];

    // Main task line
    const mainLine = [
      chalk.dim(`[${task.id}]`),
      task.title,
      task.priority ? formatPriority(task.priority) : '',
      formatStatus(task.status),
    ]
      .filter(Boolean)
      .join(' ');

    lines.push(`  ${mainLine}`);

    // Additional details
    const details: string[] = [];

    if (task.assignee) {
      details.push(chalk.cyan(`@${task.assignee}`));
    }

    if (task.due_date) {
      const dueDate = new Date(task.due_date);
      const isOverdue = dueDate < new Date() && task.status !== 'done';
      details.push(
        isOverdue
          ? chalk.red(`Due: ${formatDateTime(dueDate, 'MMM dd')}`)
          : chalk.gray(`Due: ${formatDateTime(dueDate, 'MMM dd')}`)
      );
    }

    if (task.tags && task.tags.length > 0) {
      details.push(task.tags.map(tag => chalk.magenta(`#${tag}`)).join(' '));
    }

    if (details.length > 0) {
      lines.push(`    ${details.join(' | ')}`);
    }

    return lines.join('\n');
  }

  /**
   * Format detailed task view
   */
  formatTaskDetail(task: TaskDetails): string {
    const output: string[] = [];

    // Header
    output.push(formatHeader(`Task ${task.id}`, 60));
    output.push('');

    // Title and status
    output.push(chalk.bold(task.title));
    output.push(formatStatus(task.status));
    output.push('');

    // Details section
    output.push(chalk.bold('Details:'));
    output.push(formatKeyValue('Priority', task.priority ? formatPriority(task.priority) : 'None'));
    output.push(formatKeyValue('Assignee', task.assignee || 'Unassigned'));
    output.push(formatKeyValue('Reporter', task.reporter || 'Unknown'));
    output.push(formatKeyValue('Created', formatRelativeTime(task.created_at)));
    output.push(formatKeyValue('Updated', formatRelativeTime(task.updated_at)));

    if (task.due_date) {
      const dueDate = new Date(task.due_date);
      const isOverdue = dueDate < new Date() && task.status !== 'done';
      const dueDateStr = formatDateTime(dueDate, 'MMM dd, yyyy');
      output.push(formatKeyValue('Due Date', isOverdue ? chalk.red(dueDateStr) : dueDateStr));
    }

    // Time tracking
    if (task.estimated_hours || task.actual_hours) {
      output.push('');
      output.push(chalk.bold('Time Tracking:'));
      if (task.estimated_hours) {
        output.push(formatKeyValue('Estimated', `${task.estimated_hours}h`));
      }
      if (task.actual_hours) {
        output.push(formatKeyValue('Actual', `${task.actual_hours}h`));
      }
    }

    // Description
    if (task.description) {
      output.push('');
      output.push(chalk.bold('Description:'));
      output.push(task.description);
    }

    // Progress
    if (task.completion_percentage !== undefined) {
      output.push('');
      output.push(chalk.bold('Progress:'));
      output.push(formatProgressBar(task.completion_percentage, 100, 30));
    }

    // Subtasks
    if (task.subtasks && task.subtasks.length > 0) {
      output.push('');
      output.push(chalk.bold(`Subtasks (${task.subtasks.length}):`));
      for (const subtask of task.subtasks) {
        const icon = subtask.status === 'done' ? 'âœ“' : 'â—‹';
        output.push(`  ${chalk.green(icon)} [${subtask.id}] ${subtask.title}`);
      }
    }

    // Dependencies
    if (task.dependencies && task.dependencies.length > 0) {
      output.push('');
      output.push(chalk.bold('Dependencies:'));
      output.push(`  ${task.dependencies.join(', ')}`);
    }

    // Tags
    if (task.tags && task.tags.length > 0) {
      output.push('');
      output.push(chalk.bold('Tags:'));
      output.push(`  ${task.tags.map(tag => chalk.magenta(`#${tag}`)).join(' ')}`);
    }

    // Metadata
    output.push('');
    output.push(formatDivider());
    if (task.comments_count) {
      output.push(`ðŸ’¬ ${task.comments_count} comment${task.comments_count > 1 ? 's' : ''}`);
    }
    if (task.attachments_count) {
      output.push(
        `ðŸ“Ž ${task.attachments_count} attachment${task.attachments_count > 1 ? 's' : ''}`
      );
    }

    return output.join('\n');
  }

  /**
   * Sort tasks by specified field
   */
  private sortTasks(tasks: TaskDetails[], sortBy: string): TaskDetails[] {
    return [...tasks].sort((a, b) => {
      switch (sortBy) {
        case 'priority':
          const priorityOrder = { P1: 1, P2: 2, P3: 3, P4: 4, P5: 5 };
          const aPriority = priorityOrder[a.priority as keyof typeof priorityOrder] || 99;
          const bPriority = priorityOrder[b.priority as keyof typeof priorityOrder] || 99;
          return aPriority - bPriority;

        case 'due_date':
          if (!a.due_date && !b.due_date) return 0;
          if (!a.due_date) return 1;
          if (!b.due_date) return -1;
          return new Date(a.due_date).getTime() - new Date(b.due_date).getTime();

        case 'created':
          return new Date(b.created_at).getTime() - new Date(a.created_at).getTime();

        case 'updated':
          return new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime();

        case 'status':
          return a.status.localeCompare(b.status);

        default:
          return 0;
      }
    });
  }

  /**
   * Group tasks by specified field
   */
  private groupTasks(tasks: TaskDetails[], groupBy: string): Map<string, TaskDetails[]> {
    const grouped = new Map<string, TaskDetails[]>();

    for (const task of tasks) {
      let key: string;

      switch (groupBy) {
        case 'status':
          key = task.status;
          break;
        case 'priority':
          key = task.priority || 'No Priority';
          break;
        case 'assignee':
          key = task.assignee || 'Unassigned';
          break;
        default:
          key = 'All Tasks';
      }

      if (!grouped.has(key)) {
        grouped.set(key, []);
      }
      grouped.get(key)!.push(task);
    }

    return grouped;
  }

  /**
   * Calculate column widths for table
   */
  private calculateTableWidths(headers: string[], showDescription: boolean): number[] {
    const baseWidths = {
      ID: 10,
      Title: showDescription ? 40 : 50,
      Status: 15,
      Priority: 10,
      Assignee: 15,
      'Due Date': 12,
      Tags: 20,
    };

    return headers.map(h => baseWidths[h as keyof typeof baseWidths] || 15);
  }
}

import type { ListrTask } from 'listr2';
import { Listr } from 'listr2';
import chalk from 'chalk';

export interface TaskItem {
  id: string;
  title: string;
  action: () => Promise<any>;
  skip?: () => boolean | string;
  enabled?: boolean;
  concurrent?: boolean;
}

export interface TaskGroup {
  title: string;
  tasks: TaskItem[];
  concurrent?: boolean;
}

/**
 * Manages concurrent task execution with visual feedback using Listr
 */
export class TaskRunner {
  private readonly renderer: 'default' | 'simple' | 'verbose';

  constructor(options?: { renderer?: 'default' | 'simple' | 'verbose' }) {
    this.renderer = options?.renderer || 'default';
  }

  /**
   * Run a single group of tasks
   */
  async runTasks(
    _title: string,
    tasks: TaskItem[],
    options?: {
      concurrent?: boolean;
      exitOnError?: boolean;
    }
  ): Promise<void> {
    const listrTasks: ListrTask[] = tasks.map(task => ({
      title: task.title,
      task: task.action,
      ...(task.skip && { skip: task.skip }),
      enabled: task.enabled ?? true,
    }));

    const listr = new Listr(listrTasks, {
      concurrent: options?.concurrent ?? false,
      exitOnError: options?.exitOnError ?? true,
      renderer: this.renderer,
      rendererOptions: {
        showSubtasks: true,
        showTimer: true,
      } as any,
    });

    try {
      await listr.run();
    } catch (error) {
      console.error(chalk.red('Task execution failed:'), error);
      throw error;
    }
  }

  /**
   * Run multiple groups of tasks
   */
  async runTaskGroups(
    groups: TaskGroup[],
    options?: {
      exitOnError?: boolean;
    }
  ): Promise<void> {
    const mainTasks: ListrTask[] = groups.map(group => ({
      title: group.title,
      task: (_ctx, task) => {
        const subtasks: ListrTask[] = group.tasks.map(item => ({
          title: item.title,
          task: item.action,
          ...(item.skip && { skip: item.skip }),
          enabled: item.enabled ?? true,
        }));

        return task.newListr(subtasks, {
          concurrent: group.concurrent ?? false,
          exitOnError: options?.exitOnError ?? true,
        });
      },
    }));

    const listr = new Listr(mainTasks, {
      concurrent: false,
      exitOnError: options?.exitOnError ?? true,
      renderer: this.renderer,
      rendererOptions: {
        showSubtasks: true,
        showTimer: true,
      } as any,
    });

    try {
      await listr.run();
    } catch (error) {
      console.error(chalk.red('Task group execution failed:'), error);
      throw error;
    }
  }

  /**
   * Run tasks with progress tracking
   */
  async runWithProgress<T>(
    title: string,
    items: T[],
    processor: (item: T, task: any) => Promise<void>,
    options?: {
      concurrent?: boolean;
      concurrency?: number;
    }
  ): Promise<void> {
    let completed = 0;
    const total = items.length;

    const listr = new Listr(
      [
        {
          title: `${title} (0/${total})`,
          task: async (_ctx, task) => {
            const updateProgress = () => {
              completed++;
              task.title = `${title} (${completed}/${total})`;
            };

            if (options?.concurrent) {
              const concurrency = options.concurrency || 5;
              const chunks: T[][] = [];

              for (let i = 0; i < items.length; i += concurrency) {
                chunks.push(items.slice(i, i + concurrency));
              }

              for (const chunk of chunks) {
                await Promise.all(
                  chunk.map(async item => {
                    await processor(item, task);
                    updateProgress();
                  })
                );
              }
            } else {
              for (const item of items) {
                await processor(item, task);
                updateProgress();
              }
            }
          },
        },
      ],
      {
        renderer: this.renderer,
        rendererOptions: {
          showSubtasks: true,
          showTimer: true,
        } as any,
      }
    );

    await listr.run();
  }

  /**
   * Create a task with retry logic
   */
  createRetryTask(
    title: string,
    action: () => Promise<any>,
    options?: {
      retries?: number;
      retryDelay?: number;
    }
  ): TaskItem {
    const maxRetries = options?.retries ?? 3;
    const retryDelay = options?.retryDelay ?? 1000;

    return {
      id: `retry-${Date.now()}`,
      title,
      action: async () => {
        let lastError: any;

        for (let attempt = 1; attempt <= maxRetries; attempt++) {
          try {
            return await action();
          } catch (error) {
            lastError = error;
            if (attempt < maxRetries) {
              await new Promise(resolve => setTimeout(resolve, retryDelay));
            }
          }
        }

        throw new Error(
          `Failed after ${maxRetries} attempts: ${lastError?.message || 'Unknown error'}`
        );
      },
    };
  }

  /**
   * Run tasks with dependency resolution
   */
  async runWithDependencies(tasks: Array<TaskItem & { dependencies?: string[] }>): Promise<void> {
    const completed = new Set<string>();
    const running = new Set<string>();

    const canRun = (task: (typeof tasks)[0]): boolean => {
      if (!task.dependencies || task.dependencies.length === 0) {
        return true;
      }
      return task.dependencies.every(dep => completed.has(dep));
    };

    const runTask = async (task: (typeof tasks)[0]): Promise<void> => {
      running.add(task.id);
      try {
        await task.action();
        completed.add(task.id);
      } finally {
        running.delete(task.id);
      }
    };

    // Group tasks by whether they can run
    const groups: TaskGroup[] = [];
    const remaining = [...tasks];

    while (remaining.length > 0) {
      const ready = remaining.filter(t => canRun(t) && !completed.has(t.id));

      if (ready.length === 0 && remaining.length > 0) {
        throw new Error('Circular dependency detected or unresolvable dependencies');
      }

      if (ready.length > 0) {
        groups.push({
          title: `Running ${ready.length} task(s)`,
          tasks: ready.map(t => ({
            ...t,
            action: () => runTask(t),
          })),
          concurrent: true,
        });

        // Remove ready tasks from remaining
        remaining.splice(0, remaining.length, ...remaining.filter(t => !ready.includes(t)));
      }
    }

    await this.runTaskGroups(groups);
  }
}

// Export singleton instance
export const taskRunner = new TaskRunner();

import { readFile, writeFile } from 'fs/promises';
import { Listr } from 'listr2';
import chalk from 'chalk';
import type { TaskGroup } from './task-runner';
import { TaskRunner } from './task-runner';

export interface TodoItem {
  id: string;
  text: string;
  completed: boolean;
  priority: 'P1' | 'P2' | 'P3' | 'P4' | 'P5';
  size: 'S' | 'M' | 'L' | 'XL';
  value: 'S' | 'M' | 'L';
  dependencies: string[];
  owner?: string;
  notes?: string;
  phase?: string;
}

export interface TodoProcessorOptions {
  concurrent?: boolean;
  maxConcurrent?: number;
  groupByPhase?: boolean;
  showDependencies?: boolean;
  generateReport?: boolean;
  dryRun?: boolean;
}

/**
 * Processes TODO items from markdown files with visual feedback
 */
export class TodoProcessor {
  private readonly taskRunner: TaskRunner;

  constructor() {
    this.taskRunner = new TaskRunner({ renderer: 'default' });
  }

  /**
   * Parse TODO.md file and extract tasks
   */
  async parseTodoFile(filePath: string): Promise<Map<string, TodoItem>> {
    const content = await readFile(filePath, 'utf-8');
    const lines = content.split('\n');
    const todos = new Map<string, TodoItem>();
    let currentPhase = '';

    for (const line of lines) {
      // Detect phase headers
      if (line.startsWith('## Phase')) {
        const phaseMatch = line.match(/## Phase \d+: (.+?) \(/);
        if (phaseMatch) {
          currentPhase = phaseMatch[1] || '';
        }
        continue;
      }

      // Parse TODO items
      const todoMatch = line.match(
        /- \[([ x])\] \*\*TASK-(\d+)\*\*: (.+?) \[([SML]|XL), (P[1-5]), Value: ([SML])\]/
      );
      if (todoMatch) {
        const [, completed, taskId, description, size, priority, value] = todoMatch;
        const id = `TASK-${taskId}`;

        // Look for dependencies in subsequent lines
        const dependencies: string[] = [];
        const lineIndex = lines.indexOf(line);
        if (lineIndex < lines.length - 1) {
          const nextLines = lines.slice(lineIndex + 1, lineIndex + 5);
          for (const nextLine of nextLines) {
            if (nextLine.includes('Dependencies:')) {
              const depMatch = nextLine.match(/Dependencies: (.+)/);
              if (depMatch && depMatch[1] !== 'None') {
                dependencies.push(...depMatch[1].split(', ').map(d => d.trim()));
              }
              break;
            }
          }
        }

        todos.set(id, {
          id,
          text: description,
          completed: completed === 'x',
          priority: priority as TodoItem['priority'],
          size: size as TodoItem['size'],
          value: value as TodoItem['value'],
          dependencies,
          phase: currentPhase || undefined,
        });
      }
    }

    return todos;
  }

  /**
   * Group todos by phase
   */
  private groupByPhase(todos: Map<string, TodoItem>): Map<string, TodoItem[]> {
    const phases = new Map<string, TodoItem[]>();

    for (const todo of todos.values()) {
      const phase = todo.phase || 'Uncategorized';
      if (!phases.has(phase)) {
        phases.set(phase, []);
      }
      const phaseArray = phases.get(phase);
      if (phaseArray) {
        phaseArray.push(todo);
      }
    }

    return phases;
  }

  /**
   * Check if a todo can be executed based on dependencies
   */
  private canExecuteTodo(todo: TodoItem, completedTodos: Set<string>): boolean {
    if (todo.completed) return false;
    if (!todo.dependencies || todo.dependencies.length === 0) return true;
    return todo.dependencies.every(dep => completedTodos.has(dep));
  }

  /**
   * Create execution groups based on dependencies
   */
  private createExecutionGroups(todos: Map<string, TodoItem>): TodoItem[][] {
    const groups: TodoItem[][] = [];
    const completed = new Set<string>(
      Array.from(todos.values())
        .filter(t => t.completed)
        .map(t => t.id)
    );
    const remaining = Array.from(todos.values()).filter(t => !t.completed);

    while (remaining.length > 0) {
      const executable = remaining.filter(t => this.canExecuteTodo(t, completed));

      if (executable.length === 0) {
        console.warn(chalk.yellow('Warning: Some tasks have unresolvable dependencies'));
        break;
      }

      groups.push(executable);
      executable.forEach(t => {
        completed.add(t.id);
        remaining.splice(remaining.indexOf(t), 1);
      });
    }

    return groups;
  }

  /**
   * Process todos with visual feedback
   */
  async processTodos(todoFile: string, options: TodoProcessorOptions = {}): Promise<void> {
    const todos = await this.parseTodoFile(todoFile);

    if (options.groupByPhase) {
      await this.processGroupedByPhase(todos, options);
    } else {
      await this.processWithDependencies(todos, options);
    }

    if (options.generateReport) {
      await this.generateReport(todos, todoFile);
    }
  }

  /**
   * Process todos grouped by phase
   */
  private async processGroupedByPhase(
    todos: Map<string, TodoItem>,
    options: TodoProcessorOptions
  ): Promise<void> {
    const phases = this.groupByPhase(todos);
    const taskGroups: TaskGroup[] = [];

    for (const [phaseName, phaseTodos] of phases) {
      if (!phaseTodos) continue;
      const completedCount = phaseTodos.filter(t => t.completed).length;
      const totalCount = phaseTodos.length;

      taskGroups.push({
        title: `${phaseName} [${completedCount}/${totalCount}]`,
        tasks: phaseTodos
          .filter(t => !t.completed)
          .map(todo => ({
            id: todo.id,
            title: `${todo.id}: ${todo.text}`,
            action: async () => {
              if (!options.dryRun) {
                // Simulate task implementation
                await new Promise(resolve => setTimeout(resolve, 100));
                todo.completed = true;
              }
            },
            skip: () => {
              if (todo.dependencies.length > 0) {
                const unmet = todo.dependencies.filter(dep => !todos.get(dep)?.completed);
                if (unmet.length > 0) {
                  return `Waiting for: ${unmet.join(', ')}`;
                }
              }
              return false;
            },
          })),
        concurrent: options.concurrent ?? false,
      });
    }

    await this.taskRunner.runTaskGroups(taskGroups, {
      exitOnError: false,
    });
  }

  /**
   * Process todos with dependency resolution
   */
  private async processWithDependencies(
    todos: Map<string, TodoItem>,
    options: TodoProcessorOptions
  ): Promise<void> {
    const groups = this.createExecutionGroups(todos);

    const listr = new Listr(
      groups.map((group, index) => ({
        title: `Execution Group ${index + 1} (${group.length} tasks)`,
        task: async (_ctx, task) => {
          const subtasks = group.map(todo => ({
            title: `${todo.id}: ${todo.text}`,
            task: async () => {
              if (!options.dryRun) {
                await new Promise(resolve => setTimeout(resolve, 100));
                todo.completed = true;
              }
            },
          }));

          return task.newListr(subtasks, {
            concurrent: options.concurrent ?? true,
            rendererOptions: {
              showSubtasks: true,
            } as any,
          });
        },
      })),
      {
        concurrent: false,
        rendererOptions: {
          showSubtasks: true,
          showTimer: true,
        } as any,
      }
    );

    await listr.run();
  }

  /**
   * Generate implementation report
   */
  private async generateReport(todos: Map<string, TodoItem>, todoFile: string): Promise<void> {
    const reportPath = todoFile.replace('.md', '-report.md');
    const completed = Array.from(todos.values()).filter(t => t.completed);
    const pending = Array.from(todos.values()).filter(t => !t.completed);

    const report = `# TODO Implementation Report

Generated: ${new Date().toISOString()}

## Summary
- Total Tasks: ${todos.size}
- Completed: ${completed.length}
- Pending: ${pending.length}
- Completion Rate: ${((completed.length / todos.size) * 100).toFixed(1)}%

## Completed Tasks
${completed.map(t => `- [x] ${t.id}: ${t.text}`).join('\n')}

## Pending Tasks
${pending.map(t => `- [ ] ${t.id}: ${t.text}`).join('\n')}

## Time Estimates
- Completed: ${this.calculateTimeEstimate(completed)} minutes
- Remaining: ${this.calculateTimeEstimate(pending)} minutes
`;

    await writeFile(reportPath, report);
    console.log(chalk.green(`\nâœ… Report generated: ${reportPath}`));
  }

  /**
   * Calculate time estimate based on task sizes
   */
  private calculateTimeEstimate(todos: TodoItem[]): number {
    const sizeMultipliers = { S: 1, M: 1.4, L: 4, XL: 8 };
    return todos.reduce((total, todo) => total + sizeMultipliers[todo.size], 0);
  }
}

// Export singleton instance
export const todoProcessor = new TodoProcessor();
